--
-- Δομή πίνακα για τον πίνακα `nea`
--

CREATE TABLE IF NOT EXISTS `nea` (
`ID` int(10) NOT NULL,
  `Titlos_arthrou` longtext NOT NULL,
  `Small_text` longtext NOT NULL,
  `Keimeno` longtext NOT NULL,
  `Eikona` longtext CHARACTER SET latin1 NOT NULL,
  `category` int(100) NOT NULL,
  `Time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `counter` int(11) NOT NULL,
  `url` varchar(500) NOT NULL,
  `source` mediumtext NOT NULL
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=99 ;

--
-- Άδειασμα δεδομένων του πίνακα `nea`
--

INSERT INTO `nea` (`ID`, `Titlos_arthrou`, `Small_text`, `Keimeno`, `Eikona`, `category`, `Time`, `counter`, `url`, `source`) VALUES
(1, 'Απειλές για το φυσικό αέριο', 'Η Βουδαπέστη ενδιαφέρεται για το σχέδιο μεταφοράς φυσικού αερίου από τη Ρωσία μέσω της Τουρκίας...', 'Η Βουδαπέστη ενδιαφέρεται για το σχέδιο μεταφοράς φυσικού αερίου από τη Ρωσία μέσω της Τουρκίας, Ελλάδας, ΠΓΔΜ, Σερβίας και Ουγγαρίας, δήλωσε την Τρίτη ο Ούγγρος πρωθυπουργός, Βίκτορ Όρμπαν."Ο «South Stream», δυστυχώς εγκαταλείφθηκε. Αλλά η Ουγγαρία είναι απολύτως αποφασισμένη να συνεχίσει τις επενδύσεις", είπε ο Όρμπαν, επισημαίνοντας ότιη Ουγγαρία έχει διπλή ευθύνη (στη μεταφορά του φυσικού αερίου), επειδή η Σερβία παίρνει φυσικό αέριο -απαραίτητο για την επιβίωσή της- από την Ουγγαρία"."Χθες βράδυ, διαπραγματεύτηκα με τον Πρωθυπουργό της Σερβίας σχετικά με μια πιθανή λύση του θέματος. Οι χώρες μας έχουν σχετικές προτάσεις. Μιλήσαμε γι ''αυτό και με την Ελλάδα...", είπε ο Ούγγρος πρωθυπουργός συμπληρώνοντας πως "εάν είμαστε σε θέση να το εφαρμόσουμε – σε κάθε περίπτωση, είναι ζήτημα μέλλοντος, όπως με διαβεβαίωσε ο κύριος Πρόεδρος Πούτιν".', 'images/demo/putina_2.jpg', 2, '2015-07-04 09:04:52', 42, '', ''),
(2, 'ΗΠΑ και Ρωσία η μεγάλη σύγκρουση', 'Εαν χρειαστεί, η Ρωσία μπορεί να εξετάσει την προστασία των εθνικών συμφερόντων της στην Αρκτική με στρατιωτικά μέσα, δήλωσε ο', 'Εαν χρειαστεί, η Ρωσία μπορεί να εξετάσει την προστασία των εθνικών συμφερόντων της στην Αρκτική με στρατιωτικά μέσα, δήλωσε ο υπουργός Άμυνας της χώρας, επισημαίνοντας την αύξηση του ενδιαφέροντος για τους πόρους της περιοχής από τις χώρες που δεν έχουν άμεση πρόσβαση στην Αρκτική."Η συνεχής στρατιωτική παρουσία στην Αρκτική και η δυνατότητα να προστατεύσει τα συμφέροντα του κράτους με στρατιωτικά μέσα θεωρείται ως αναπόσπαστο μέρος της γενικής πολιτικής για την εγγύηση της εθνικής ασφάλειας"', 'images/demo/hpa.jpg', 2, '2015-07-01 14:49:25', 29, '', ''),
(3, 'Βαρουφάκης: Έχουμε διασφαλίσει τα χρήματα για μισθούς και συντάξεις', '«Η εξέλιξη των δημοσίων εσόδων δεν είναι καθόλου κακή. Υπήρξε μείωση το πρώτο δίμηνο, αλλά παράλληλα υπήρξε μείωση δαπανών, οπότε αυτό έχει μεγάλη σημασία. Δεν υπάρχει κανένα πρόβλημα στην εξασφάλιση των κονδυλίων', '«Η εξέλιξη των δημοσίων εσόδων δεν είναι καθόλου κακή. Υπήρξε μείωση το πρώτο δίμηνο, αλλά παράλληλα υπήρξε μείωση δαπανών, οπότε αυτό έχει μεγάλη σημασία. Δεν υπάρχει κανένα πρόβλημα στην εξασφάλιση των κονδυλίων για μισθούς και συντάξεις» δήλωσε ο υπουργός Οικονομικών Γιάνης Βαρουφάκης σε συνέντευξή του το πρωί της Κυριακής στον Alpha.  Ερωτηθείς «μέχρι πότε;» απάντησε ότι δεν υπάρχει κανένα πρόβλημα γενικότερα σε αυτό το ζήτημα. «Βεβαίως απόλυτα» ήταν η απάντησή του όταν ρωτήθηκε εάν έχει εξασφαλίσει η κυβέρνηση αυτά τα χρήματα.  Το πρόβλημα που υπάρχει είναι ότι το Μάρτιο και τον Απρίλιο και μετά τον Ιούνιο - Ιούλιο υπάρχουν μεγάλες αποπληρωμές χρέους. Ο υπουργός Οικονομικών τόνισε ότι μέσω των διαπραγματεύσεων που είναι σε εξέλιξη θα υπάρξει λύση και σε αυτό το ζήτημα.  Σε ερώτημα για την «τρύπα 2 δις στα έσοδα» που διαπίστωσαν, σύμφωνα με πληροφορίες, στις πρώτες έρευνες τα τεχνικά κλιμάκια, ο κ. Βαρουφάκης είπε ότι δεν γνωρίζει κάτι τέτοιο, επιρρίπτοντας ευθύνες στην προηγούμενη', 'images/demo/giannis-baroufakis.jpg', 1, '2015-07-01 11:45:36', 2, '', ''),
(4, ' Αριστοτέλης Ωνάσης: 40 χρόνια από το θάνατο του', ' Ο Έλληνας μεγιστάνας Αριστοτέλης Ωνάσης γεννήθηκε στις 15 Ιανουαρίου του 1906 στη Σμύρνη, σε σχετικά εύπορη για την περιοχή και την εποχή εκείνη οικογένεια.', ' Ο Έλληνας μεγιστάνας Αριστοτέλης Ωνάσης γεννήθηκε στις 15 Ιανουαρίου του 1906 στη Σμύρνη, σε σχετικά εύπορη για την περιοχή και την εποχή εκείνη οικογένεια. Ήταν το δεύτερο παιδί των γονιών του. Είχε προηγηθεί η Άρτεμις, δυο χρόνια πριν.', ' images/demo/newego_LARGE_t_1101_53565761.jpg', 3, '2015-07-01 14:09:00', 3, '', ''),
(5, 'Το NATO πρέπει να προετοιμασθεί για ρωσική επίθεση, προειδοποιεί στρατηγός του', 'Το NATO πρέπει να προετοιμαστεί για το ενδεχόμενο μιας μεγάλης ρωσικής επίθεσης, με συμβατικές δυνάμεις, με στόχο εδάφη που ανήκουν σε κράτη-μέλη της Συμμαχίας, δήλωσε ο ', 'Το NATO πρέπει να προετοιμαστεί για το ενδεχόμενο μιας μεγάλης ρωσικής επίθεσης, με συμβατικές δυνάμεις, με στόχο εδάφη που ανήκουν σε κράτη-μέλη της Συμμαχίας, δήλωσε ο υποδιοικητής των νατοϊκών δυνάμεων στην Ευρώπη, βρετανός στρατηγός σερ Άντριαν Μπράντσο.«Η Ρωσία μπορεί να πιστεύει πως οι μεγάλου μεγέθους συμβατικές δυνάμεις που μπορεί να συγκροτήσει γρήγορα θα μπορούν, μελλοντικά, να χρησιμοποιηθούν όχι μόνον για εκφοβισμό και εκβιασμό, αλλά για την κατάληψη εδαφών του NATO», δήλωσε χαρακτηριστικά ο σερ Άντριαν Μπράντσο, μιλώντας το βράδυ της Παρασκευής σε εκδήλωση think-tank στο Λονδίνο.Πηγή: http://www.skai.gr/news/world/article/275890/to-nato-prepei-na-proetoimasthei-gia-rosiki-epithesi-proeidopoiei-stratigos-tou/#ixzz3W53vO8Z2 Follow us: @skaigr on Twitter | skaigr on Facebook', 'images/demo/nato-eswteriki.jpg', 2, '2015-07-01 14:36:10', 3, '', ''),
(6, 'Η Ελλάδα στρέφεται προς την Ρωσία,αλλά...', 'Η Ελλάδα στρέφεται προς την Κίνα και τη Ρωσία για βοήθεια, αλλά δεν μπορεί να ξεφύγει από τους εταίρους της στην Ευρωζώνη, αναφέρει νέο', 'Η Ελλάδα στρέφεται προς την Κίνα και τη Ρωσία για βοήθεια, αλλά δεν μπορεί να ξεφύγει από τους εταίρους της στην Ευρωζώνη, αναφέρει νέο δημοσίευμά του, το βρετανικό οικονομικό περιοδικό Economist, ενώ προειδοποιεί παράλληλα ότι εξαντλείται ο χρόνος για τον Έλληνα πρωθυπουργό, Αλ. Τσίπρα', 'images/demo/rg.jpg', 1, '2015-07-01 11:45:37', 1, '', ''),
(7, 'Ο Τσίπρας θέτει σε κίνδυνο τη θέση της ΕΕ για τη Ρωσία', 'Αντιδράσεις εκ μέρους Γερμανών πολιτικών προκαλεί η επικείμενη επίσκεψη του Πρωθυπουργού στην Μόσχα. Να μην δυσαρεστήσει τους Ευρωπαίους εταίρους του καλεί τον Αλέξη Τσίπρα ο Πρόεδρος του', 'Αντιδράσεις εκ μέρους Γερμανών πολιτικών προκαλεί η επικείμενη επίσκεψη του Πρωθυπουργού στην Μόσχα. Να μην δυσαρεστήσει τους Ευρωπαίους εταίρους του καλεί τον Αλέξη Τσίπρα ο Πρόεδρος του Ευρωπαϊκού Κοινοβουλίου Μάρτιν Σουλτς, ενώ ανάλογη στάση τηρεί και ο Αντικαγκελάριος Ζίγκμαρ Γκάμπριελ. Διαφορετική άποψη εκφράζει η επικεφαλής της Αριστεράς στο Ευρωκοινοβούλιο, Γκάμπι Τσίμερ.Με την λίστα μεταρρυθμίσεών του και την επίσκεψή του στο Βερολίνο είχε αρχίσει ο (Αλέξης) Τσίπρας να ανακτά εμπιστοσύνη και αξιοπιστία, επισημαίνει ο κ. Σουλτς σε συνέντευξή του στην «Hannoversche Allgemeine Zeitung» και τονίζει ότι θα ήταν «απαράδεκτο» εάν τώρα υπολογίζει ότι ως αντάλλαγμα για ρωσική βοήθεια «θα θέσει σε κίνδυνο την κοινή θέση της ΕΕ σε ό,τι αφορά την Ρωσία». ', 'images/demo/tsipras.jpg', 1, '2015-07-01 11:45:38', 1, '', ''),
(8, 'Πως θα είναι οι ουρανοξύστες του μέλλοντος;(pics)', 'Την εποχή της εξέλιξης και της τεχνολογίας το σίγουρο είναι ότι πολλά θα αλλάξουν στην καθημερινότητά μας προκειμένου αυτή να γίνει καλύτερη, πράγμα που παρατηρούμε', 'Την εποχή της εξέλιξης και της τεχνολογίας το σίγουρο είναι ότι πολλά θα αλλάξουν στην καθημερινότητά μας προκειμένου αυτή να γίνει καλύτερη, πράγμα που παρατηρούμε ήδη τα τελευταία χρόνια που η λύση σε πολλά προβλήματα βρίσκεται στο χέρι μας και συγκεκριμένα στο κινητό μας τηλέφωνο. Είναι γεγονός, λοιπόν, πως η τεχνολογία επηρεάζει κάθε τομέα της καθημερινής μας ζωής, όπως φυσικά επηρεάζει και την αρχιτεκτονική.', 'images/demo/farma3.jpg', 3, '2015-07-04 12:21:05', 22, '', ''),
(9, 'Πόσο ισχυρή είναι η Ρωσία του Πούτιν', 'Οπου και αν κοιτάξεις αυτές τις ημέρες, βλέπεις τη Ρωσία να ξαναπαίζει ρόλο πανίσχυρης δύναμης και το ηγεμονικό «δόγμα Πούτιν» να κυριαρχεί στο εσωτερικό και στο εξωτερικό. Πάρτε, για παράδειγμα,', 'Οπου και αν κοιτάξεις αυτές τις ημέρες, βλέπεις τη Ρωσία να ξαναπαίζει ρόλο πανίσχυρης δύναμης και το ηγεμονικό «δόγμα Πούτιν» να κυριαρχεί στο εσωτερικό και στο εξωτερικό. Πάρτε, για παράδειγμα, τη Συρία και το Ιράν, τις δύο διεθνείς κρίσεις ολκής όπου επικράτησε η «διπλωματική λύση» που ήθελε η Μόσχα. Και μόλις την περασμένη εβδομάδα ο ωμός οικονομικός εκβιασμός έπεισε την Ουκρανία να μην υπογράψει τη συμφωνία ελευθέρου εμπορίου με την ΕΕ και να ξαναπέσει στην αγκαλιά του Κρεμλίνου - άλλη μία νίκη του Πούτιν στη γεωπολιτική σκακιέρα.  Στην παγκόσμια οικονομία; Με όπλο το πετρέλαιο και το φυσικό αέριο η Μόσχα διεισδύει παντού, όχι μόνο στις παραδοσιακές αγορές της αλλά και στη Λαϊκή Κίνα. Με μια ενεργειακή συμφωνία-μαμούθ μεταξύ της κρατικής Rosneft και της κινεζικής China National Petroleum Corp. το Κρεμλίνο θα αρχίσει από το 2014 να παρέχει στους «κόκκινους καπιταλιστές» 365 εκατ. τόνους πετρελαίου για 25 χρόνια μέσω του αγωγού Ανατολικής Σιβηρίας - Ειρηνικού Ωκεανού (ESPO) έναντι τ', 'images/demo/Russian_army_mobilizing_for_war.jpg', 2, '2015-07-01 14:35:48', 3, '', ''),
(10, 'Σοβαρές προκλήσεις από την Τουρκία..Ενεργοποιήθηκε ολόκληρο το ελληνικό σύστημα αεράμηνας', 'Μία σοβαρή τουρκική πρόκληση που λίγο έλειψε να βάλει «φωτιά» στο Αιγαίο, αφού η Πολεμική Αεροπορία αναγκάστηκε μέσα στη νύχτα να απογειώσει αρκετά αεροσκάφη και να τεθούν σε πλήρη επιχειρησιακή ετοιμότητα τα αντιαεροπορικά συστήματα, αποκαλύπτει το defencenet.gr.', 'Μία σοβαρή τουρκική πρόκληση που λίγο έλειψε να βάλει «φωτιά» στο Αιγαίο, αφού η Πολεμική Αεροπορία αναγκάστηκε μέσα στη νύχτα να απογειώσει αρκετά αεροσκάφη και να τεθούν σε πλήρη επιχειρησιακή ετοιμότητα τα αντιαεροπορικά συστήματα, αποκαλύπτει το defencenet.gr.  Συγκεκριμένα, μετά από μία δύσκολη ημέρα, αφού οι Τούρκοι είχαν προχωρήσει σε αρκετές παραβάσεις του FIR Αθηνών και παραβιάσεις του εθνικού εναέριου χώρου από μαχητικά, αεροσκάφη ηλεκτρονικού πολέμου CN-235 και ελικόπτερα, τα ελληνικά ραντάρ έπιασαν μέσα στη νύχτα έναν σχηματισμό μαχητικών F-16 να εισέρχεται στο Αιγαίο από την περιοχή νοτίως της Ρόδου. Ταυτόχρονα, ένας άλλος σχηματισμός τεσσάρων τουρκικών αεροσκαφών πραγματοποιούσε είσοδο από το βόρειο Αιγαίο και την ίδια στιγμή εισέρχονταν στο Αιγαίο και ένα CN-235. Επρόκειτο δηλαδή για ένα οργανωμένο σχέδιο της Άγκυρας να αμφισβητήσει τα κυριαρχικά δικαιώματα της χώρας και να δοκιμάσει τα αντανακλαστικά της ελληνικής αεράμυνας σε πολλά πεδία ταυτόχρονα και μέσα στο σκοτάδι', 'images/demo/planes.jpg', 1, '2015-07-01 11:45:27', 1, '', ''),
(11, 'Τσίπρας - Μεντβέντεφ: Η Ελλάδα ένας σημαντικός εταίρος για την Ρωσία', 'Ως «έναν πολύ σημαντικό εταίρο με προοπτική» αντιμετωπίζει η Ρωσία την Ελλάδα, δήλωσε ο Ρώσος πρωθυπουργός Ντμίτρι Μεντβέντεφ, υποδεχόμενος τον πρωθυπουργό Αλέξη Τσίπρα στο Μέγαρο Υποδοχών της ρωσικής κυβέρνησης. Ο Ν. Μεντβέντεφ εξέφρασε την ελπίδα ότι', 'Ως «έναν πολύ σημαντικό εταίρο με προοπτική» αντιμετωπίζει η Ρωσία την Ελλάδα, δήλωσε ο Ρώσος πρωθυπουργός Ντμίτρι Μεντβέντεφ, υποδεχόμενος τον πρωθυπουργό Αλέξη Τσίπρα στο Μέγαρο Υποδοχών της ρωσικής κυβέρνησης. Ο Ν. Μεντβέντεφ εξέφρασε την ελπίδα ότι «εκτός από την πολιτική παράμετρο της επίσκεψης, θα υπάρξουν και άλλα αποτελέσματα».\r\nΑπό την πλευρά του ο Α. Τσίπρας χαρακτήρισε «πολύ ουσιαστική» τη συνάντησή του με τον πρόεδρο της Ρωσίας Βλαντίμιρ Πούτιν, εκτιμώντας ότι μπήκαν οι βάσεις για μια ριζική επανεκκίνηση των σχέσεων των δύο χωρών, "σχέσεις, οι οποίες έχουν βεβαίως πολύ βαθιές ρίζες, καθώς είναι δεδομένα τα αισθήματα φιλίας και αδελφοσύνης μεταξύ των λαών μας".\r\nΑμέσως μετά τη συνάντηση με τον Ρώσο πρωθυπουργό Μεντβέντεφ, ο Αλέξης Τσίπρας συναντήθηκε με τον Πατριάρχη Μόσχας και πάσης Ρωσίας Κύριλλο, στην Ιερά Μονή του Αγίου Δανιήλ.', 'images/demo/tsi_med.jpg', 1, '2015-07-01 11:45:34', 1, '', ''),
(12, ' O Τσίπρας θα είναι ο κεντρικός τιμώμενος ξένος ηγέτης στο Οικονομικό Φόρουμ της Αγίας Πετρούπολης', 'Οι δυνατότητες είναι αρκετά μεγάλες, αναφέρει ο Ντμίτρι Πεσκόφ ', 'Το Κρεμλίνο επιβεβαίωσε πως ο πρωθυπουργός Αλ. Τσίπρας θα είναι ο κεντρικός τιμώμενος ξένος ηγέτης στο ετήσιο Οικονομικό Φόρουμ της Αγίας Πετρούπολης ενώ ο Ντμίτρι Πεσκόφ, εκπρόσωπος Τύπου του Ρώσου ηγέτη, χαρακτήρισε πολύ θετικές τις τρίωρες συνομιλίες που είχε ο Βλαντίμιρ Πούτιν με τον Έλληνα πρωθυπουργό σε χθεσινοβραδινές δηλώσεις του σε Ρώσους δημοσιογράφους.   «Οι συνομιλίες ήταν πολύ θετικές. Όντως διαμορφώθηκαν πολλά επίπεδα συνεργασίας, τα πάντα συμφωνήθηκαν και υπάρχουν κοινά σχέδια προς υλοποίηση» ανέφερε χαρακτηριστικά ο κ. Πεσκόφ. ', 'images/demo/tsipras-poutin.jpg', 1, '2015-07-03 17:30:19', 16, '', ''),
(13, 'Αρχιεπίσκοπος Ιερώνυμος: Στη διάθεση του κράτους η εκκλησιαστική περιουσία', 'Δεν αφορά σε πώλησή της αλλά σε αξιοποίηση με απώτερο σκοπό την αποπληρωμή του χρέους. ', 'Έτοιμος να προχωρήσει μαζί με το κράτος στην αξιοποίηση της εκκλησιαστικής περιουσίας και τα έσοδα να διατεθούν για την αποπληρωμή του χρέους, εμφανίστηκε για πρώτη φορά ο Αρχιεπίσκοπος Αθηνών και Πάσης Ελλάδος, Ιερώνυμος. \r\n\r\nΟ ίδιος διευκρίνισε ότι δεν μιλά για πώληση της εκκλησιαστικής περιουσίας, αλλά για αξιοποίησή της, προκειμένου να παραμείνει σε ελληνικά χέρια, υπό την προϋπόθεση ότι τα έσοδά που θα προκύψουν, θα διατεθούν για την αποπληρωμή του χρέους. \r\n\r\nΟ Αρχιεπόσκοπος μιλώντας σε πρωινή εκπομπή του ΣΚΑΪ υπογράμμισε ότι το θέμα θα συζητηθεί εν ευθέτω χρόνω με τον πρωθυπουργό και έστειλε μήνυμα στη γερμανίδα Καγκελάριο Άνγκελα Μέρκελ, τονίζοντας ότι «δεν έχω διαφορές μαζί της, αλλά ό,τι μπορεί να γίνει υπέρ του ελληνικού λαού, θα ήταν ευπρόσδεκτο». \r\n\r\nΔεν παρέλειψε μάλιστα να σημειώσει ότι η Άνγκελα Μερκελ απέστειλε τον Γιοακίμ Φούχτελ, εκπρόσωπο της καγκελαρίας για την ελληνογερμανική συνεργασία, στην Αρχιεπισκοπή, για να συζητήσει μαζί του. \r\n\r\nΠηγή: protothema.gr ', 'images/demo/ieronumos.jpg', 1, '2015-07-04 18:49:06', 30, '', ''),
(14, 'Η Ρωσία έτοιμη να χτυπήσει την Τουρκία, αν βοηθήσει το ΝΑΤΟ στην Ουκρανία...', 'Η Ρωσία ετοιμάζει σχέδιο επίθεσης εναντίον της Τουρκίας σε περίπτωση που της "μπουν" ιδέες να βοηθήσουν το ΝΑΤΟ στην ουκρανική κρίση ή χρησιμοποιήσουν τους χιλιάδες τζιχαντιστές που έχουν βρει καταφύγιο στην επικράτειά της, εναντίον της σε ρόλο δολιοφθορών και πρόκλησης μεγάλων τρομοκρατικών ενεργειών ή ξεσηκωμού των τουρκογενών πληθυσμών της Τσετσενίας.', 'Όπως αναφέρεται ο Ρώσος στρατηγός, Oleg Makareviç, έχει προβάλει προσομοιώσεις των τουρκικών στρατιωτικών αεροδρομίων με εντολή χτυπήματος από τις ρωσικές ένοπλες δυνάμεις.\r\n\r\nΗ είδηση αυτή πέρασε στην τουρκική πλευρά από την ρωσική εφημερίδα Kommersant όπως αναφέρει ο γνωστός Διεθνολόγος-Τουρκολόγος Ν.Χειλαδάκης, και έχει βάλει σε μεγάλη περίσκεψη τους Τούρκους καθώς για πρώτη φορά διαφαίνεται τόσο φανερά η προοπτική να δεχτούν μια σοβαρή επίθεση από την Ρωσία.\r\n\r\nΣύμφωνα με τα τουρκικά δημοσιεύματα, ο Ρώσος στρατηγός έδωσε αυτές τις εντολές επισημαίνοντας τον κίνδυνο επέκτασης της ουκρανικής κρίσης με συνέπεια η Τουρκία, που είναι μια χώρα του ΝΑΤΟ, αλλά και η Λετονία, να αποτελέσουν δυνητική απειλή για την Ρωσία.', 'images/demo/Erdogan_Putin.jpg', 2, '2015-07-02 10:52:04', 4, '', ''),
(15, 'Η Ρωσία έχει δικαίωμα ανάπτυξης πυρηνικών όπλων στην Κριμαία ', 'Σύμφωνα με αξιωματούχο του ρωσικού υπουργείου Εξωτερικών ', 'Η Ρωσία έχει το δικαίωμα να αναπτύξει πυρηνικά όπλα στη χερσόνησο της Κριμαίας, την οποία προσάρτησε πέρυσι η Μόσχα από την Ουκρανία, δήλωσε σήμερα αξιωματούχος του ρωσικού υπουργείου Εξωτερικών, σύμφωνα με το Reuters. Πρόσθεσε ωστόσο πως δεν γνωρίζει αν υπάρχουν σχετικά σχέδια.   «Δεν ξέρω εάν υπάρχουν πυρηνικά όπλα εκεί τώρα. Δεν ξέρω εάν υπάρχουν σχέδια, αλλά, επί της αρχής, η Ρωσία μπορεί να το κάνει», δήλωσε ο Μιχαήλ Ουλιανόφσκ, επικεφαλής της υπηρεσίας ελέγχου των εξοπλισμών του υπουργείου Εξωτερικών, σύμφωνα με το πρακτορείο ειδήσεων Ιnterfax. \r\nΠηγή: www.lifo.gr', 'images/demo/putin52902.jpg', 2, '2015-07-01 14:36:08', 4, '', ''),
(16, 'Ο εμπολα δεν ειναι πιο φανατηφορος', 'Νέα ανάλυση έδειξε ότι ο ιός Έμπολα δεν εξελίχθηκε ώστε να γίνει πιο θανατηφόρος, παρά τη μετάλλαξη από τότε που εμφανίστηκε', 'Νέα ανάλυση έδειξε ότι ο ιός Έμπολα δεν εξελίχθηκε ώστε να γίνει πιο θανατηφόρος, παρά τη μετάλλαξη από τότε που εμφανίστηκε, πριν 40 χρόνια.\r\nΤα ενδιαφέροντα αποτελέσματα του University of Manchester δείχνουν ότι το πολύ υψηλότερο ποσοστό θνησιμότητας κατά την τρέχουσα εξάπλωση -με τον αριθμό σχεδόν στα 1.500 άτομα- δεν οφειλόταν σε μεταλλάξεις ή εξέλιξη που καθιστούσαν πιο θανατηφόρο ή πιο ισχυρό τον ιό.\r\nΟ καθηγητής Simon Lovell από το Faculty of Life Sciences, εξήγησε ότι χρησιμοποίησε στοιχεία κάθε έξαρσης από το 1976 και ανακάλυψε ότι ενώ ο Έμπολα μεταλλασσόταν η λειτουργία του ιού είχε μείνει ίδια τις περασμένες 4 δεκαετίες, κάτι που προκάλεσε έκπληξη. Ο Έμπολα που προέκυψε τώρα από το 1970, πιθανόν θα ξαναεμφανιστεί.\r\n\r\nΗ έρευνα δημοσιεύτηκε στο περιοδικό "Virology".', 'images/demo/health.jpg', 4, '2015-07-01 10:02:10', 0, '', ''),
(17, 'Δήλωση Πούτιν: Έτοιμοι να συνεργαστούμε  με τις ΗΠΑ', 'Την χείρα προς τις ΗΠΑ τείνει ο Ρώσος πρόεδρος, Βλαντιμίρ Πούτιν, τονίζοντας ότι έχει κοινά συμφέροντα με τη χώρα του και είναι ανάγκη να εργαστεί μαζί τους πάνω σε μια κοινή ατζέντα.', 'Τόνισε δε ότι:«Έχουμε διαφωνίες σε αρκετά θέματα στη διεθνή ατζέντα. Όμως ταυτόχρονα υπάρχει κάτι που μας ενώνει, που μας υποχρεώνει να εργαστούμε μαζί», δήλωσε ο Βλάντιμιρ Πούτιν.«Εννοώ τις γενικές προσπάθειες για να γίνει η παγκόσμια οικονομία πιο δημοκρατική, μετρημένη και ισορροπημένη, έτσι ώστε η παγκόσμια τάξη να είναι πιο δημοκρατική. Έχουμε μια κοινή ατζέντα», πρόσθεσε ο ρώσος πρόεδρος.', 'images/imagefornews/putin.jpg', 2, '2015-07-01 11:45:40', 2, '', ''),
(18, 'Α. Μπαλτάς: 3000 - 4000 Νέες Προσλήψεις Σε Όλες Της Βαθμίδες Της Εκπαίδευσης', 'Την 31η Μαρτίου 2015 πραγματοποιήθηκε συνάντηση του Υπουργού Παιδείας καθ. Α. Μπαλτά με το ΚΔΣ της ΟΣΕΠ-ΤΕΙ στο Υπουργείο Παιδείας. Έγινε μια εποικοδομητική συζήτηση σε θετικό κλίμα και ο Υπουργός δήλωσε, ότι δεν αμφισβητείται ο ρόλος των ΤΕΙ στον ενιαίο χώρο της Ανώτατης Εκπαίδευσης και Έρευνας.', 'Όσον αφορά τη χρηματοδότηση των Ιδρυμάτων, είπε ότι στην παρούσα χρονική στιγμή δεν υπάρχει η δυνατότητα χρηματοδότησης των ΑΕΙ λόγω της οικονομικής δυσπραγίας. Δήλωσε επίσης, ότι στο πολυνομοσχέδιο του Υπουργείου Παιδείας που αναμένεται να κατατεθεί σύντομα στη Βουλή εκτός της κατάργησης των Συμβουλίων των Ιδρυμάτων και των μεταβατικών διατάξεων μεταφοράς των αρμοδιοτήτων τους, δεν πρόκειται να υπάρξει καμία άλλη σημαντική αλλαγή που να αφορά το θεσμικό πλαίσιο των Πανεπιστημίων και ΤΕΙ.\r\n\r\nΟ Υπουργός δήλωσε ότι στα πλαίσια του τρέχοντος προϋπολογισμού του κράτους, προβλέπεται να γίνουν 3000-4000 νέες προσλήψεις σε όλες της βαθμίδες της εκπαίδευσης, επισημαίνοντας ότι ο καταμερισμός των θέσεων δεν έχει γίνει ακόμη.\r\n\r\nΤέλος ο Υπουργός Παιδείας ενημέρωσε το ΚΔΣ της ΟΣΕΠ-ΤΕΙ ότι στρατηγικός στόχος του Υπουργείου είναι η υλοποίηση μεταρρύθμισης σε όλες της βαθμίδες της εκπαίδευσης έπειτα από διάλογο με τα εμπλεκόμενα μέρη. Στα πλαίσια αυτά δεσμεύτηκε ότι όταν ξεκινήσει ο διάλογος για την', 'images/imagefornews/c1834f362b9912b65be6806deb3f8105_XL.jpg', 5, '2015-07-01 10:02:10', 0, '', ''),
(19, 'Το πολυαναμενόμενο τρέιλερ του «Batman VS. Superman»', 'Το πρώτο teaser trailer της πολυαναμενόμενης ταινίας Batman VS. Superman: Dawn of Justice που φέρνει αντίπαλους δύο θρυλικούς υπερήρωες, δόθηκε στη δημοσιότητα.\r\n', 'Σε αυτό βλέπουμε σκηνές από ένα πλήθος που υποδέχεται τον Σούπερμαν (Χένρι Κάβιλ) σαν θεό και σωτήρα (έξυπνο το παιχνίδι με την θεϊκή υπόσταση που πάντα συνόδευε τον μύθο του Σούπερμαν), ο οποίος στη συνέχεια μοιάζει να καταρρίπτεται. Ένα τεράστιο άγαλμα του γνωστού υπερ-ήρωα έχει πάνω του γραμμένες τις λέξεις «Ψεύτικες θεότητες».\r\n\r\nΟ Μπάτμαν (Μπεν Άφλεκ) φαίνεται πως είναι αυτός που καλείται να τον σταματήσει. Στην ταινία βλέπουμε έναν μεγαλύτερης ηλικίας Σκοτεινό Ιππότη που μοιάζει να έχει καταληφθεί από οργή. Θα κληθεί να αντιμετωπίσει τον Σούπερμαν, στον οποίο ακούμε να απευθύνει την φράση: «Πες μου, ματώνεις;»\r\n\r\nΤην ταινία έχει σκηνοθετήσει ο Ζακ Σνάιντερ και αναμένεται να προβληθεί στις αίθουσες τον Μάρτιο του 2016.', 'images/imagefornews/batman-v-superman-poster-batman-vs-superman-and-the-dc-movies-slow-down.jpeg', 3, '2015-07-04 19:01:51', 47, '', ''),
(20, 'ICOMOS: Δράσεις για την Παγκόσμια Ημέρα Μνημείων-Τοποθεσιών', 'Ως Παγκόσμια Ημέρα Μνημείων και Τοποθεσιών έχει καθιερωθεί η 18η Απριλίου από την UNESCO και το Διεθνές Συμβούλιο Μνημείων και Τοποθεσιών (ICOMOS), το οποίο κάθε χρόνο προτείνει ένα συγκεκριμένο θέμα για την ανάδειξη του νοήματός της.', 'Έτσι, κάθε χρόνο διεξάγονται εκδηλώσεις από τις εθνικές και διεθνείς επιστημονικές επιτροπές του Συμβουλίου, με κύριο στόχο την ευαισθητοποίηση της κοινής γνώμης και της Πολιτείας για θέματα πολιτιστικής κληρονομιάς. Φέτος, που συμπληρώνονται 50 χρόνια από την ίδρυση του ICOMOS, το πεδίο των δράσεων είναι ευρύ και αφορά ποικίλες πτυχές της προστασίας και ανάδειξης της αρχιτεκτονικής πολιτιστικής κληρονομιάς.Συγκεκριμένα, την Τρίτη 21 Απριλίου η εορταστική εκδήλωση που θα πραγματοποιηθεί στο κτίριο του ελληνικού Διεθνούς Συμβουλίου Μνημείων και Τοποθεσιών (Πειραιώς 73, Αθήνα), στις 18:00, θα είναι αφιερωμένη στην Παγκόσμια Ημέρα Μνημείων και Τοποθεσιών, στην επέτειο των 50 χρόνων, καθώς και στη βράβευση των διακεκριμένων μελών του ICOMOS, Πέτρου Θέμελη, Νικόλαου Μουτσόπουλου, Νικόλαου Αγριατώνη, Διονύσιου Ζήβα και Αναστάσιου Πορτελάνου.\r\nΣε ημερομηνία που θα ανακοινωθεί προσεχώς θα παρουσιαστεί το βιβλίο «Η προστασία της αρχαιολογικής κληρονομιάς σε καιρούς οικονομικής κρίσης» («The Pro', 'images/imagefornews/archeological-site-near-athens.jpg', 3, '2015-07-06 14:48:45', 59, '', ''),
(21, 'Υβριδική τεχνολογία που δεκαπλασιάζει το bandwidth του Wi-Fi', 'Τον δεκαπλασιασμό του bandwidth συστημάτων Wi-Fi μέσω της χρήσης φώτων LED για τη μετάδοση πληροφοριών υπόσχεται μία νέα εφεύρεση ερευνητών του Oregon State University', 'Τον δεκαπλασιασμό του bandwidth συστημάτων Wi-Fi μέσω της χρήσης φώτων LED για τη μετάδοση πληροφοριών υπόσχεται μία νέα εφεύρεση ερευνητών του Oregon State University.\r\nΗ εν λόγω τεχνολογία μπορεί να ενσωματωθεί σε υπάρχοντα συστήματα για την αντιμετώπιση προβλημάτων bandwidth σε περιοχές με πολύ κόσμο, όπως τα τέρμιναλ αεροδρομίων ή οι καφετέριες, αλλά και σε σπίτια όπου υπάρχουν πολλές συσκευές Wi-Fi.\r\nΠρόσφατες εξελίξεις στην τεχνολογία LED έχουν καταστήσει δυνατή τη ρύθμιση του φωτός LED ταχύτερα, ανοίγοντας δυνατότητες όσον αφορά στη χρήση φωτός για ασύρματη μετάδοση δεδομένων. «Η χρήση φωτός για τη μετάδοση σημάτων είναι μια παλιά τεχνολογία, που ανάγεται στην εποχή που Ρωμαίοι στρατιώτες επικοινωνούσαν ο ένας με τον άλλον χρησιμοποιώντας έναν απλό κώδικα λάμψεων με καθρέφτες» αναφέρει ο Θιν Νγκουγιέν, επίκουρος καθηγητής ηλεκτρολογίας/ μηχανολογίας υπολογιστών και επικεφαλής ερευνητής του προγράμματος\r\nΤο πρωτότυπο, που αποκαλείται WiFO, χρησιμοποιεί LED που είναι πέρα από το οπτικό φάσμα του ανθρώπινου ματιού και δημιουργεί ένα αόρατο «κώνο» φωτός μέσα στον οποίο μπορούν να ληφθούν δεδομένα. Για να αντιμετωπιστεί το πρόβλημα της μικρής ζώνης επίδρασης, ο Νγκουγιέν και οι συνεργάτες του δημιούργησαν ένα υβριδικό σύστημα που μπορεί να αλλάζει από τους LED μεταδότες στην οροφή στο υπάρχον σύστημα WiFi και το αντίθετο. Οι μεταδότες είναι μικρές φωτοδίοδοι που κοστίζουν κάτω από ένα δολάριο ο καθένας και μπορούν να είναι διασυνδεδεμένοι μέσω USB port. «Πιστεύουμε ότι αν αυτή η τεχνολογία απογειωθεί, η επόμενη γενιά laptops θα περιλαμβάνει δέκτη-φωτοδίοδο» σημειώνει ο ίδιος.', 'images/imagefornews/wi_fi_zone_stock-100020374-large.png', 8, '2015-07-01 14:08:37', 2, '', ''),
(22, 'Χανιά: Επιχείρηση καθαρές παραλίες από τον δήμο Κισάμου', 'Η συγκεκριμένη δράση διοργανώνεται στη χώρα μας για τέταρτη συνεχή χρονιά και θέτει ως στόχο να καθαριστεί από εθελοντές μέσα σε μία μόνο μέρα ολόκληρη η χώρα από κάθε λογής σκουπίδια.', 'Στο πλευρό του δήμου, στο πλαίσιο της φετινής εκστρατείας, συμμετέχουν μία σειρά από φορείς, σύλλογοι, εθελοντικές οργανώσεις και ομάδες πολιτών της Κισσάμου, με στόχο να υπάρξει ενεργοποίηση και ευαισθητοποίηση της τοπικής κοινωνίας και των πολιτών για τον καθαρισμό διαφόρων περιοχών της Κισσάμου.\r\n\r\nΗ δράση καθαρισμού στην Κίσσαμο θα πραγματοποιηθεί την Κυριακη 26 Απριλίου και ο καθαρισμός αφορά τις παραλίες Φαλασάρνων και Μαύρου Μόλου, τη Λίμνη Κισσάμου, το παραλιακό μέτωπο Γραμβούσας, την παραλία γηπέδου Κισσάμου, τη Βάθη-Πλοκαμιανά και τη Χρυσοσκαλίτισα.\r\n\r\nΣε κάθε σημείο καθαρισμού θα υπάρχει εκπρόσωπος και τεχνική υποστήριξη από τη δημοτική υπηρεσία καθαριότητας ενώ ο δήμος θα διανείμει σακούλες και γάντια στους εθελοντές που θα συμμετάσχουν στη δράση καθαρισμού.', 'images/imagefornews/kathari-akti.jpg', 7, '2015-07-02 18:21:11', 1, '', ''),
(23, 'Την Κυριακή 26 Απριλίου καθαρίζουμε στο παραποτάμιο δάσος του Αξιού\r\n', 'Ο ΣΚΑΪ ξεκίνησε τις δράσεις καθαρισμών σε περιοχές Νατούρα από το Απρίλιο του 2013. Από τότε μέχρι σήμερα έχουν γίνει 34 δράσεις σε όλη την Ελλάδα με τη συμμετοχή εκατοντάδων περιβαλλοντικών οργανώσεων και φορέων.\r\n', 'την Κυριακή 26 Απριλίου, το «Όλοι Μαζί Μπορούμε και στο Περιβάλλον» σε συνεργασία με το Φορέα Διαχείρισης Δέλτα Αξιού και το Δήμο Χαλκηδόνας Θεσσαλονίκης, διοργανώνει εθελοντικό καθαρισμό στο παραποτάμιο δάσος του Αξιού.\r\n\r\nΤο παραποτάμιο δάσος και το Δέλτα του Αξιού είναι από τις πρώτες περιοχές στην Ελλάδα, που εντάχθηκαν στο ευρωπαϊκό δίκτυο προστατευόμενων περιοχών Νατούρα. Διαθέτει 95 σπάνια είδη της ελληνικής χλωρίδας πολλά από τα οποία είναι ενδημικά.\r\n\r\nΣημείο συνάντησης, η περιοχή Μούντο στο παραποτάμιο Δάσος του Αξιού, στις 10 το πρωί.\r\n\r\nΣτη δράση θα λάβουν μέρος περιβαλλοντικές οργανώσεις και σύλλογοι από τη Θεσσαλονίκη.\r\n\r\nΟ ΣΚΑΪ ξεκίνησε τις δράσεις καθαρισμών σε περιοχές Νατούρα από το Απρίλιο του 2013. Από τότε μέχρι σήμερα έχουν γίνει 34 δράσεις σε όλη την Ελλάδα με τη συμμετοχή εκατοντάδων περιβαλλοντικών οργανώσεων και φορέων.\r\n\r\nΗ χώρα μας έχει συνολικά 443 περιοχές, οι οποίες ανήκουν στο δίκτυο Νatura και καλύπτουν το 27,1% της ελληνικής έκτασης.\r\n\r\nΗ δράση γίνεται με την υποστήριξη του Ράδιο Θεσσαλονίκη 94,5\r\n\r\nΓια περισσότερες πληροφορίες, μπορείτε να επισκεφθείτε το www.oloimaziboroume.gr\r\n\r\nΕγγραφείτε και στις online κοινότητες του Όλοι Μαζί Μπορούμε:\r\ntwitter.com/oloimaziboroume\r\nyoutube.com/oloimaziboroume\r\nfacebook.com/oloimaziboroume\r\n\r\nΌλοι Μαζί Μπορούμε και στο Περιβάλλον\r\n\r\n\r\nΠηγή: http://www.skai.gr/news/environment/article/279840/tin-kuriaki-26-apriliou-katharismos-sto-parapotamio-dasos-tou-axiou/#ixzz3Y7vu0qh4 \r\nFollow us: @skaigr on Twitter | skaigr on Facebook', 'images/imagefornews/400px-Axios_river.jpg', 7, '2015-07-01 10:02:10', 0, '', ''),
(24, 'ΟΜΟΣΠΟΝΔΙΑΚΗ ΤΡΑΠΕΖΑ ΗΠΑ: Αυξημένος ο κίνδυνος του Grexit', 'Η Fed (Ομοσπονδιακή Τράπεζα των ΗΠΑ) θεωρεί ότι ο κίνδυνος μιας αρνητικής έκβασης της ελληνικής κρίσης έχει αυξηθεί και οδηγεί την Ευρωζώνη σε αχαρτογράφητα ύδατα.', 'Σύμφωνα με εκτιμήσεις, ενδεχόμενοι κεφαλαιακοί έλεγχοι θα μπορούσαν να οδηγήσουν σε χρεοκοπία και έξοδο της Ελλάδας από την Ευρωζώνη:α) αν η ΕΚΤ δεν συνεχίσει να στηρίζει τη χώραβ) αν το ΔΝΤ δεν της δώσει κάποια περίοδο χάριτοςγ) αν δεν επιτευχθεί γρήγορα συμφωνία για την επανεκκίνηση της χρηματοδότησηςΑν δεν πληρωθεί μία δόση προς το ΔΝΤ, αυτό δεν θα σήμαινε αυτομάτως και χρεοκοπίαΣε αντίθεση με τις δημόσιες δηλώσεις της γενικής διευθύντριας του ΔΝΤ Κριστίν Λαγκάρντ ότι δεν προβλέπεται χαριστική περίοδος για μία ανεπτυγμένη χώρα όπως η Ελλάδα, η Fed εκτιμά ότι αν δεν πληρωθεί μία δόση προς το Ταμείο, αυτό δεν θα σήμαινε αυτομάτως και χρεοκοπία, καθώς οι κανονισμοί του προβλέπουν περίοδο χάριτος τουλάχιστον ενός μηνός.Ωστόσο, σημειώνει ότι με το που θα ενημερωθεί αρμοδίως το Εκτελεστικό Συμβούλιο, αυτό θα οδηγήσει σε χρεοκοπία και έναντι των δανείων που έχει χορηγήσει το EFSF. Η Fed εκτιμά ότι αν το Ταμείο έδινε περίοδο χάριτος ενός μηνός στην Ελλάδα, αυτό θα την βοηθούσε, εφόσον η ΕΚΤ συνέχιζε να προσφέρει το σωσίβιο ρευστότητας προς τις ελληνικές τράπεζες.', 'images/imagefornews/fed-usa.jpg', 2, '2015-07-01 10:02:10', 0, '', ''),
(25, 'Σεισμός Νεπάλ: Συγκλονιστικά βίντεο από τσουνάμι σε πισίνα και χιονοστιβάδα(pics & vids)', 'Συγκλονιστικά βίντεο και εικόνες έρχονται από το Νεπάλ που συγκλονίστηκε από τον πανίσχυρο σεισμό των 7,9 Ρίχτερ το περασμένο Σάββατο. Ο αριθμός των νεκρών ξεπερνά πλέον του 3.220 και ο αριθμός των τραυματιών τους 6.540. ', 'Συγκλονιστικά βίντεο και εικόνες έρχονται από το Νεπάλ που συγκλονίστηκε από τον πανίσχυρο σεισμό των 7,9 Ρίχτερ το περασμένο Σάββατο. Ο αριθμός των νεκρών ξεπερνά πλέον του 3.220 και ο αριθμός των τραυματιών τους 6.540.Στο βίντεο που ακολουθεί φαίνεται ένα κύμα κυριολεκτικά που σχηματίζεται στα νερά μιας πισίνας μετά το χτύπημα του σεισμού.Την ανάσα κόβουν και οι τρομακτικές σκηνές που εκτυλίσσονται μπροστά στην κάμερα την ώρα που πέφτει η χιονοστιβάδα στα Ιμαλάια. Άνθρωποι θάβονται στο χιόνι και πέφτουν πάνω σε βράχια. Πολλοί είναι οι επιβάτες που έχουν χάσει τη ζωή τους στο Έβερεστ ενώ ανυπολόγιστος είναι ακόμη ο αριθμός αυτών που έχουν παγιδευτεί σε υψόμετρα άνω των 5.000 μέτρων. ', 'images/imagefornews/9-nepal.PNG', 2, '2015-07-06 14:01:15', 6, '', '');
INSERT INTO `nea` (`ID`, `Titlos_arthrou`, `Small_text`, `Keimeno`, `Eikona`, `category`, `Time`, `counter`, `url`, `source`) VALUES
(26, 'UNICEF: Σχεδόν 1 εκατομμύριο παιδιά στο Νεπάλ χρεάζονται άμεσα βοήθεια', 'Σχεδόν ένα εκατομμύριο παιδιά έχουν "πληγεί σημαντικά" από τον σφοδρό σεισμό των 7,8 βαθμών στο Νεπάλ που στοίχισε τη ζωή σε περισσότερους από 3.200 ανθρώπους, δήλωσε σήμερα εκπρόσωπος του Ταμείου Παιδιών του Οργανισμού Ηνωμένων Εθνών (UNICEF)', 'Σχεδόν ένα εκατομμύριο παιδιά έχουν "πληγεί σημαντικά" από τον σφοδρό σεισμό των 7,8 βαθμών στο Νεπάλ που στοίχισε τη ζωή σε περισσότερους από 3.200 ανθρώπους, δήλωσε σήμερα εκπρόσωπος του Ταμείου Παιδιών του Οργανισμού Ηνωμένων Εθνών (UNICEF) την ώρα που οι ομάδες έρευνας και διάσωσης συνεχίζουν το δύσκολο έργο τους.Με εκατοντάδες χιλιάδες Νεπαλέζους να κοιμούνται στο ύπαιθρο ή σε σκηνές, η UNICEF προειδοποιεί για τον κίνδυνο υδατογενών και μολυσματικών επιδημιών."Αυτό που γνωρίζουμε στην παρούσα φάση είναι ότι υπάρχουν σχεδόν ένα εκατομμύριο παιδιά που έχουν πληγεί σημαντικά. Η μεγαλύτερη ανησυχία μας αυτή τη στιγμή είναι η πρόσβαση σε πόσιμο νερό και σε βασικές εγκαταστάσεις υγιεινής. Γνωρίζουμε ότι εξαντλούνται οι επάρκειες τροφίμων και νερού", δήλωσε τηλεφωνικώς στο Ρόιτερς ο Κρίστοφερ Τάϊντεϊ της UNICEF."Κατακλυζόμαστε από αιτήματα για διάσωση και βοήθεια από ολόκληρη τη χώρα", είπε ο Ντίπαλ Πάντα, μέλος της υπηρεσίας διαχείρισης καταστροφών.\r\nΤα νοσοκομεία στην πρωτεύουσα Κατμαντού είναι ήδη πλήρη, γεγονός που αναγκάζει κάποιους ασθενείς και τραυματίες να κείτονται στον δρόμο αναζητώντας θεραπεία μαζί με χιλιάδες εκτοπισμένους επιζήσαντες των οποίων τα σπίτια είτε καταστράφηκαν ολοσχερώς είτε κινδυνεύουν να καταρρεύσουν.\r\nΕκτός πρωτεύουσας, οι πληροφορίες για την έκταση της καταστροφής και για τον αριθμό των νεκρών και των τραυματιών παραμένουν συγκεγχυμένες.\r\nΛιγοστές αναφορές υπάρχουν, για παράδειγμα, για την περιοχή Γκόρκα, μία από τις σφοδρότερα πληγείσες περιοχές. Η ορεινή αυτή περιοχή, η οποία βρίσκεται στα κεντρικά της χώρας, έχει πληθυσμό σχεδόν 270.000 κατοίκους. Ζημαντικές ζημιές φέρεται να έχει υποστεί και η περιοχή Λαμτζούνγκ.\r\nΑξιωματούχοι της αστυνομίας του Νεπάλ δηλώνουν ότι συνολικά 3.218 άνθρωποι είναι επιβεβαιωμένα νεκροί, ενώ 6.500 είναι οι τραυματίες. Εξήντα-έξι σκοτώθηκαν στα σύνορα με την Ινδία και τουλάχιστον 20 στο Θιβέτ, όπως μετέδωσε το επίσημο κινεζικό πρακτορείο ειδήσεων.\r\nΣτη φτωχή αυτή χώρα των 28 εκατομμυρίων κατοίκων αναλογούν μόλις 2.1 γιατροί και 50 νοσοκομειακές κλίνες για κάθε 10.000 ανθρώπους, σύμφωνα με τον Παγκόσμιο Οργανισμό Υγείας.', 'images/imagefornews/newego_LARGE_t_1101_54497343.JPG', 2, '2015-07-01 14:09:02', 3, '', ''),
(27, 'Καθηγήτρια έστελνε λινκ στους φοιτητές τις με πορνό', 'Η Lisa McElroy, καθηγήτρια της Νομικής σχολής στο πανεπιστήμιο Dextel στη Φιλαδέλφεια, έστειλε κατά λάθος στους φοιτητές της ένα λινκ με βίντεο πορνό.', 'Η Lisa McElroy, καθηγήτρια της Νομικής σχολής στο πανεπιστήμιο Dextel στη Φιλαδέλφεια, έστειλε κατά λάθος στους φοιτητές της ένα λινκ με βίντεο πορνό.\r\nΗ καθηγήρια κατάφερε να απασχολήσει τα διεθνή μέσα ενημέρωσης αφού η είδηση αναπαράχθηκε σε αστραπιαίους χρόνους με τους χρήστες των social media να οργιάζουν και πολλά μέσα να οδηγούν την καθηγήτρια σε "δημόσιο εξευτελισμό".', 'images/imagefornews/daskala-mathites-660_0.jpg', 9, '2015-07-04 18:42:52', 6, '', ''),
(28, 'Στα χέρια της UEFA η λίστα με τους υποψήφιους κατηγορούμενους', 'Στο περιθώριο της συνάντησής του με τον Σταύρο Κοντονή και τους φορείς του ποδοσφαίρου την Τετάρτη (29/4), ο Τζάνι Ινφαντίνο έλαβε τη λίστα με τους 26 υποψήφιους κατηγορούμενους', 'Στον Νο 2 αξιωματούχο της ευρωπαϊκής ομοσπονδίας, Τζάνι Ινφαντίνο, ο οποίος βρέθηκε στην Αθήνα την Τετάρτη (29/4) για να έχει συνάντηση με τον υφυπουργό Σταύρο Κοντονή και τους φορείς του ελληνικού ποδοσφαίρου, δόθηκε η λίστα με τα ονόματα των υποψήφιων κατηγορούμενων μεταφρασμένη, όπως αυτή έχει καταρτιστεί από τον ειδικό ανακριτή διαφθοράς, Γιώργο Ανδρεάδη, μετά τις καταθέσεις μαρτύρων και βάσει του πορίσματος Κορέα.Πλέον άπαντες περιμένουν τις εξελίξεις, διότι επρόκειτο περί κάτι μη αναμενόμενου και η ευρωπαϊκή ομοσπονδία έχει πλέον απόλυτη γνώση της κατάστασης που έχει διαμορφωθεί και ενώ η ανακριτική διαδικασία συνεχίζεται.', 'images/imagefornews/infantino.jpg', 6, '2015-07-01 10:02:10', 0, '', ''),
(29, 'Τεθωρακισμένα με τεχνολογία Formula-1', 'Όταν ακούει κανείς για τεχνολογία η οποία προέρχεται από τον χώρο της Formula-1 σίγουρα δεν σκέφτεται άρματα μάχης και στρατιωτικά τεθωρακισμένα γενικότερα', 'Όταν ακούει κανείς για τεχνολογία η οποία προέρχεται από τον χώρο της Formula-1 σίγουρα δεν σκέφτεται άρματα μάχης και στρατιωτικά τεθωρακισμένα γενικότερα, ωστόσο στην περίπτωση της BAE Systems αυτό το αξίωμα διαψεύδεται, καθώς τεχνικοί της εταιρείας εισήγαγαν το σύστημα Active Damping σε οχήματα της «οικογένειας» των CV90, με αποτέλεσμα το σπάσιμο ρεκόρ ταχύτητας σε ανώμαλο έδαφος και την αύξηση της ευελιξίας τους.\r\n', 'images/imagefornews/tethorakismena-me-texnologia-formula-1.jpg', 8, '2015-07-01 11:51:51', 1, '', ''),
(30, 'Λαβρόφ: Απαράδεκτες οι κατηγορίες της Ε.Ε εναντίον της Gazprom', 'Οι κατηγορίες της Ευρωπαϊκής Ένωσης εναντίον της ρωσικής κρατικής εταιρείας ενέργειας Gazprom είναι μια απαράδεκτη απόπειρα να εφαρμοστούν αναδρομικά οι πρόσφατοι κανόνες της ΕΕ σε προηγούμενα συμβόλαια, δήλωσε σήμερα ο Ρώσος υπουργός Εξωτερικών Σεργκέι Λαβρόφ.', 'Όταν κατά τη διάρκεια συνέντευξής του σε τρεις ραδιοφωνικούς σταθμούς ο κ. Λαβρόφ κλήθηκε να σχολιάσει την υπόθεση απάντησε πως   «Όλα τα συμβόλαια που ισχύουν τώρα τα οποία υπέγραψε η Gazprom με τους εταίρους της, υπεγράφησαν με πλήρη σεβασμό προς το νομικό καθεστώς που ίσχυε στην ΕΕ την περίοδο εκείνη».\r\n«Αφού η ΕΕ υιοθέτησε το αποκαλούμενο τρίτο ενεργειακό πακέτο (.) υπήρξαν προσπάθειες, και συνεχίζονται τώρα, να εφαρμοστούν αναδρομικά αυτές οι απαιτήσεις και σε παλαιά συμβόλαια. Αυτό είναι εντελώς απαράδεκτο», υπογράμμισε ο Ρώσος υπουργός.', 'images/imagefornews/lavrov_wide.jpg', 2, '2015-07-01 14:40:05', 10, '', ''),
(31, 'Συναγερμός στη NASA: Ανεξέλεγκτο διαστημικό σκάφος κατευθύνεται προς τη γη (Βίντεο)', 'Συναγερμός έχει σημάνει στη NASA, καθώς ένα ρωσικό, μη επανδρωμένο, διαστημικό σκάφος κατευθύνεται ανεξέλεγκτο προς τη γη, σύμφωνα με το γαλλικό πρακτορείο.', '«Οι Ρώσοι χειριστές της πτήσης έχασαν τον έλεγχο του μη επανδρωμένου φορτηγού διαστημικού σκάφους Progress, το οποίο επρόκειτο να ανεφοδιάσει τον Διεθνή Διαστημικό Σταθμό (ISS), και το σκάφος θα πέσει στη Γη», δήλωσε ένας αξιωματούχος της ρωσικής διαστημικής υπηρεσίας.\r\n«Άρχισε την πτώση του. Η πορεία του είναι τελείως εκτός ελέγχου», πρόσθεσε ο αξιωματούχος, ο οποίος ζήτησε να μην κατονομασθεί.\r\nΣύμφωνα με τον αξιωματούχο, οι Ρώσοι ελεγκτές της πτήσης θα προσπαθήσουν άλλες δύο φορές να αποκαταστήσουν τη σύνδεση με το σκάφος, χωρίς όμως μεγάλες ελπίδες να τα καταφέρουν.\r\n«Είναι αδύνατο να πούμε πότε ακριβώς θα πέσει στη Γη το φορτηγό σκάφος, αυτό εξαρτάται από πολλούς παράγοντες. Όμως η πτώση θα πραγματοποιηθεί σε ανεξέλεγκτες συνθήκες», ανέφερε ο ίδιος.\r\nΤο Progress M-27M μετέφερε επιστημονικό υλικό και είδη πρώτης ανάγκης, όπως τρόφιμα και νερό, όμως η απώλειά του δεν θα δημιουργήσει πρόβλημα στα έξι μέλη του πληρώματος του ISS, τα οποία διαθέτουν αποθέματα για πολλούς μήνες.\r\nΟι Ρώσοι χειριστές αντιμετώπισαν προβλήματα στη μετάδοση δεδομένων λίγο μετά την εκτόξευση του σκάφους την Τρίτη με πύραυλο Σογιούζ από το κοσμοδρόμιο του Μπαϊκονούρ και αποφάσισαν να αλλάξουν το σχέδιο πτήσης του.\r\nΩστόσο, οι αρχές είχαν διαβεβαιώσει χθες, Τρίτη, πως το Progress συνεχίζει την αποστολή του και θα προσδεθεί στις 30 Απριλίου στον ISS, ενώ η πτήση προβλεπόταν αρχικά να διαρκέσει έξι ώρες.\r\nΗ NASA από την πλευρά της επιβεβαίωσε ότι το σκάφος «δεν μεταφέρει εφόδια ουσιαστικής σημασίας για τη λειτουργία του αμερικανικού τμήματος του ISS».', 'images/imagefornews/nasa-pyraylos-arhs.jpg', 2, '2015-07-01 19:48:18', 4, '', ''),
(32, 'Δείτε τι τράβηξε με τη φωτογραφική του ένας 14χρονος κατά την εκδρομή του σχολείου του σε νεκροταφείο πολέμου', 'Σε εκδρομή που πραγματοποίησε σχολείο, σε γερμανικό νεκροταφείο πολέμου στη Γαλλία, ένα 14χρονο αγόρι από τη Βρετανία απαθανάτισε με το κινητό του τηλέφωνο μια φιγούρα που', 'Ο 14χρονος έφηβος Mitch Glover, από το Leamington Spa, επισκέφτηκε λόγω σχολικής εκδρομής το νεκροταφείο Neuville-St Vaast, στη βόρεια Γαλλία.Όταν έπεστρεψε απ τν εκδρομή παρατήρησε στην φωτογραφία που τράβηξε, μια φιγούρα που μοιάζει με άνθρωπο που φοράει τη στολή ενός Σκωτζέζικου τάγματος.Το νεκροταφείο Neuville-St Vaast είναι το μεγαλύτερο γερμανικό νεκροταφείο στη Γαλλία, στο οποίο υπάρχουν 44.833 τάφοι.Η 50χρονη μητέρα του Mitch, Sue, δήλωσε στην Daily Mail «Είδα ότι δεν έμοιαζε με Γερμανό στρατιώτη, τότε ένας φίλος, μου είπε ότι η φιγούρα μοιάζει να φοράει τα Σκωτζέζικα ρούχα που φορούσαν οι Highlanders Seaforth κατά το Α’ Παγκοσμίο Πολέμο. «Φαίνεται σαν κρατάει κάτι στο χέρι.Μοιάζει να είναι σε επιφυλακή κρατώντας ένα τουφέκι» δήλωσε η Βρετανίδα μητέρα του έφηβου.Οι Seaforth Highlanders ήταν ένα ιστορικό τάγμα του Βρετανικού στρατού.Κατά τη διάρκεια του Α’ Παγκοσμίου Πολέμου πήραν μέρος στη Μάχη του ποταμού Μάρνη και στη Μάχη στο ποταμό Aisne.Στα μέσα Σεπτεμβρίου του 1914, το τάγμα συμμετείχε ενεργά στη μάχη του ποταμού Aisne και υπέστησαν σοβαρές απώλειες.ΠΗΓΗ: www.diaforetiko.gr', 'images/imagefornews/20140625_53ab1ae3c7473.jpg', 9, '2015-07-01 19:17:34', 1, '', ''),
(33, 'NSA: 12.000 αιτήματα για παρακολούθηση στην BND', 'Η γερμανική υπηρεσία πληροφοριών BND, που κατηγορείται ότι κατασκόπευε εταιρείες και δημόσιους οργανισμούς για λογαριασμό της αμερικανικής Εθνικής Υπηρεσίας Ασφαλείας (NSA), είχε διαγράψει 12.000 αιτήματα για την παρακολούθηση αξιωματούχων, υποστηρίζει το περιοδικό Der Spiegel.', 'Σύμφωνα με γερμανικά μέσα ενημέρωσης, η BND παρακολουθούσε μεταξύ άλλων το υπουργείο Εξωτερικών της Γαλλίας, τη γαλλική προεδρία και την Ευρωπαϊκή Επιτροπή. \r\n\r\nΗ λαϊκή εφημερίδα Bild έγραψε ότι στόχος παρακολουθήσεων είχαν γίνει και αυστριακοί αξιωματούχοι.\r\n\r\nΩστόσο το Spiegel γράφει ότι το 2013 ένας γερμανός πράκτορας μελέτησε τα αιτήματα που είχαν υποβληθεί από την NSA στην BND, στο πλαίσιο της συνεργασίας που είχαν ξεκινήσει οι δύο χώρες από το 2002 για την καταπολέμηση της τρομοκρατίας. \r\n\r\nΧρησιμοποιώντας λέξεις-κλειδιά όπως "diplo", "bundesamt" και "gov" (χαρακτηριστικές των διευθύνσεων ηλεκτρονικού ταχυδρομείου που χρησιμοποιούν αξιωματούχοι), ο πράκτορας εντόπισε 12.000 αιτήματα. \r\n\r\nΜεταξύ αυτών περιλαμβάνονταν οι διευθύνσεις "ορισμένου αριθμού υψηλόβαθμων αξιωματούχων της γαλλικής διπλωματίας", καθώς επίσης και μελών "θεσμών της Ευρωπαϊκής Ένωσης και πολλών ευρωπαϊκών χωρών", συνεχίζει το περιοδικό.\r\n\r\nΣτις 14 Αυγούστου 2013 ο πράκτορας απευθύνθηκε στους ανωτέρους και ζητώντας να του πουν τι να κάνει με τα αιτήματα αυτά. Η απάντηση που πήρε ήταν "να τα διαγράψει".\r\n\r\nΗ εφημερίδα Bild έγραψε την Δευτέρα ότι η BND προσπαθούσε από το 2005 να κατασκοπεύσει την EADS (νυν Airbus) και τη Eurocopter (σήμερα Airbus Helicopters) για λογαριασμό της NSA. \r\n', 'images/imagefornews/image.jpg', 2, '2015-07-01 19:46:04', 1, '', ''),
(34, 'Γνωρίστε το Microsoft Edge, το διάδοχο του Internet Explorer – Πώς θα επηρεάσει τις iOS και Android συσκευές σας;', 'Όπως αναφέρεται σε σχετικό δημοσίευμα του Ars Technica, ο νέος browser - διάδοχος του διάσημου Internet Explorer, αποτελεί γεγονός.', 'Διαθέτει νέα μηχανή, ενώ στα νέα χαρακτηριστικά του περιλαμβάνεται η νέα σελίδα New Tab, η οποία προβάλλει σελίδες, εφαρμογές που σχετίζονται με αυτές και άλλο υλικό, βάσει υλικού που έχει δοθεί από τον χρήστη στην ψηφιακή βοηθό της εταιρείας, Cortana. Επίσης, μεταξύ των άλλων χαρακτηριστικών του περιλαμβάνεται το Web Extensions, με browser extensions γραμμένα σε HTML και JavaScript.\r\n\r\nTα εν λόγω extensions μπορούν να εισαχθούν από άλλους browsers, όπως ο Chrome και ο Firefox, με λίγες μόνο αλλαγές. Επίσης, στον Edge έχει εισαχθεί και το Reddit Enhancement Suite, δημοφιλές plugin που λειτουργεί με κάθε browser εκτός του Explorer.\r\n\r\nΌπως σημειώνεται σε δημοσίευμα του Mashable, o Εdge χαρακτηρίζεται από μινιμαλιστικό σχεδιασμό, ειδικά για να προβάλλονται οι ιστοσελίδες σε πρώτο πλάνο, αφήνοντας σε δεύτερη μοίρα άλλα στοιχεία (εικονίδια κλπ) που θα μπορούσαν να επιβαρύνουν το φόρτωμά τους.\r\n\r\nΕπίσης, όπως ήταν αναμενόμενο, έχει δοθεί ιδιαίτερη μέριμνα στην ενσωμάτωση της ψηφιακής «βοηθού», Cortana, η οποία θα ενεργοποιείται όταν ο χρήστης «χτυπά» keywords στην address bar ή επιλέγει θέματα σε μια ιστοσελίδα. O Edge θα αρχίσει να διατίθεται με τα Windows 10 αργότερα μέσα στο έτος.\r\n\r\nΜεταξύ των σημαντικών ανακοινώσεων της συνδιάσκεψης ήταν και η διευκόλυνση της έλευσης εφαρμογών iOS και Android στην πλατφόρμα εφαρμογών των Windows.\r\n\r\nΗ εταιρεία παρουσίασε δύο νέα software development kits τα οποία μακροπρόθεσμα θα επιτρέψουν σε χρήστες του λειτουργικού να «τρέχουν» εφαρμογές για τα λειτουργικά της Google και της Apple σε συσκευές με Windows 10, φέρνοντας περισσότερες εφαρμογές στο λειτουργικό - ναυαρχίδα της.\r\n\r\nΕπίσης, στα τηλέφωνα με Windows θα περιλαμβάνεται και ένα υποσύστημα Android, όπως έγινε γνωστό.', 'images/imagefornews/microsoft-edge-500-800x547.jpg', 8, '2015-07-04 12:39:26', 26, '', ''),
(35, 'Ταινία της Ημέρας-Fast & Furious 7: Μαχητές των Δρόμων ', 'Ο αδελφός του τραυματισμένου γκάνγκστερ Όουεν Σο, ένας θανάσιμα επικίνδυνος Βρετανός πρώην πράκτορας, αναλαμβάνει να εκδικηθεί την παρέα του Ντομ Τορέτο. ', 'Ο τελευταίος δέχεται την απροσδόκητη και πολύτιμη βοήθεια ενός μυστικού Αμερικανού πράκτορα, για χάρη του οποίου, όμως, πρέπει πρώτα να κλέψει μια τεχνολογικά προωθημένη συσκευή εντοπισμού από έναν διαβόητο τρομοκράτη.', 'images/imagefornews/maxresdefault.jpg', 9, '2015-07-03 11:56:35', 1, '', ''),
(36, 'Σκηνές εμφυλίου στην πΓΔΜ μετά τις αιματηρές συγκρούσεις', 'Μνήμες εμφυλίου αναβιώνουν στην πρώην Γιουγκοσλαβική Δημοκρατία της Μακεδονίας, μετά τις αιματηρές συγκρούσεις που σημειώθηκαν σε αλβανόφωνη συνοικία της πόλης Κουμάνοβο. Στις συγκρούσεις σκοτώθηκαν έξη αστυνομικοί και αρκετοί ένοπλοι, οι οποίοι εικάζεται πως εισήλθαν στη χώρα από το Κόσοβο. Συνεχίζεται η ανταλλαγή πυρών και την Κυριακή.', 'Σύμφωνα με την υπουργό Εσωτερικών της πΓΔΜ, Γκορντάνα Γιανκούλοφσκα, στη διάρκεια των πολύωρων ένοπλων συγκρούσεων μεταξύ ισχυρών δυνάμεων της αστυνομίας και «τρομοκρατικής ένοπλης ομάδας» σκοτώθηκαν υπήρξαν και πάνω από 30 τραυματισμοί. Πρόσθεσε δε ότι μέχρι στιγμής η αστυνομία δεν διαθέτει πληροφορίες πως μεταξύ των νεκρών είναι και άμαχοι πολίτες.\r\nΗ υπουργός Εσωτερικών σημείωσε ότι οι περισσότεροι ένοπλοι παραδόθηκαν στην αστυνομία μετά από μάχες που διήρκεσαν 15 και πλέον ώρες και πως η επιχείρηση της αστυνομίας συνεχίζεται, μέχρι την εξουδετέρωση «και του τελευταίου τρομοκράτη». Η ίδια ανέφερε ότι κάποια από τα μέλη της τρομοκρατικής ένοπλης ομάδας είναι από την πΓΔΜ και κάποια άλλα προέρχονται από «γειτονική χώρα».\r\nΣυμπλήρωσε ότι η ένοπλη ομάδα, τον αριθμό των μελών της οποίας δεν ήταν σε θέση να διευκρινίσει, είχε σκοπό να επιτεθεί σε αστυνομικά τμήματα και σε κρατικούς θεσμούς της χώρας. Σημείωσε ότι οι «τρομοκράτες» προφανώς θέλησαν να εκμεταλλευτούν την κατάσταση που επικρατεί στην ΠΓΔΜ (στην οποία έχει εκδηλωθεί βαθιά πολιτική κρίση) και να αποσταθεροποιήσουν τη χώρα.\r\nΙσχυρές δυνάμεις των ειδικών δυνάμεων της αστυνομίας, με τεθωρακισμένα οχήματα και ελικόπτερα εισέβαλαν νωρίς το πρωί του Σαββάτου στην αλβανόφωνη συνοικία του Κουμάνοβο για τον εντοπισμό και την εξουδετέρωση «τρομοκρατικής ένοπλης ομάδας». Κατά την είσοδο των δυνάμεων της αστυνομίας στη συνοικία της πόλης Κουμάνοβο, οι ένοπλοι Αλβανοί άνοιξαν πυρ με όπλα, βόμβες και ρουκέτες, ανέφερε η αστυνομία.\r\nΤο Κουμάνοβο, μία πόλη με περισσότερους από 80.000 κατοίκους, η οποία απέχει 40 χλμ. από τα Σκόπια και βρίσκεται κοντά στα σύνορα της ΠΓΔΜ με τη Σερβία, θύμιζε το βράδυ του Σαββάτου "πόλη φάντασμα", καθώς ο πληθυσμός της πόλης κλείστηκε στα σπίτια του, τα περισσότερα καταστήματα δεν λειτουργούσαν και στους δρόμους δεν κυκλοφορούσαν παρά ελάχιστα οχήματα.', 'images/imagefornews/newego_LARGE_t_1101_54502855_type13031.jpg', 2, '2015-07-02 17:31:30', 23, '', ''),
(37, 'Τσίπρας: Δεν υποκύπτουμε σε πιέσεις - Τώρα αρχίζει η πραγματική διαπραγμάτευση\r\n\r\n', '«Είμαστε υποχρεωμένοι να μην υποκύψουμε σε πιέσεις και εκβιασμούς» διεμήνυσε ο πρωθυπουργός Αλέξης Τσίπρας κατά την ομιλία του στην ΚΟ του ΣΥΡΙΖΑ, συντηρώντας φαινομενικά το κλίμα ρήξης', '«Είμαστε υποχρεωμένοι να μην υποκύψουμε σε πιέσεις και εκβιασμούς» διεμήνυσε ο πρωθυπουργός Αλέξης Τσίπρας κατά την ομιλία του στην ΚΟ του ΣΥΡΙΖΑ, συντηρώντας φαινομενικά το κλίμα ρήξης με τους πιστωτές. «Μπαίνουμε στην τελική ευθεία... Τώρα αρχίζει η πραγματική διαπραγμάτευση», ανέφερε πάντως λίγο αργότερα. «Δεν δεχόμαστε απειλές... Η Ευρώπη να σταματήσει να πυροβολεί τα πόδια της» σημείωσε ο πρωθυπουργός. Ο στόχος δεν είναι να πετύχουμε μία συμφωνία, συμπλήρωσε, αλλά μία λύση στο χρηματοδοτικό πρόβλημα της χώρας.\r\n«Δεν λειτουργούμε με το άγχος και την πίεση της στιγμής, έχουμε μπροστά μας εντολή τετραετίας. Σε βάθος τετραετίας ξηλώνουμε το μνημόνιο» υπογράμμισε ο πρωθυπουργός.«Οι πιέσεις εξυπηρετούν πολιτικές σκοπιμότητες για την ταπείνωση της ελληνικής κυβέρνησης, όχι μόνο της κυβέρνησης, αυτό είναι το λιγότερο, αλλά και του ελληνικού λαού» τόνισε. «Παρά τις μνημονιακές παγίδες εντός και εκτός της χώρας καταφέραμε να πατήσουμε στα πόδια μας. Σε αυτό το δρόμο σκοπεύουμε να συνεχίσουμε, στο δρόμο των μαχών» επισήμανε, κατηγορώντας τους εταίρους για εσκεμμένη τύφλωση και για επίδειξη πυγμής με στόχο να σκοτώσουν τα συμφέροντα της κοινωνικής πλειοψηφίας.Ο Αλέξης Τσίπρας σημείωσε πως στόχος είναι μια συμφωνία με σαφή αναδιανεμητικά χαρακτηριστικά και όχι προτάσεις από ΔΝΤ και ΕΕ που δεν έχουν λογική. «Η εντολή του ελληνικού λαού είναι σαφής... Να τερματίσουμε την πολιτική της λιτότητας» παρατήρησε.Ο πρωθυπουργός διευκρίνισε ότι η κυβέρνηση είναι έτοιμη να συζητήσει για να φτάσει σε αμοιβαία συμφωνία η οποία δεν θα πρέπει να αποτελεί συνέχεια της μνημονιακής πολιτικής.«Μας ζητείται να αποδεχθούμε μια συμφωνία που θα παρασύρει μια οικονομία σε μια υφεσιακή δίνη. Είμαστε υποχρεωμένοι να πράξουμε το καθήκον μας... Να μην υποκύψουμε σε εκβιασμούς» πρόσθεσε.Σύμφωνα με τον πρωθυπουργό, η συμφωνία πρέπει να περιέχει δεσμευτικές ρήτρες για το χρηματοδοτικό πρόβλημα της Ελλάδος, που επιδεινώθηκε με ευθύνη των θεσμών. «Οι ευρωπαίοι λειτουργούν α λα καρτ, θέλουν τις σκληρές προτάσεις του ΔΝΤ, αλλά δεν συζητούν τη θέση του για αναδιάρθρωση του χρέους» επανέλαβε. \r\n«Η ελληνική κυβέρνηση έχει καταθέσει τις προτάσεις της και αναμένει με ενδιαφέρον την επίσημη απάντηση των θεσμών» δήλωσε από την πλευρά του ο κυβερνητικός εκπρόσωπος Γαβριήλ Σακελλαρίδης. Επισήμανε ακόμη πως η κυβέρνηση έχει αποδείξει έμπρακτα και κατ’ επανάληψη πως θα εξακολουθήσει να εργαζόμαστε για την εξεύρεση λύσης διευκρίνισε.\r\n«Η εντολή που πήραμε από το λαό δεν είναι εντολή δημιουργικής ασάφειας, πήραμε σαφή εντολή απο το λαό για λύση», είπε δε ο Αλέξης Τσίπρας. \r\n', 'images/imagefornews/1.jpg', 1, '2015-07-02 08:59:02', 17, '', ''),
(38, 'Ο Χάρι Πότερ πατά το θεατρικό σανίδι', 'Ο Χάρι Πότερ επιστρέφει. Οχι στα βιβλιοπωλεία ή στο σινεμά, αλλά στο θέατρο.Οπως ανακοίνωσε η συγγραφέας Τζ. Κ. Ρόουλινγκ στη 18η επέτειο της έκδοσης του πρωταρχικού βιβλίου «Ο Χάρι Πότερ και η φιλοσοφική λίθος», το θεατρικό έργο', '«Ο Χάρι Πότερ και το καταραμένο παιδί» θα κάνει παγκόσμια πρεμιέρα στο θέατρο Palace του Λονδίνου το 2016 και αποτελεί μια ανέκδοτη ιστορία της σειράς που δεν έχει άλλη θέση παρά στη θεατρική σκηνή.\r\nΗ Ρόουλινγκ χαρακτήρισε, ακόμη, το θεατρικό, αποτέλεσμα της συνεργασίας της με τον συγγραφέα Τζακ Θορν και τον σκηνοθέτη Τζον Τίφανι, μια «πραγματική απόλαυση για τους θαυμαστές», χωρίς να θέλει να κάνει περαιτέρω αποκαλύψεις.\r\nΣτόχος των συντελεστών είναι να διατηρηθεί ζωντανό το στοιχείο της έκπληξης, ενώ η ιστορία και όλα τα παραλειπόμενα θα αποκαλυφθούν σταδιακά μέσα στο καλοκαίρι στην επίσημη ιστοσελίδα της παράστασης. Τα εισιτήρια για το έργο, που φαντάζει σίγουρη εμπορική επιτυχία δεδομένης της τεράστιας δημοφιλίας του Χάρι Πότερ, θα αρχίσουν να προπωλούνται από το ερχόμενο φθινόπωρο.\r\nΘυμίζουμε ότι τον Νοέμβριο του 2016 αναμένεται στις αίθουσες και το πρίκουελ της κινηματογραφικής σειράς με τίτλο «Fantastic beasts and where to find them», στο οποίο ήρωας είναι ο συγγραφέας με το όνομα Νιουτ Σκαμάντερ.', 'images/imagefornews/o-DANIEL-RADCLIFFE-facebook.jpg', 9, '2015-07-01 14:50:26', 14, '', ''),
(39, 'Μηχανογραφικό 2015: Νηπιαγωγών και Προσχολικής Αγωγής των ΤΕΙ\r\n', 'Τα τμήματα νηπιαγωγών αποτελούν μία από τις δημοφιλέστερες επιλογές πολλών μαθητριών κυρίως της θεωρητικής κατεύθυνσης. Τα τμήματα δηλώνονται φυσικά από μαθητές της θετικής και τεχνολογικής κατεύθυνσης, αφού είναι κοινά για το 1ο και 2ο επιστημονικό πεδίο , άλλα η «δεξαμενή» τους ανέκαθεν ήταν η θεωρητική κατεύθυνση. ', 'Στα τμήματα αυτά στρέφονται πολλοί μαθητές που τρέφουν αγάπη για τα μικρά παιδιά και προσδοκούν να εργαστούν σε ένα νηπιαγωγείο. Ακολούθως θα παρουσιάσουμε τις επαγγελματικές διεξόδους και την κατάσταση που επικρατεί στον εργασιακό στίβο.Τα τμήματα νηπιαγωγών αποτελούν μία από τις δημοφιλέστερες επιλογές πολλών μαθητριών κυρίως της θεωρητικής κατεύθυνσης. Τα τμήματα δηλώνονται φυσικά από μαθητές της θετικής και τεχνολογικής κατεύθυνσης, αφού είναι κοινά για το 1ο και 2ο επιστημονικό πεδίο , άλλα η «δεξαμενή» τους ανέκαθεν ήταν η θεωρητική κατεύθυνση. Στα τμήματα αυτά στρέφονται πολλοί μαθητές που τρέφουν αγάπη για τα μικρά παιδιά και προσδοκούν να εργαστούν σε ένα νηπιαγωγείο. Ακολούθως θα παρουσιάσουμε τις επαγγελματικές διεξόδους και την κατάσταση που επικρατεί στον εργασιακό στίβο.Στον ακόλουθο πίνακα μπορούν οι ενδιαφερόμενοι να δουν όλα τα τμήματα νηπιαγωγών, τη βάση εισαγωγής και το συνολικό αριθμό πρώτων προτιμήσεων. Από τα στοιχεία προκύπτει ότι 1.895 υποψήφιοι Ημερήσιων Λυκείω είχαν ως πρώτη τους επιλογή ένα από τα 9 τμήματα νηπιαγωγών.Πολλοί μαθητές ρωτούν γιατί πολλά από τα τμήματα δε φέρουν την ονομασία «Νηπιαγωγών». Πράγματι από τον πιο πάνω πίνακα διαπιστώνουμε ότι μόνο τα τμήματα Ιωαννίνων και Φλώρινας φέρουν την εν λόγω ονομασία. Το γεγονός αυτό δεν θα πρέπει να ανησυχεί μαθητές και γονείς γιατί και τα άλλα 7 τμήματα είναι ισότιμα όσοι τα επιλέξουν εντάσσονται στον κλάδο ΠΕ60 Νηπιαγωγών.Προσοχή στα τμήματα Προσχολικής Αγωγής των ΤΕΙ. Ομοιότητες και διαφορές ως προς τα επαγγελματικά δικαιώματα!Στο μηχανογραφικό δελτίο του 2015 υπάρχουν και 3 τμήματα των ΤΕΙ με την ονομασία Προσχολικής Αγωγής. Τα τμήματα αυτά δεν θα πρέπει να τα συγχέουν οι μαθητές με αυτά των ΑΕΙ που έχουν παραπλήσια ονομασία. Οι απόφοιτοι των τμημάτων των ΤΕΙ είναι βρεφονηπιαγωγοί   και όχι νηπιαγωγοί. Δηλαδή οι απόφοιτοι των ΤΕΙ εντάσσονται στον κλάδο ΤΕ βρεφονηπιοκόμων και οι απόφοιτοι των ΑΕΙ στον κλάδο ΠΕ Νηπιαγωγών. Φυσικά κοινό στοιχείο και των δυο ειδικοτήτων είναι η ενασχόληση με τα παιδιά. Το στοιχείο στο οποίο διαφοροποιούνται πρακτικά είναι η ηλικία των παιδιών ( αν και στις ιστοσελίδες των τμημάτων προσχολικής αγωγής αναφέρεται ως ηλικία των παιδιών από 2 μηνών έως 5,5 χρονών). Για παράδειγμα σε κανονισμούς λειτουργίας δημοτικών παιδικών σταθμών ορίζεται το εξής:', 'images/imagefornews/2104253_b1.jpg', 5, '2015-07-01 10:02:10', 0, '', ''),
(40, 'Μηχανογραφικό 2015: Αξιόλογες σχολές και για χαμηλές βαθμολογίες\r\n', 'Μετά την ανακοίνωση των βαθμολογιών ξεκινά το δύσκολο έργο της συμπλήρωσης του μηχανογραφικού δελτίου. Κάποιοι μαθητές μπορεί να έχουν απογοητευτεί γιατί δεν κατάφεραν να γράψουν όσο θα ήθελαν, όμως δεν πρέπει να απογοητεύονται καθώς υπάρχουν στο μηχανογραφικό καλές επιλογές για όλα τα βαθμολογικά βαλάντια ακόμα και για τα πιο μικρά. ', 'Όσοι επιθυμούν να γίνουν φιλόλογοι θα πρέπει να γνωρίζουν ότι το δικαίωμα ένταξης στον κλάδο των φιλολόγων το έχουν όχι μόνο οι απόφοιτοι των τμημάτων Φιλολογίας άλλα και οι απόφοιτοι των τμημάτων Ιστορίας-Αρχαιολογίας, Ιστορίας, Ιστορίας-Εθνολογίας, Φιλοσοφίας Παιδαγωγικής και Ψυχολογίας, Φιλοσοφίας και Παιδαγωγικής, Φιλοσοφίας, Φιλοσοφικών και κοινωνικών σπουδών.\r\nΤο τμήμα Βαλκανικών Σλαβικών και Ανατολικών Σπουδών του Πανεπιστημίου Μακεδονίας περιέχει στο πρόγραμμα του ξένες γλώσσες, όπως τα Ρώσικα, Σέρβικα, Ρουμάνικα, Βουλγαρικά κ.α., μαθήματα οικονομικών και δίνει τη δυνατότητα στους απόφοιτους να εγγραφούν στο Οικονομικό Επιμελητήριο και να εργαστούν ως οικονομολόγοι. Το δικαίωμα εγγραφής στο Οικονομικό Επιμελητήριο το έχουν και όσοι επιλέξουν τα τμήματα Δημόσιας Διοίκησης Παντείου, Διεθνών και Ευρωπαϊκών Σπουδών Πειραιά.Οι απόφοιτοι του τμήματος Κοινωνικής Διοίκησης και Πολιτικής Επιστήμης με εισαγωγική κατεύθυνση Κοινωνική Διοίκηση έχουν τη δυνατότητα να εργαστούν ως κοινωνικοί λειτουργοί.\r\n\r\nΕιδική Αγωγή\r\n\r\n\r\n\r\nΤο δικαίωμα εργασίας ως δασκάλων ειδικής αγωγής το έχουν οι απόφοιτοι του Τμήματος Ειδικής Αγωγής Πανεπιστημίου Θεσσαλίας άλλα και οι απόφοιτοι του Τμήματος Εκπαιδευτικής και Κοινωνικής Πολιτικής του Πανεπιστημίου Μακεδονίας. Προσοχή το τμήμα Κοινωνικής και Εκπαιδευτικής Πολιτικής Κορίνθου δε σου δίνει τη δυνατότητα αυτή.\r\n\r\nΕπάγγελμα Μουσικός\r\n\r\n\r\nΑν κάποιοι μαθητές γνωρίζουν ένα μουσικό όργανο άλλα δεν έδωσαν εξετάσεις στα δυο ειδικά μαθήματα που απαιτούνται για τα τμήματα Μουσικών Σπουδών μπορούν να επιλέξουν το τμήμα Λαϊκής και Παραδοσιακής Μουσικής στην Άρτα που τους δίνει το δικαίωμα να εργαστούν ως καθηγητές μουσικής σε δημόσια και ιδιωτικά σχολεία.\r\n\r\nΔιοίκηση Επιχειρήσεων από Θεωρητική\r\n\r\n\r\nΟλιγάριθμα είναι τα Τμήματα των ΤΕΙ του 1ου Επιστημονικού Πεδίου και ένα εξ αυτών που παρουσιάζει ενδιαφέρον είναι το Τμήμα Διοίκησης Επιχειρήσεων του ΤΕΙ Ηπείρου. Όπως αναφέρεται στην ιστοσελίδα του Τμήματος η εισαγωγική κατεύθυνση Διοίκηση Επιχειρήσεων έχει δύο κατευθύνσεις προχωρημένου εξαμήνου: Α. Διοίκηση Επιχειρήσεων Β. Εφαρμοσμένων Ξένων Γλωσσών στη Διοίκηση και την Οικονομία.\r\n\r\n \r\n\r\nΠληροφορική από Θεωρητική\r\n\r\n \r\n\r\nΤο Τμήμα Πολιτισμικής Τεχνολογίας και Επικοινωνίας, που βρίσκεται στη Μυτιλήνη, με βάση 11.031 μόρια δίνει τη δυνατότητα στους αποφοίτους να εργαστούν ως εκπαιδευτικοί Πληροφορικής αφού εντάσσονται στον κλάδο ΠΕ19. Επαγγελματικές προοπτικές και εξέλιξη αποφοίτων\r\n\r\n \r\n\r\n \r\n\r\n2ο Επιστημονικό Πεδίο\r\n\r\nΠολλές οι διέξοδοι για μαθηματικούς, φυσικούς, χημικούς, βιολόγους\r\n\r\nΤα τμήματα Μαθηματικών, Φυσικής, Χημείας άλλα και Βιολογίας απαιτούν την προσοχή των μαθητών καθώς η διέξοδός τους δεν είναι μόνο η εκπαίδευση όπως πιστεύουν οι περισσότεροι μαθητές και γονείς. Τα τμήματα έχουν πολλές διεξόδους σε περίπτωση που ο μαθητής εκπονήσει και ένα μεταπτυχιακό. Για παράδειγμα ο μαθηματικός μπορεί να ασχοληθεί με τα οικονομικά ή την πληροφορική, ο Χημικός με την οινολογία, τα τρόφιμα κλπ.\r\n\r\n', 'images/imagefornews/pan4_2015_5_18_16_58_22_b2.jpg', 5, '2015-07-01 10:02:10', 0, '', ''),
(41, '«Ξύπνησε» το ρομπότ Philae', '«Ξύπνησε» το ρομπότ Philae, το οποίο θεωρούνταν χαμένο, επτά μήνες μετά την προσεδάφισή του σε κομήτη, σύμφωνα με όσα ανακοίνωσε σήμερα ο Ευρωπαϊκός Οργανισμός Διαστήματος (ESA).Σύμφωνα με την ανακοίνωση', 'Σύμφωνα με την ανακοίνωση, την Πέμπτη έλαβε για πρώτη φορά σήματα από το ρομπότ, το οποίο άρχισε να «μιλάει» με την ομάδα του στη Γη για πρώτη φορά αφότου έπεσε σε νάρκη μετά την προβληματική προσεδάφισή του στον κομήτη τον Νοέμβριο.\r\n\r\n«Το Philae είναι πολύ καλά και είναι έτοιμο για επιχειρήσεις», ανέφερε ο διευθυντής του προγράμματος Στέφαν Ουλαμέκ σε δήλωσή του στον ιστότοπο της ESA. \r\n\r\nΟι επιστήμονες πιστεύουν πως το ρομπότ δέχεται αυξημένες ποσότητες ηλιακού φωτός καθώς ο κομήτης πλησιάζει στον ήλιο, με αποτέλεσμα οι ηλιακοί συλλέκτες του να μπορούν να παράγουν την ενέργεια που χρειάζεται για να αποστέλλει δεδομένα. Καθώς βρισκόταν έως τώρα στη σκιά οι ηλιακοί συλλέκτες του Philae, που επρόκειτο να τροφοδοτούν με ενέργεια το ρομπότ μετά την εξάντληση των μπαταριών του μερικές ημέρες μετά την προσεδάφιση, δέχονταν ηλιακό φως για πολύ λιγότερες από τις έξι με επτά ώρες την ημέρα που χρειαζόταν. \r\n\r\nΟι επιστήμονες ελπίζουν πως δείγματα που θα εξορύξει το Philae από τον κομήτη θα αποκαλύψουν λεπτομέρειες για την εξέλιξη των πλανητών και ενδεχομένως και της ζωής. Οι βράχοι και οι πάγοι που αποτελούν τους κομήτες διατηρούν αρχαία οργανικά μόρια σαν μια χρονοκάψουλα.', 'images/imagefornews/1081610_h0496974.limghandler.jpg', 8, '2015-07-02 10:41:19', 7, '', '');
INSERT INTO `nea` (`ID`, `Titlos_arthrou`, `Small_text`, `Keimeno`, `Eikona`, `category`, `Time`, `counter`, `url`, `source`) VALUES
(42, 'Ημερίδα «Καλύτερη Πόλη-τρόποι και πράξεις βελτίωσης της ζωής στις πόλεις»', 'Πώς η περιβαλλοντική κατάσταση των πόλεων επηρεάζει την ποιότητα ζωής; Τι θα μπορούσε να αλλάξει προκειμένου να βελτιωθεί η καθημερινότητα των πολιτών; Ποια είναι τα καλά παραδείγματα και οι καλές πρακτικές που μπορούν να αναλάβουν οι δημόσιες υπηρεσίες και οι οργανώσεις της κοινωνίας των πολιτών;', 'Σε αυτά τα ερωτήματα θα επιχειρήσει να δώσει απάντηση η ημερίδα που διοργανώνει στις 30 Ιουνίου 2015 η WWF – Καλύτερη Ζωή, το πρόγραμμα του WWF Ελλάς για τη βελτίωση της καθημερινότητας των πολιτών.\r\n\r\nΗ ημερίδα με τίτλο «Καλύτερη Πόλη – τρόποι και πράξεις βελτίωσης της ζωής στις πόλεις» θα πραγματοποιηθεί στο Impact Hub Athens (Καραϊσκάκη 28, Ψυρρή, στάση μετρό Μοναστηράκι) κι έχει ως κύριο στόχο την ενημέρωση και ευαισθητοποίηση των πολιτών και των στελεχών της Δημόσιας Διοίκησης για δράσεις βελτίωσης της καθημερινότητας τόσο της ζωής των πολιτών όσο και των περιβαλλοντικών συνθηκών σε κάθε πόλη της χώρας.\r\n\r\nΜεταφορές, διατροφή, αστικό πράσινο, απορρίμματα, ενέργεια και καταναλωτικά πρότυπα, είναι τα θέματα που θα συζητηθούν σε μια προσπάθεια να δοθεί το έναυσμα για περισσότερες πρωτοβουλίες σε επίπεδο τοπικής αυτοδιοίκησης. Στην ημερίδα θα παρουσιαστούν θετικές πρωτοβουλίες που συμβαίνουν σε Ελλάδα και εξωτερικό και θα υπάρξει μια σφαιρική παρουσίαση για τους τρόπους που μπορεί να γίνει μια πόλη της Ελλάδας πραγματικά βιώσιμη!', 'images/imagefornews/imerida-kalyteri-poli-123201.jpg', 7, '2015-07-02 08:23:50', 1, '', ''),
(43, 'Πακιστάν: άνθρωποι νεκροί σαν τις μύγες λόγω καύσωνα – Πάνω από 800 θύματα', 'Τουλάχιστον 780 άνθρωποι στο Καράτσι του Πακιστάν και ακόμα 30 σε άλλα σημεία της επαρχίας Σιντ έχουν χάσει τη ζωή τους εξαιτίας του καταστροφικού κύματος καύσωνα που πλήττει την ασιατική χώρα.', 'κπρόσωπος του Οργανισμού Πρόνοιας Edhi δήλωσε στο Γαλλικό Πρακτορείο ότι τα νεκροτομεία τους έχουν παραλάβει εκατοντάδες πτώματα και πλέον δεν υπάρχει χώρος για άλλα.\r\n\r\nΑναφορές κάνουν λόγο για ανθρώπους που πέφτουν νεκροί στο δρόμο λόγω των ακραίων θερμοκρασιών που επικρατούν.\r\n\r\nΤην Τρίτη η θερμοκρασία έφθασε τους 45 βαθμούς Κελσίου με τον πρωθυπουργό του Πακιστάν, Ναουάζ Σαρίφ να ζητά μέτρα έκτακτης ανάγκης με τη συμμετοχή του στρατού που συστήνει κέντρα θερμοπληξίας. Η Εθνική Διαχειριστική Αρχή Καταστροφών της χώρας (NDMA) δήλωσε ότι είχε λάβει εντολές από τον κ Σαρίφ να αναλάβει άμεση δράση για την αντιμετώπιση της κρίσης.\r\n\r\nΟι κάτοικοι της περιοχής εμφανίζονται οργισμένοι με τις αρχές, διότι οι περικοπές ρεύματος έχουν περιορίσει τη χρήση των κλιματιστικών μονάδων και των ανεμιστήρων, όπως λένε οι ανταποκριτές του BBC στην περιοχή.\r\n\r\nΤην κατάσταση επιδεινώνει η μαζική αποχή από την λήψη νερού λόγω της νηστείας που επιβάλλει το Ραμαζάνι, ενώ υποφέρουν ιδιαιτέρως ηλικιωμένοι και ασθενείς που νοσηλεύονται σε νοσοκομεία.\r\n\r\nΟι καύσωνες δεν σπανίζουν κατά τη διάρκεια των καλοκαιρινών μηνών στο Πακιστάν, αλλά οι παρατεταμένες διακοπές ρεύματος φαίνεται ότι έχουν επιδεινώσει την κατάσταση παρά τις διαβεβαιώσεις του ίδιου του πρωθυπουργού.', 'images/imagefornews/pakistan-kausonas-123163.jpg', 7, '2015-07-01 10:02:10', 1, '', ''),
(44, 'Νέα νίκη για Εθνική με σούπερ Παπαγιάννη', 'Αν και δυσκολεύτηκε, η Εθνική μας ομάδα επικράτησε 70-65 της Δομινικανής Δημοκρατίας για το Παγκόσμιο πρωτάθλημα U 19.\r\n\r\n', 'Το εθνικό μας συγκρότημα, έχοντας και πάλι ως πρωταγωνιστή τον Παπαγιάννη που βοήθησε πολύ αμυντικά, πέτυχε τη δεύτερη νίκη σε ισάριθμα παιχνίδια στον τέταρτο όμιλο.\r\n\r\n', 'images/imagefornews/1 (1).jpg', 6, '2015-07-02 08:40:49', 5, '', ''),
(45, 'ΠΑΟΚ: Στη λίστα Άρνεσεν ο Τοϊβόνεν', 'Τα δεδομένα για την απόκτηση του Όλα Τοϊβόνεν από την Ρεν εξετάζουν με προσοχή οι άνθρωποι του ΠΑΟΚ.\r\n', 'Ο Σουηδός μεσοεπιθετικός εμφανίζεται, σύμφωνα με δημοσιεύματα από την πατρίδα του, ως στόχος του Φρανκ Άρνεσεν και ο «Δικέφαλος» είναι έτοιμος να... χτυπήσει την πόρτα της γαλλικής ομάδας για την απόκτησή του, ακόμα και με την μορφή του... δανείου.\r\n', 'images/imagefornews/1 (2).jpg', 6, '2015-07-01 10:02:10', 3, '', ''),
(46, 'Εσιέν: «Έτοιμος για Champions League και τίτλους»', 'O Μίκαελ Εσιέν εμφανίζεται έτοιμος για μια νέα αρχή και αποφασισμένος να οδηγήσει τον Παναθηναϊκό στις επιτυχίες.\r\n\r\n', 'Ο Μίκαελ Εσιέν ήρθε για μόνιμη εγκατάσταση στην Ελλάδα τα ξημερώματα της Κυριακής και μίλησε στους δημοσιογράφους που τον περίμεναν. Ο Γκανέζος αποκάλυψε εμφανίστηκε πανέτοιμος για μια νέα αρχή στην καριέρα του και αποφασισμένος να οδηγήσει στο «τριφύλλι» και πάλι στις επιτυχίες, μέχρι και στους ομίλους του Champions League.\r\n\r\n', 'images/imagefornews/1 (3).jpg', 6, '2015-07-01 14:50:54', 3, '', ''),
(47, 'Πώς να μειώσετε τους κινδύνους της οστεοπόρωσης κατά την εμμηνόπαυση\r\n\r\n', 'Κατά την εμμηνόπαυση η μείωση των οιστρογόνων ευνοεί την δραστηριότητα των οστεοκλαστών, κύτταρα που καταστρέφουν τα οστά, ενώ η οστική μάζα μειώνεται μετά τα 30, ανεξάρτητα από το φύλλο, αλλά επιταχύνεται κατά την εμμηνόπαυση.\r\n\r\n', 'Εκτός από την μείωση των οιστρογόνων υπάρχουν και άλλοι παράγοντες που συμβάλλουν στην καταστροφή των οστών όπως είναι η πυκνότητα και η δομή των οστών που αλλάζουν και καθιστούν τα οστά πιο εύθραυστα.\r\nΜια στις δύο γυναίκες εμφανίζει οστεοπόρωση κατά την εμμηνόπαυση και περισσότερες από μια στις δύο υφίσταται κάταγμα.\r\n\r\n\r\n', 'images/imagefornews/6d689ba29d179b3c24b4d51b0ab80618_L.jpg', 4, '2015-07-02 18:12:30', 3, '', ''),
(48, 'Δέκα διατροφικά tips για ένα υγιεινό καλοκαίρι', 'Τα Σαββατοκύριακα του καλοκαιριού στην παραλία, τα μπάρμπεκιου στην πίσω αυλή καθώς και τα υπαίθρια δείπνα είναι επιτέλους εδώ. Αυτές οι συναθροίσεις όμως, συχνά μας φορτώνουν με υψηλής θερμιδικής αξίας ζυμαρικά, τσιπς, παγωτά και κοκτέιλ.', 'Απολαύστε το ζεστό καλοκαίρι με τα αγαπημένα σας πρόσωπα, διατηρώντας παράλληλα τη διατροφή σας υπό έλεγχο', 'images/imagefornews/f9e83c0874a77b3c944a817451ae84d2_L.jpg', 4, '2015-07-02 11:01:13', 21, '', ''),
(49, 'Χυμός πατζάρι: Ένα νέο φυσικό ...αναβολικό', 'Πόσες φορές δεν έχουμε ακούσει για διάφορα φυσικά προϊόντα, τρόφιμα ή μη τα οποία υπόσχονται θεαματική βελτίωση της απόδοσης σε αθλητές και ασκούμενους. Γεγονός βέβαια αποτελεί ότι τα σύγχρονα δεδομένα στον τομέα της αθλητικής διατροφής είναι λίγα και ολοένα εξελίσσονται.\r\n', 'Συνεπώς, θα πρέπει να είμαστε αρκετά σκεπτικιστές απέναντι σε όλο αυτό τον βομβαρδισμό από νέα μαγικά σκευάσματα που απευθύνονται στους αθλητές.\r\n', 'images/imagefornews/1 (5).jpg', 4, '2015-07-03 11:07:02', 6, '', ''),
(50, 'Κίνδυνος για τη διδασκαλία των αρχαίων ελληνικών στη Γαλλία', 'Το Υπουργείο Εξωτερικών παρακολουθεί στενά τις εξελίξεις.της γαλλικής εκπαιδευτικής μεταρρύθμισης αναφορικά με τον κίνδυνο περιορισμού της διδασκαλίας της αρχαίας ελληνικής γλώσσας στη Β/θμια εκπαίδευση και προβαίνει στις απαραίτητες ενέργειες προς τους αρμόδιους φορείς, στο πλαίσιο των διμερών σχέσεών μας με τη Γαλλία.', 'Μεταξύ αυτών των ενεργειών, σημειώνεται ενδεικτικά η πρωτοβουλία της Πρέσβεως μας στο Παρίσι, που έθεσε το θέμα σε ανώτατο επίπεδο στην «Academie francaise» (Γαλλική Ακαδημία), της οποίας η επιρροή σε θέματα πολιτισμού είναι ιδιαίτερα σημαντική στη Γαλλία.Τα παραπάνω αναφέρει μεταξύ των άλλων σε έγγραφο του που διαβίβασε στη Βουλή ο αναπληρωτής υπουργός Ευκλείδης Τσακαλώτος μετά από ερώτηση του Βουλευτή των ΑΝΕΛ Δημήτρη Καμμένου σχετικά με τη διδασκαλία Αρχαίων Ελληνικών στη Γαλλία αλλά και διεθνώς.Ο κ. Καμμένος  μεταξύ των ερωτημάτων που έθεσε ήταν και αυτό για τον τρόπο με τον οποίο σκοπεύει η Ελληνική Κυβέρνηση να προασπίσει τη συνέχιση της διδασκαλίας των Αρχαίων Ελληνικών στα γαλλικά σχολεία. Σημείωνε στο κείμενό του ότι το ποσοστό των μαθητών που επιλέγουν σήμερα τα Αρχαία Ελληνικά στη Γαλλία φαίνεται να μην ξεπερνά το 2,2%. Ο βουλευτής των ΑΝΕΛ αναφερόταν στην εισαγωγική παράγραφο της ερώτησής του στο γεγονός πως σύμφωνα με δημοσιεύματα στους δρόμους κατέβηκαν στις 19/5/2015 χιλιάδες Γάλλοι καθηγητές, που συμμετείχαν στην 24ωρη απεργία, για να διαμαρτυρηθούν ενάντια στις προτεινόμενες αλλαγές στη παιδεία, που μεταξύ των άλλων βάζουν στο στόχαστρο τα Αρχαία και τα Λατινικά τα οποία και θα αντικαταστήσουν με την επιλογή  «Γλώσσες και πολιτισμοί της αρχαιότητας».', 'images/imagefornews/sabuurad_b2.jpg', 5, '2015-07-02 19:52:42', 4, '', ''),
(51, 'Αναστολή Συνόδου Πρυτάνεων λόγω έκτακτων πολιτικών εξελίξεων', 'Ζητά την άμεση αναβολή της κατάθεσης του σχεδίου νόμου του Παιδείας', 'Η 79η Σύνοδος Πρυτάνεων που έχει συγκληθεί στο Ηράκλειο Κρήτης αποφάσισε την άμεση αναστολή των εργασιών της σήμερα Σάββατο λόγω των έκτακτων πολιτικών εξελίξεων.  \r\n\r\nΗ Σύνοδος των Πρυτάνεων παρακολουθεί με προσοχή και αίσθημα ευθύνης όλες τις εξελίξεις, παραμένει σε διαρκή συνεδρίαση και ζητά από τον Υπουργό Παιδείας την άμεση αναβολή κατάθεσης του σχεδίου νόμου για την ανώτατη εκπαίδευση μέχρι να εξομαλυνθεί η κατάσταση.', 'images/imagefornews/prytaneis_2015_4_25_20_5_14_b2.jpg', 5, '2015-07-02 17:30:50', 11, '', ''),
(52, 'Ντε Γκίντος: Να επιστρέψουμε στις διαπραγματεύσεις ακόμα και αν οι έλληνες πουν «ΟΧΙ»', 'Η ευρωζώνη θα συνεχίσει να είναι ανοιχτή σε συνομιλίες με την ελληνική κυβέρνηση ακόμα και αν ο λαός ψηφίσει ‘όχι’ στο δημοψήφισμα της Κυριακής, δήλωσε σήμερα ο Ισπανός υπουργός Οικονομικών Λουίς Ντε Γκίντος.', '«Όλες οι πόρτες είναι ανοιχτές. Εάν ψηφίσουν ‘ναι’ η κυβέρνηση είπε ότι θα συνεχίσει, αλλά εάν ψηφίσουν ‘όχι’, εμείς θα συνεχίσουμε να είμαστε ανοιχτοί σε συνομιλίες μαζί τους», είπε ο ντε Γκίντος σε ραδιοφωνική του συνέντευξη.\r\n«Μια κατάρρευση των διαπραγματεύσεων θα μπορούσε να έχει συνέπειες, ακόμα και για την παραμονή της Ελλάδας στο ευρώ, αλλά όλοι θέλουν η Ελλάδα να παραμείνει στην ευρωζώνη».', 'images/imagefornews/gre1524924_22854215-thumb-large.jpg', 1, '2015-07-03 12:05:03', 23, '', ''),
(53, 'Το βίντεο που πρέπει να δουν όλοι οι Έλληνες', 'Αφυπνιστικό βίντεο, παρακαλώ διαδώστε το με οποιονδήποτε τρόπο ακόμη και στην πιο απομακρυσμένη γωνιά της Ελλάδας μας. Είναι καιρός να ξυπνήσουν όλοι οριστικά!', 'Πρέπει να πάρουμε την Ελλάδα μας πίσω!\r\n***Πολλά μπράβο στην Ένωση Ελλήνων Ερευνητών.***\r\n', 'images/imagefornews/ellada_3.png', 1, '2015-07-04 18:39:23', 21, '', ''),
(54, 'Παλμύρα: Οι Τζιχαντιστές κατέστρεψαν άγαλμα λιονταριού 2.000 ετών', 'Στις 21 Μαίου οι Τζιχαντιστές κατέλαβαν την πόλη Παλμύρα, προκαλώντας την έντονη ανησυχία για τη μοίρα των αρχαίων που βρίσκονται εκεί. Ως αναμενόμενο το κακό έγινε. Οι Τζιχαντιστές κατέστρεψαν ένα άγαλμα λιονταριού 2.000 ετών που βρισκόταν έξω από το μουσείο της Παλμύρας,', 'Στις 21 Μαίου οι Τζιχαντιστές κατέλαβαν την πόλη Παλμύρα, προκαλώντας την έντονη ανησυχία για τη μοίρα των αρχαίων που βρίσκονται εκεί. Ως αναμενόμενο το κακό έγινε. Οι Τζιχαντιστές κατέστρεψαν ένα άγαλμα λιονταριού 2.000 ετών που βρισκόταν έξω από το μουσείο της Παλμύρας, ένα αναντικατάστατο δείγμα τέχνης.\r\n\r\nΓια την ιστορία, το άγαλμα από ασβεστόλιθο ανακαλύφθηκε το 1977 από μία πολωνική αρχαιολογική αποστολή, και χρονολογείται από τον 1ο αιώνα μ.Χ.\r\n\r\nΣύμφωνα με πληροφορίες τα μέλη του Ισλαμικού Κράτους έχουν ναρκοθετήσει τον αρχαιολογικό χώρο, ενώ έχουν καταστρέψει δύο μαυσωλεία.\r\n\r\nΣε μια προσπάθεια να περισώσει ότι μπορεί η Συρία μετέφερε τα σημαντικότερα αρχαία αντικείμενα εκτός της πόλης.', 'images/imagefornews/TZIXANTISTES.jpg', 2, '2015-07-03 11:59:18', 3, '', ''),
(55, 'Η Δέσποινα Βανδή βαφτίζει τον γιο της μετά από 8 χρόνια', 'Μεγάλο θέμα είχε γίνει στο παρελθόν η συνεχής αναβολή της Δέσποινας Βανδή, να βαφτίσει τον γιο της… που στα 8 του χρόνια παραμένει αβάφτιστος. Τώρα όμως η γνωστή τραγουδίστρια και ο σύζυγος της Ντέμης Νικολαίδης δηλώνουν έτοιμοι να κάνουν το μυστήριο και μάλιστα πολύ σύντομα.', 'Μεγάλο θέμα είχε γίνει στο παρελθόν η συνεχής αναβολή της Δέσποινας Βανδή, να βαφτίσει τον γιο της… που στα 8 του χρόνια παραμένει αβάφτιστος. Τώρα όμως η γνωστή τραγουδίστρια και ο σύζυγος της Ντέμης Νικολαίδης δηλώνουν έτοιμοι να κάνουν το μυστήριο και μάλιστα πολύ σύντομα.Σύμφωνα με το ρεπορτάζ, η βάφτιση του γιου τους θα γίνει 22 Ιουλίου στη Δράμα, και όπως είναι φυσικό το ζευγάρι θα αποφύγει τις κάμερες και τους δημοσιογράφους εκείνη την ημέρα.', 'images/imagefornews/DESPOINA.jpg', 9, '2015-07-03 12:15:37', 9, '', ''),
(56, 'Χωρισμός «βόμβα» για τους Ben Affleck – Jennifer Garner', 'Αλλο ένα αγαπημένο ζευγάρι διάσημων ηθοποιών, αποφάσισε να δώσει τέλος στον γάμο του μία ημέρα μετά τη δέκατη επέτειο του.', 'Η Jennifer Garner και ο Ben Affleck, που έχουν αποκτήσει μαζί τρία παιδιά, τη Violet, 9 ετών, τη Seraphina, 6 ετών και τον Samuel, τριών ετών, δημοσιοποίησαν την ανακοίνωση του χωρισμού τους.\r\n\r\n «Μετά από πολλή και προσεκτική σκέψη λάβαμε τη δύσκολη απόφαση να πάρουμε διαζύγιο.Προχωράμε με αγάπη και φιλικά συναισθήματα ο ένας προς τον άλλο και μια δέσμευση να μεγαλώνουμε μαζί τα παιδιά μας, των οποίων η ιδιωτικότητα ζητούμε να γίνει σεβαστή το δύσκολο αυτό διάστημα. Αυτό θα είναι το μοναδικό μας σχόλιο σχετικά με αυτό το ιδιωτικό, οικογενειακό ζήτημα. Σας ευχαριστούμε για την κατανόηση».', 'images/imagefornews/ben.jpg', 9, '2015-07-03 12:15:24', 4, '', ''),
(57, 'Λατινική Αμερική', 'Κατοικούμενη τότε από τους λεγόμενους Προκολομβιανούς πολιτισμούς (Ίνκας, Μάγιας, Αζτέκοι και άλλοι), η Νότια ή Λατινική Αμερική εξερευνήθηκε, αποικήθηκε και κατακτήθηκε, για εκατονταετίες από τους Ισπανούς και ', 'Η Λατινική Αμερική αποτελεί ένα κράμα φυλών και πολιτισμών. Παρά ταύτα τρεις είναι οι βασικές φυλές, οι γηγενείς, οι Ευρωπαίοι, αρχικά Ίβηρες, και οι μεταφερθέντες από Αφρική νέγροι.\r\nΚυριότερη εξ αυτών θεωρείται η Ιβηρική που έφερε στη περιοχή τη νομοθεσία, τη γλώσσα και τη θρησκεία της Ευρώπης κυριαρχώντας έτσι σ΄ όλη τη περιοχή, πολιτικά, κοινωνικά, οικονομικά και πολιτιστικά. Αντίθετα οι νέγροι που υφίστανται κατά εκατομμύρια στη Βραζιλία και Καραϊβική φαίνονται να ελαττώνονται συνεχώς.\r\nΗ δε τρίτη, των γηγενών που διακρίνονται σε πολλές επιμέρους φυλές ιδιαίτερα στη Βολιβία, Ισημερινό, Περού και Μεξικό φέρεται να αντιμετωπίζει σοβαρά φυλετικά προβλήματα διαφόρου φύσεως όπως οικονομικά, κοινωνικά κ.λπ. Πολλοί εξ αυτών που αποκτούν εθνική συνείδηση των προγόνων τους αντιδρούν ή δε συμμετέχουν σε κοινές διεθνείς οικονομικές κατευθύνσεις, διατηρώντας αμφισβητήσεις μέχρι και δυσαρέσκειες.', 'images/imagefornews/rio.jpg', 10, '2015-07-04 15:12:38', 9, '', ''),
(58, 'Ιστορία της Κίνας', 'Ο κινεζικός πολιτισμός δημιουργήθηκε σε ποικίλα τοπικά κέντρα κατά μήκος τόσο της κοιλάδας του Κίτρινου Ποταμού όσο και της κοιλάδας του Ποταμού Γιανγκτσέ στη Νεολιθική Εποχή, με τον Κίτρινο Ποταμό να θεωρείται', 'Ο κινεζικός πολιτισμός δημιουργήθηκε σε ποικίλα τοπικά κέντρα κατά μήκος τόσο της κοιλάδας του Κίτρινου Ποταμού όσο και της κοιλάδας του Ποταμού Γιανγκτσέ στη Νεολιθική Εποχή, με τον Κίτρινο Ποταμό να θεωρείται ως η κοιτίδα του κινεζικού πολιτισμού. Με χιλιάδες χρόνια συνεχούς ιστορίας, η Κίνα αποτελεί έναν από τους αρχαιότερους πολιτισμούς της υφηλίου. Η γραπτή ιστορία της Κίνας αρχίζει από την εποχή της Δυναστείας Σανγκ (γύρω στα 1.700-1.046 π.Χ.), παρόλο που αρχαία ιστορικά κείμενα όπως τα Αρχεία του Μεγάλου Ιστορικού (γύρω στο 100 π.Χ.) και τα Χρονικά Μπαμπού επιβεβαιώνουν την ύπαρξη μιας δυναστείας ονόματι Ξία πριν από αυτή των Σανγκ. Οστά από μαντεία, με αρχαία κινεζική γραφή από τον καιρό της δυναστείας Σανγκ έχουν ραδιοχρονολογηθεί μέχρι και το 1.500 π.Χ. Μεγάλο μέρος της κινεζικής κουλτούρας, λογοτεχνίας και φιλοσοφίας αναπτύχθηκε περαιτέρω κατά τη διάρκεια της Δυναστείας Τσου (1045-256 π.Χ.).Η Δυναστεία των Τσου άρχισε να λυγίζει σε εξωτερικές και εσωτερικές πιέσεις τον 8ο αιώνα π.Χ. και το βασίλειο τελικώς διασπάστηκε σε μικρότερα κρατίδια, ξεκινώντας από την Περίοδο της Άνοιξης και την Περίοδο του Φθινοπώρου και καταλήγοντας στην Περίοδο των Εμπόλεμων Βασιλείων. Αυτή η περίοδος αποτελεί μια από τις πολλές περιόδους αναταραχής στην κινεζική ιστορία (με πιο πρόσφατη τον κινεζικό εμφύλιο πόλεμο).Τις περιόδους ανάμεσα σε αυτές των πολλαπλών βασιλείων και των πολεμάρχων, κινεζικές δυναστείες (ή πιο πρόσφατα, δημοκρατίες) κυβέρνησαν όλη την Κίνα (εκτός της περιοχής Ξινγιάνγκ και του Θιβέτ, υπό κινεζική κατοχή σήμερα, όπου η κινεζική Αρχή δεν υπήρξε συνεχής). Η πρακτική αυτή άρχισε με τη Δυναστεία Τσιν: το 221 π.Χ., ο Τσιν Σι Χουάνγκ ένωσε τα διάφορα εμπόλεμα βασίλεια και σχημάτισε την πρώτη κινεζική Αυτοκρατορία. Οι επόμενες δυναστείες στην κινεζική ιστορία ανέπτυξαν γραφειοκρατικά συστήματα τα οποία διευκόλυναν τον αυτοκράτορα στον απευθείας έλεγχο αχανών περιοχών.Η συμβατική όψη της κινεζικής ιστορίας είναι αυτή της εναλλαγής περιόδων πολιτικής ενότητας με περιόδους διχόνοιας και αναταραχών, με την Κίνα να περιέρχεται κατά περιόδους υπό την κυριαρχία άλλων λαών της Ασίας, οι περισσότεροι εκ των οποίων αφομοιώθηκαν από τον κινεζικό πολιτισμό των Χαν. Πολιτιστικές και πολιτικές επιρροές από πολλά μέρη της Ασίας, εισαχθείσες από αλλεπάλληλα κύματα μετανάστευσης, επέκτασης και πολιτιστικής αφομοίωσης, αποτελούν μέρος της μοντέρνας κουλτούρας της Κίνας.', 'images/imagefornews/kina_3_0.jpg', 10, '2015-07-05 19:59:25', 6, '', ''),
(59, 'Αρχαία Αίγυπτος', 'Η υπερτρισχιλιετής ιστορία της αρχαίας Αιγύπτου χωρίζεται σε οκτώ ή εννέα περιόδους, οι οποίες αποκαλούνται ενίοτε Βασίλεια και εν τάχει αναλύονται στο παρακάτω χρονολόγιο. Αυτή η σύγχρονη ταξινόμηση είναι αυθαίρετη και βασίζεται σε ', 'Η υπερτρισχιλιετής ιστορία της αρχαίας Αιγύπτου χωρίζεται σε οκτώ ή εννέα περιόδους, οι οποίες αποκαλούνται ενίοτε Βασίλεια και εν τάχει αναλύονται στο παρακάτω χρονολόγιο. Αυτή η σύγχρονη ταξινόμηση είναι αυθαίρετη και βασίζεται σε κοινωνικά κριτήρια ευημερίας, ενότητας και διακυβέρνησης από μία κεντρική εξουσία. Οι ίδιοι οι αρχαίοι Αιγύπτιοι δεν ομαδοποιούσαν τους κυβερνήτες τους βάσει τέτοιων κριτηρίων. Αντίθετα χρησιμοποιούσαν την έννοια των δυναστειών σε όλη την ιστορική τους διαδρομή. Ο λίθος του Παλέρμο απλά κατονομάζει όλους τους βασιλείς, χωρίς να υποδεικνύει κάποιο είδος ομαδοποίησης. Ο Κανόνας του Τορίνο ομαδοποιεί τους βασιλείς ή βασίλισσες σύμφωνα με την διαδοχή ή καταγωγή τους.Τη διάκριση των τριάντα δυναστειών, όπως τη χρησιμοποιούμε σήμερα, την οφείλουμε στον Μανέθωνα, τον Αιγύπτιο ιερέα που έζησε στις αρχές της Πτολεμαϊκής περιόδου. Σε πολλές περιπτώσεις όμως δεν είναι απόλυτα κατανοητό γιατί ο Μανέθων ομαδοποίησε ορισμένους βασιλείς σε μια δυναστεία και άλλους σε κάποια άλλη. Για παράδειγμα η 18η Δυναστεία ξεκινά με τον Άμασις (Αχμόσες), αδελφό του τελευταίου βασιλιά της 17ης δυναστείας του Μανέθωνα. Θεωρητικά ο Άμασις και ο Μάριος, θα έπρεπε να ταξινομούνται στην ίδια δυναστεία.Οι θρησκευτικές πεποιθήσεις των Αιγυπτίων στις πρώιμες περιόδους είναι δυνατόν να ομαδοποιηθούν σε πέντε διακριτές ομάδες:Εννεάδα της Ηλιούπολης, με κυρίαρχη θεότητα τον Ατούμ.Ογδοάδα της Ερμούπολης, με κυρίαρχη θεότητα τον Ρα.Τριάδα της Ελεφαντίνης, Κνουμ-Σατέτ-Ανουκέτ, με κυρίαρχη θεότητα τον Κνουμ.', 'images/imagefornews/Pyramids_egypt.jpg', 10, '2015-07-03 13:19:23', 3, '', ''),
(60, 'Αιγυπτιακή μυθολογία', 'Οι θρησκευτικές πεποιθήσεις των Αιγυπτίων στις πρώιμες περιόδους είναι δυνατόν να ομαδοποιηθούν σε πέντε διακριτές ομάδες:\r\nΕννεάδα της Ηλιούπολης, με κυρίαρχη θεότητα τον Ατούμ.\r\nΟγδοάδα της Ερμούπολης, με κυρίαρχη θεότητα τον Ρα.\r\nΤριάδα της Ελεφαντίνης, Κνουμ-Σατέτ-Ανουκέτ, με κυρίαρχη θεότητα τον Κνουμ.', 'Τριάδα των Θηβών, Άμων-Μουτ-Κχονσού, με κυρίαρχη θεότητα τον Άμωνα.\r\nΤριάδα της Μέμφιδας Πτα-Σεκμέτ-Νεφερτέμ , με κυρίαρχη θεότητα τον Πτα.\r\nΟι κυρίαρχες πεποιθήσεις αρχαίων Αιγυπτίων συγχωνεύονταν και τροποποιούνταν ανάλογα με το ποιες ηγετικές ομάδες διεκδικούσαν την πολιτική εξουσία, επιβάλλοντας τις λατρευτικές πεποιθήσεις του ηγεμονικού οίκου τους. Αρχικά ο Ρα και ο Άμων συγχωνεύθηκαν σε μία θεότητα, τον Άμμωνα-Ρα και αργότερα ο Ρα απορροφήθηκε με τη σειρά του από τον Ώρο, για να δημιουργηθεί μια νέα θεότητα με το όνομα Ρα-Χεράκτυ.', 'images/imagefornews/ancient-egypt-image3.jpg', 11, '2015-07-06 11:09:38', 39, 'https://el.wikipedia.org/wiki/%CE%91%CE%B9%CE%B3%CF%85%CF%80%CF%84%CE%B9%CE%B1%CE%BA%CE%AE_%CE%BC%CF%85%CE%B8%CE%BF%CE%BB%CE%BF%CE%B3%CE%AF%CE%B1', ''),
(61, 'Ερημη από τουρίστες η Αίγυπτος λόγω των πολιτικών ταραχών', 'Η τουριστική βιομηχανία της Αιγύπτου πασχίζει να σταθεί στα πόδια της μετά από την χειρότερη χρονιά στη σύγχρονη ιστορία της, καθώς εκατομμύρια τουρίστες έμειναν μακριά από τα θέρετρα και τα μνημεία της χώρας λόγω των πολιτικών ταραχών. ', 'Τα έσοδα από τον τουρισμό έπεσαν στα 4,3 δισ. ευρώ το 2013, σε σύγκριση με 9,2 δισ. ευρώ το έτος-ρεκόρ 2010, πριν από την εξέγερση που ανέτρεψε τον Χόσνι Μουμπάρακ, και οδήγησε σε τρία χρόνια πολιτικής αστάθειας.\r\n\r\nΜόνο 9,5 εκατομμύρια τουρίστες έμειναν σε ξενοδοχεία της Αιγύπτου το 2013, έναντι 14,7 εκατ. το 2010, σύμφωνα με τον υπουργό τουρισμού Χισάμ Ζααζού.\r\n\r\n«Μιλάμε για πόλεις- φαντάσματα», είπε ο Ζααζού στον Guardian. Μερικές από τα πιο διάσημες τουριστικές πόλεις της Αιγύπτου είχαν τον χειρότερο μήνα τους τον Σεπτέμβριο. «Ημασταν σχεδόν στο μηδέν της τουριστικής κίνησης σε ορισμένες περιοχές της Αιγύπτου - με πληρότητα 1 % των ξενοδοχείων στο Λούξορ, 0 % στο Αμπού Σιμπέλ, 0 % στο Ασουάν. Στην Ερυθρά Θάλασσα, ήταν λίγο καλύτερα - αλλά μιλάμε για πληρότητα 10, 15, 20 %. Ηταν η χειρότερη χρονιά από το 2010».', 'images/imagefornews/81E64F2D4C2D68E19E7FC825BE8E0F76.jpg', 12, '2015-07-06 15:03:42', 11, '', ''),
(62, 'Γιούνκερ: Αν οι Έλληνες πουν «όχι» θα αποδυναμωθεί η ελληνική διαπραγματευτική θέση', 'Η επικράτηση του «όχι» την Κυριακή θα είναι ένα σοκ (σ.σ.για τη χώρα) γιατί θα αποδυναμώσει πολύ τη θέση της Ελλάδας, δήλωσε σήμερα στο Λουξεμβούργο ο πρόεδρος της Κομισιόν, Ζαν-Κλοντ Γιούνκερ.\r\nΣυγκεκριμένα, ερωτηθείς', 'Συγκεκριμένα, ερωτηθείς σχετικά με το δημοψήφισμα, ο κ. Γιούνκερ είπε ότι, αν οι Έλληνες ψηφίσουν «ναι», οι διαπραγματεύσεις που θα ακολουθήσουν θα είναι δύσκολες, εάν ψηφίσουν «όχι», που θα είναι ένα σοκ, θα εξασθενήσει σημαντικά η θέση της Ελλάδας.Από την πλευρά του, ο πρωθυπουργός του Λουξεμβούργου Ξαβιέ Μπέτελ συμφώνησε με τον κ. Γιούνκερ, λέγοντες ότι το «ναι» θα οδηγήσει σε διαπραγματεύσεις που δεν θα είναι εύκολες, αλλά με το «όχι» θα είναι εξαιρετικά δύσκολες.\r\nΕίπε ότι δεν θέλει να πάρει θέση για ένα «όχι» ή ένα «ναι», υπογραμμίζοντας ότι ο ελληνικός λαός θα αποφασίσει και η απόφασή του αυτή θα γίνει σεβαστή. Ωστόσο, επεσήμανε ότι ο χρόνος που δόθηκε στους Έλληνες πολίτες για να αποφασίσουν στο δημοψήφισμα είναι πολύ μικρός.\r\nΤέλος, έθεσε θέμα εμπιστοσύνης στο πρόσωπο του Έλληνα πρωθυπουργού, τονίζοντας ότι με τον κ. Τσίπρα ήμασταν δύο μέρες μαζί και μιλούσαμε στην τελευταία σύνοδο κορυφής και δεν μας είπε τίποτα και την επόμενη μέρα πήγε στην Ελλάδα και ανακοίνωσε το δημοψήφισμα.', 'images/imagefornews/zan-klont-giounker.jpg', 2, '2015-07-04 08:40:45', 3, '', ''),
(63, 'Γ. Σταθάκης: Το χρέος στο επίκεντρο της διαπραγμάτευσης από Δευτέρα', 'Δύο ημέρες πριν το δημοψήφισμα της Κυριακής και μία ημέρα μετά την δημοσιοποίηση της έκθεσης του ΔΝΤ για τη βιωσιμότητα του ελληνικού χρέους, ο κ. Σταθάκης (φωτ. αρχείου) είπε ότι το χρέος μετά από πέντε χρόνια λιτότητας δεν είναι βιώσιμο και πως το «όχι» θα ενισχύσει την προσπάθεια της κυβέρνησης για μείωσή του.', 'H απομείωση του δημοσίου χρέους θα είναι στο επίκεντρο των διαπραγματεύσεων με τους δανειστές της χώρας τη Δευτέρα μετά το δημοψήφισμα δήλωσε στo Reuters ο υπουργός Οικονομίας Γιώργος Σταθάκης.\r\nΔύο ημέρες πριν το δημοψήφισμα της Κυριακής και μία ημέρα μετά την δημοσιοποίηση της έκθεσης του ΔΝΤ για τη βιωσιμότητα του ελληνικού χρέους, ο κ. Σταθάκης είπε ότι το χρέος μετά από πέντε χρόνια λιτότητας δεν είναι βιώσιμο και πως το «όχι» θα ενισχύσει την προσπάθεια της κυβέρνησης για μείωσή του.\r\n«Όποιο και αν είναι το αποτέλεσμα, από Δευτέρα θα καταβάλουμε κάθε δυνατή προσπάθεια για την επίτευξη μιας συμφωνίας με τους εταίρους μας», πρόσθεσε σε δηλώσεις του που απέστειλε μέσω e-mail στο Reuters.\r\n\r\nΑναφερόμενος στην Έκθεση του ΔΝΤ σχετικά με τη βιωσιμότητα του ελληνικού χρέους, όπου υπογραμμίζεται πως η Ελλάδα θα χρειαστεί επέκταση της λήξης των δανείων που έχει λάβει από την ΕΕ και μεγάλη διαγραφή χρέους τόνισε ότι «η δημοσιοποίηση της έκθεσης του ΔΝΤ, μας υπενθυμίζει με τον πλέον επίκαιρο τρόπο, τα πλέον κρίσιμα σημεία του ελληνικού προβλήματος. Ότι τα ελληνικά προγράμματα ήσαν εξαρχής καταδικασμένα σε αποτυχία, τα πρωτογενή πλεονάσματα που προβλέπονταν ήταν υπερβολικά για μια οικονομία σε ύφεση, και το πρόγραμμα ιδιωτικοποιήσεων ανεδαφικό».\r\nΠηγή: ΑΜΠΕ', 'images/imagefornews/giorgos-stathakis-banking-kai-insurance-forum.jpg', 1, '2015-07-06 15:03:28', 20, '', ''),
(64, 'Σόιμπλε: «Προβλέψιμη η αποτυχία των διαπραγματεύσεων»', '«H Αθήνα δεν θέλει μεταρρυθμίσεις» σύμφωνα με τον γερμανό υπουργό Οικονομικών', 'Προβλέψιμη χαρακτήρισε ο υπουργός Οικονομικών της Γερμανίας Βόλφγκανγκ Σόιμπλε την αποτυχία στις διαπραγματεύσεις με την ελληνική κυβέρνηση, καθώς όπως εξήγησε η Αθήνα «δεν θέλει κανένα πρόγραμμα μεταρρυθμίσεων».\r\n\r\nΣε συνέντευξή του που δημοσιεύεται στο σημερινό σαββατιάτικο φύλλο της γερμανικής εφημερίδας Bild σχολιάζει ότι «όποιος γνώριζε τα οικονομικά δεδομένα και έπαιρνε στα σοβαρά τις δηλώσεις του Αλέξη Τσίπρα πριν και μετά (σ.σ. την εκλογή του τον Ιανουάριο) αντιλαμβανόταν ότι η ελληνική κυβέρνηση δεν θέλει κανένα πρόγραμμα μεταρρυθμίσεων».\r\n\r\n«Για αυτό αντιμετώπιζα εξαρχής με μεγάλο σκεπτικισμό (...) το αποτέλεσμα των συνομιλιών με την κυβέρνηση στην Αθήνα. Το ότι αυτός ο σκεπτικισμός τελικά επιβεβαιώθηκε δεν με εξέπληξε πραγματικά», συνέχισε ο Σόιμπλε.\r\n\r\nΤο εάν η Ελλάδα θα παραμείνει στο ευρώ ή θα βγει από την Ευρωζώνη «σε σύντομο χρονικό διάστημα», είναι ένα ερώτημα στο οποίο «μόνο οι Έλληνες μπορούν να απαντήσουν», δήλωσε ο υπουργός Οικονομικών της Γερμανίας.\r\n\r\n«Ένα πράγμα είναι σαφές: δεν θα εγκαταλείψουμε τον λαό στην Ελλάδα», είπε.', 'images/imagefornews/2ABFA1C1758029BBF4548B6F6BCF98D1.jpg', 2, '2015-07-04 12:40:27', 21, '', ''),
(65, 'Ερτογάν: Ανοικτό το ενδεχόμενο πρόωρων εκλογών', 'Ο Ερντογάν αφήνει ανοικτό το ενδεχόμενο να προκηρυχθούν νέες εκλογές. Ο πρόεδρος της Τουρκίας με δηλώσεις του,  ανέφερε ότι είναι πιθανές νέες εκλογές εάν αποτύχουν οι διαπραγματεύσεις για τον σχηματισμό κυβέρνησης συμμαχίας, καθώς το κόμμα του απέτυχε', 'Ο Ερντογάν αφήνει ανοικτό το ενδεχόμενο να προκηρυχθούν νέες εκλογές. Ο πρόεδρος της Τουρκίας με δηλώσεις του,  ανέφερε ότι είναι πιθανές νέες εκλογές εάν αποτύχουν οι διαπραγματεύσεις για τον σχηματισμό κυβέρνησης συμμαχίας, καθώς το κόμμα του απέτυχε να εξασφαλίσει την πλειοψηφία των εδρών στην Εθνοσυνέλευση στις βουλευτικές εκλογές της 7ης Ιουνίου.\r\n\r\nΟ Ερντογάν αναμένεται να αναθέσει την επόμενη εβδομάδα την εντολή σχηματισμού κυβέρνησης συμμαχίας στον νυν πρωθυπουργό Αχμέτ Νταβούτογλου, τον ηγέτη του ισλαμοσυντηρητικού Κόμματος Δικαιοσύνης και Ανάπτυξης (AKP), αλλά μέχρι στιγμής δεν είναι σαφές εάν αυτός θα καταφέρει να συγκεντρώσει επαρκή υποστήριξη ώστε να έχει σταθερή κοινοβουλευτική πλειοψηφία.\r\n\r\n"Αυτό που επιθυμώ είναι ο σχηματισμός ενός συνασπισμού με κοινοβουλευτική πλειοψηφία", είπε ο Ερντογάν σε υποστηρικτές του.\r\n\r\nΌμως, "εάν δεν μπορέσει να σχηματιστεί κυβέρνηση... ο λαός μας θα δώσει λύση ξανά. Αν η Βουλή δεν μπορεί να δώσει λύση, είναι ο λαός μας αυτός που θα το πράξει. Κανείς δεν πρέπει να διστάσει να ζητήσει ξανά τη λαϊκή ετυμηγορία", πρόσθεσε, επικαλούμενος τα "σοβαρά ζητήματα" με τα οποία βρίσκεται αντιμέτωπη η Τουρκία.\r\n\r\n', 'images/imagefornews/ertogan_madata_745273719.jpg', 2, '2015-07-04 18:29:25', 19, '', ''),
(66, 'Ανατροπή από την τελευταία μεγάλη δημοσκόπηση!', 'Κόντρα στις περισσότερες έρευνες, η πιο πρόσφατη δημοσκόπηση (Metron Analysis) για το κρίσιμο δημοψήφισμα της Κυριακής, παρουσιάζει μία σημαντική αλλαγή στα προγνωστικά.', ' δημοσκόπηση έγινε από τη γνωστή εταιρία Metron Analysis για λογαριασμό της εφημερίδας "Παραπολιτικά" που θα κυκλοφορήσει το Σάββατο και δείχνει ότι -έστω και για μία μονάδα- το "ΟΧΙ" προπορεύεται!\r\n\r\nΗ έρευνα ξεκίνησε τη Δευτέρα και ολοκληρώθηκε στις 19:00 το απόγευμα της Παρασκευής σε δείγμα 3.612 πολιτών από ολόκληρη την Ελλάδα.', 'images/imagefornews/dimoskopisi-Alco-dimopsifisma.jpg', 1, '2015-07-04 09:54:25', 4, '', '');
INSERT INTO `nea` (`ID`, `Titlos_arthrou`, `Small_text`, `Keimeno`, `Eikona`, `category`, `Time`, `counter`, `url`, `source`) VALUES
(67, 'Ονδούρα: Απαίτησαν με μεγάλη διαδήλωση παραίτηση προέδρου', 'Την παραίτηση του προέδρου της Ονδούρας Χουάν Ορλάντο Ερνάντες -ο οποίος αντιμετωπίζει κατηγορίες για διαφθορά- απαίτησαν με μεγάλη διαδήλωσή τους δεκάδες χιλιάδες πολίτες της χώρας, οι οποίοι βγήκαν στους δρόμους της πρωτεύουσας χθες.', 'Οι διαδηλωτές -εκτιμώνται κοντά στους 60.000 και πολλοί εξ αυτών με δαυλούς στα χέρια, συμμετείχαν σε μια θορυβώδη πορεία η οποία κατέληξε στο προεδρικό μέγαρο. Η διαδήλωση αυτή έγινε για έκτη Παρασκευή στη σειρά.\r\n\r\n"Όχι άλλη διαφθορά, έξω ο ΧΟΕ", φώναζαν οι διαδηλωτές, αναφερόμενοι στον πρόεδρο με τα αρχικά του ονόματός του, καθώς έπεφτε το σκοτάδι.\r\n\r\nΚόμματα και οργανώσεις της αντιπολίτευσης θέλουν να δημιουργηθεί μια ανεξάρτητη επιτροπή έρευνας την οποία θα στελεχώσουν ξένοι, όπως μια επιτροπή στη Γουατεμάλα που υποστηρίζουν τα Ηνωμένα Έθνη κι έχει συμβάλει στις έρευνες για υποθέσεις διαφθοράς.\r\n\r\nΕπισημαίνεται ότι ο Ερνάντες έχει παραδεχθεί ότι κατά τη διάρκεια της προεκλογικής του εκστρατείας το 2013 είχε δεχθεί 150.000 δολάρια από εταιρείες που σχετίζονται με το σκάνδαλο, αλλά διατείνεται ότι ο ίδιος και το κόμμα του δεν γνώριζαν την προέλευση του ποσού αυτού.', 'images/imagefornews/mega_LARGE_t_53761_1363172_type12250.jpg', 1, '2015-07-04 09:53:35', 3, '', ''),
(68, 'Ευρωπαϊκή οπισθοχώρηση την παραμονή της ψηφοφορίας', 'Ιδιαίτερο ενδιαφέρον παρουσιάζει η αλλαγή στάσης της Ευρώπης αναφορικά με το αποτέλεσμα του επερχόμενου δημοψηφίσματος της Κυριακής.', 'Χαρακτηριστική είναι η αλλαγή στάσης του Ντόναλντ Τουσκ.\r\n\r\nΟ πρόεδρος του Ευρωπαϊκού Συμβουλίου σε συνέντευξή του στην ιστοσελίδα Politico, εμφανίστηκε εμφανώς διαλλακτικότερος σε σχέση με το προηγούμενο χρονικό διάστημα.\r\n\r\n"Άσχετα με το πως θα ψηφίσουν οι Έλληνες στο δημοψήφισμα της Κυριακής, η ΕΕ αναζητά τρόπους για να τους κρατήσει μέσα στο ενιαίο νόμισμα, αν και αυτό μπορεί να απαιτήσει μια εντελώς νέα προσέγγιση που θα επιτρέψει στην Ευρωζώνη να συνυπάρξει με μια χρεοκοπημένη χώρα", δήλωσε σε συνέντευξη του ο Τουσκ.\r\n\r\nΜε τίτλο της συνέντευξης "κανείς από μας δεν είναι αγγελούδι", ο Ντόναλντ Τουσκ εμφανίζεται πολύ πιο διαλλακτικός από τις περασμένες ημέρες και -σε αντίθεση με τις δηλώσεις τόσο του προέδρου της Κομισιόν Ζαν Κλοντ Γιούνκερ και του προέδρου του ευρωκοινοβουλίου Μάρτιν Σουλτς- δήλωσε ότι "είναι πολύ ξεκάθαρο ότι το δημοψήφισμα δεν αφορά την παραμονή ή όχι στην ευρωζώνη. Στην πραγματικότητα κανείς δεν ενδιαφέρεται -ελπίζω ότι κανείς δεν ενδιαφέρεται- για αυτή την επιλογή".\r\n\r\nO πρόεδρος του Ευρωπαϊκού Συμβουλίου κράτησε την πόρτα ορθάνοιχτη σε μια άλλη οικονομική διάσωση της Αθήνας, δεν χρησιμοποίησε με ευκολία τη λέξη χρεοκοπία και υποβάθμισε την σημασία του δημοψηφίσματος της Κυριακής, σε σχέση με τις διαπραγματεύσεις που τονίζει ότι θα συνεχιστούν, ανεξαρτήτως αποτελέσματος.', 'images/imagefornews/EURO SIGN SHMAIA EE_b2.jpg', 1, '2015-07-04 10:03:05', 1, '', ''),
(69, 'Τρομοκρατούν τους Έλληνες οι Financial Times που μιλάει για κούρεμα 30% σε καταθέσεις άνω των 8.000 ευρώ!', 'Σοκάρουν οι αποκαλύψεις των Financial Times, για σχέδια έκτακτης ανάγκης των ελληνικών τραπεζών, τα οποία προβλέπουν (κούρεμα) τουλάχιστον 30% για όλες τις καταθέσεις άνω των 8.000 ευρώ!', 'Τραπεζικές πηγές και παράγοντες της αγοράς αποκαλύπτουν πως εξετάζεται haircut τουλάχιστον 30% για όλες τις καταθέσεις άνω των 8.000 ευρώ, προκειμένου να αποφευχθεί η οικονομική κατάρρευση της Ελλάδας, σύμφωνα με τη βρετανική εφημερίδα, με το σενάριο να αφορά τουλάχιστον μία ελληνική τράπεζα.\r\n\r\nΤι απάντησε η Τράπεζα της Ελλάδας\r\n\r\nΜε αφορμή το δημοσίευμα των FT για κούρεμα στις καταθέσεις πάνω από 8.000 ευρώ πηγές στην ΤτΕανέφεραν: «Η ελληνική νομοθεσία δεν προβλέπει κούρεμα καταθέσεων ακόμη και αν ενσωματώνονταν στο εθνικό δίκαιο η κοινοτική...\r\nοδηγία, προβλέπεται προστασία για καταθέσεις έως 100.00 ευρώ. Για να αλλάξει αυτό το όριο, απαιτείται νόμος του κράτους». Και όπως σχολίαζαν χαρακτηριστικά οι ίδιες πηγές, «σε μια τέτοια συγκυρία είναι προφανές ότι κάτι τέτοιο δεν μπορεί να γίνει».', 'images/imagefornews/D-D_dnt_araxni_619483236_177069317.jpg', 1, '2015-07-04 10:31:30', 2, '', ''),
(70, 'Ο EFSF δεν τραβάει ακόμη την "πρίζα" από τις Ελληνικές τράπεζες', 'Ο Ευρωπαϊκός Μηχανισμός Σταθερότητας δεν καθιστά άμεσα απαιτητά τα δάνεια προς το ελληνικό Δημόσιο, αλλά διατηρεί το δικαίωμά του να το πράξει παρακολουθώντας συνεχώς την κατάσταση, όπως υπογραμμίζεται', 'όπως υπογραμμίζεται στην ανακοίνωσή του. Ουσιαστικά το ΔΣ του EFSF, που κλήθηκε να αποφασίσει τις επόμενες κινήσεις του μετά τη μη πληρωμή από την Ελλάδα των δόσεων Ιουνίου στο ΔΝΤ, αποδεχόμενο τη σχετική εισήγηση του επικεφαλής του Κλάους Ρέγκλινγκ, δεν τραβά την πρίζα στην Ελλάδα, απαιτώντας άμεση εξόφληση των δανείων, αλλά επιφυλάσσεται αναλόγως και των εξελίξεων που θα ακολουθήσουν.\r\nΥπενθυμίζεται ότι ο επικεφαλής του Ευρωπαϊκού Ταμείου Χρηματοπιστωτικής Σταθερότητας (EFSF) Κλάους Ρέγκλινγκ εισηγήθηκε στο Διοικητικό Συμβούλιο του Ταμείου να μην ζητήσει την άμεση αποπληρωμή των δανείων, συνολικού ύψους 130,9 δισ. ευρώ, αλλά ούτε να παραιτηθεί του συγκεκριμένου δικαιώματός του.\r\n\r\nΜε την έκδοση Επιφύλαξης Δικαιωμάτων, ο EFSF διατηρεί όλες τις επιλογές του ανοικτές ως πιστωτής.\r\n\r\n«Ο EFSF είναι ο μεγαλύτερος πιστωτής στην Ελλάδα. Αυτή η περίπτωση αθέτησης των υποχρεώσεων είναι αιτία βαθιάς ανησυχίας. Καταστρατηγεί τη δέσμευση που ανέλαβε η Ελλάδα να τηρήσει τις οικονομικές υποχρεώσεις της προς όλους τους πιστωτές της, και ανοίγει την πόρτα σε σοβαρές συνέπειες για την ελληνική οικονομία και τον ελληνικό λαό», ανέφερε ο κ. Ρέγκλινγκ.\r\n', 'images/imagefornews/B-bank_greek_513362407.jpg', 1, '2015-07-06 12:09:28', 3, '', ''),
(71, 'O Σόιμπλε απαντά στο ΔΝΤ ότι βιωσιμότητα του ελληνικού χρέους δεν σημαίνει και “κούρεμα”', 'Στην έκθεση του ΔΝΤ για τη βιωσιμότητα του ελληνικού χρέους, απαντά το υπουργείο Οικονομικών της Γερμανίας, δια του εκπροσώπου του Βόλφανγκ Σόιμπλε, Μάρτιν Γιέγκερ.\r\n\r\nΒιωσιμότητα του ελληνικού χρέους δεν ', 'Στην έκθεση του ΔΝΤ για τη βιωσιμότητα του ελληνικού χρέους, απαντά το υπουργείο Οικονομικών της Γερμανίας, δια του εκπροσώπου του Βόλφανγκ Σόιμπλε, Μάρτιν Γιέγκερ.\r\n\r\nΒιωσιμότητα του ελληνικού χρέους δεν σημαίνει απαραιτήτως haircut, δήλωσε ο κ. Γιέγκερ.\r\n\r\nΌσον αφορά ενδεχόμενο πρόγραμμα της Ελλάδας με τον ESM, ξεκαθάρισε ότι η διαδικασία δεν είναι απλή και ότι θα χρειαστεί έγκριση της Bundestag.', 'images/imagefornews/S-_0_0_0Soimple_981062887.jpg', 1, '2015-07-04 10:32:52', 4, '', ''),
(72, 'Βαρουφάκης: Είτε με «ναι» είτε με «όχι» οι τράπεζες θα ανοίξουν ξανά την Τρίτη', 'Στην διαβεβαίωση ότι οι τράπεζες θα ανοίξουν ξανά την Τρίτη, άσχετα με το αποτέλεσμα του δημοψηφίσματος, προχώρησε ο υπουργός Οικονομικών, Γ. Βαρουφάκης, τονίζοντας ότι ο πρωθυπουργός Αλ. Τσίπρας θα μεταβεί την Δευτέρα στις Βρυξέλλες για μια καλύτερη συμφωνία αν επικρατήσει το «όχι», ενώ επανέλαβε ότι ο ίδιος σε περίπτωση ενός «ναι» θα παραιτηθεί.', 'Σε συνέντευξή του στην ισπανική εφημερίδα «El Mundo», ο Γ. Βαρουφάκης χαρακτηρίζει «τρομοκρατία» όσα γίνονται στην Ελλάδα και εκτιμά ότι αν αφήσουν την χώρα μας να συντριβεί θα χαθεί 1 τρισ. ευρώ.\r\n«Αυτό που κάνουν με την Ελλάδα έχει ένα όνομα: τρομοκρατία. Γιατί μας υποχρέωσαν να κλείσουμε τις τράπεζες; Για να τρομοκρατήσουν τον κόσμο. Και όταν σκορπάς τρόμο, αυτό είναι τρομοκρατία» δηλώνει ο Γ. Βαρουφάκης.\r\nΕρωτηθείς γιατί είναι τόσο βέβαιος ότι θα υπάρξει συμφωνία ακόμη και αν ψηφίσουν «όχι» οι πολίτες την Κυριακή, ο υπουργός Οικονομικών υποστηρίζει ότι η μη επίτευξη συμφωνίας θα ήταν κάτι που θα κόστιζε ακριβά και στις δυο πλευρές:\r\n\r\n«Είμαι σίγουρος, επειδή διακυβεύονται πολλά, τόσο για την Ελλάδα, όσο και για την Ευρώπη. Αν η Ελλάδα συντριβεί, 1 τρισ. ευρώ (ποσό που ισούται με το ΑΕΠ της Ισπανίας) θα χαθεί. Είναι πάρα πολλά χρήματα και δεν πιστεύω ότι η Ευρώπη θα μπορούσε να επιτρέψει κάτι τέτοιο».', 'images/imagefornews/newego_LARGE_t_1101_54527912_type12713.jpg', 1, '2015-07-04 10:34:11', 1, '', ''),
(73, 'Εκτός... το σηκώνει η Αργεντινή! ', 'Η Αργεντινή ετοιμάζεται για πέμπτη φορά να διεκδικήσει το Κόπα Αμέρικα και η παράδοση είναι υπέρ της αφού η διοργάνωση είναι μακριά από τη χώρα της!', 'Δυο Κόπα Αμέρικα έχει καταφέρει να πάρει στην ιστορία της η Αργεντινή. Φέτος έχει την ευκαιρία να φτάσει σε ένα τρίτο τρόπαιο, με την «παράδοση» να την ευνοεί! Κι αυτό γιατί τις δυο προηγούμενες φορές που κατάφερε να σηκώσει την κούπα, το έκανε μακριά από τη χώρα της! Δυο φορές διοργάνωσε το Κόπα Αμέρικα η Αργεντινή, και τις δυο έμεινε μακριά από το τρόπαιο!\r\n\r\nΗ πρώτη ήταν το 1987 όταν περιορίστηκε στην τέταρτη θέση, ενώ η τελευταία ήταν στην προηγούμενη διοργάνωση. Το 2011 η Αργεντινή έφτασε στα προημιτελικά κι εκεί αποκλείστηκε από την Ουρουγουάη στα πέναλτι, η οποία πήρε και το τρόπαιο!\r\n\r\nΟι «αλμπισελέστε» έφτασε στην πρώτη της επιτυχία το 1991, όταν πήρε το τρόπαιο στη Χιλή! Μάλιστα, τότε δεν υπήρχε τελικός αλλά τελική φάση ομίλων, στην οποία η Αργεντινή με δυο νίκες και μια ισοπαλία (σε σύστημα βαθμολόγησης 2-1-0), έφτασε στους 5 βαθμούς και πήρε το τρόπαιο.', 'images/imagefornews/58086a06e42dd512954d7a38ed98cb98_L.jpg', 6, '2015-07-06 13:09:34', 5, '', ''),
(74, 'Λιβύη: Τουλάχιστον 6 νεκροί και 10 τραυματίες σε βομβιστικές επιθέσεις', 'Τουλάχιστον 6 άνθρωποι σκοτώθηκαν και 10 τραυματίστηκαν από την έκρηξη αρκετών αυτοκινήτων που ήταν παγιδευμένα με εκρηκτικά στην πόλη Ντέρνα της Λιβύης, δήλωσε χθες ιατρική πηγή.', 'Τουλάχιστον 6 άνθρωποι σκοτώθηκαν και 10 τραυματίστηκαν από την έκρηξη αρκετών αυτοκινήτων που ήταν παγιδευμένα με εκρηκτικά στην πόλη Ντέρνα της Λιβύης, δήλωσε χθες ιατρική πηγή.\r\nΣύμφωνα με το λιβυκό πρακτορείο ειδήσεων Lana, οι νεκροί ανέρχονται σε 5 και οι τραυματίες σε 15. Μεταξύ των τραυματιών της επίθεσης αυτής βρίσκονται "γυναίκες και παιδιά", προσθέτει το πρακτορείο, σύμφωνα με το οποίο επρόκειτο για επίθεση αυτοκτονίας.\r\nΣτην Ντέρνα σημειώνονται σφοδρές συγκρούσεις μεταξύ των τζιχαντιστών του Ισλαμικού Κράτους, αντίπαλων ισλαμιστών μαχητών και των ενόπλων δυνάμεων της κυβέρνησης που αναγνωρίζει η διεθνής κοινότητα.\r\nΗ πόλη αυτή στην ανατολική Λιβύη αποτελεί ένα μόνο από τα μέτωπα στην πολύπλευρη σύγκρουση στην βορειοαφρικανική αυτή χώρα, όπου δύο αντίπαλες κυβερνήσεις και διάφορες ένοπλες οργανώσεις πολεμούν για τον έλεγχό της τέσσερα χρόνια μετά την πτώση του Μουάμαρ Καντάφι.\r\n\r\nΠηγή: ΑΠΕ-ΜΠΕ  ', 'images/imagefornews/6AE6010CA9A16DAA0AC270B76E925DFC.jpg', 2, '2015-07-04 14:41:33', 15, '', ''),
(75, 'Boeing 777: Ειδικό δικαστήριο για το μαλαισιανό αεροσκάφος που κατερρίφθη στην Ουκρανία θέλει η Ολλανδία', 'Ως την "καλύτερη επιλογή" χαρακτήρισε ο Ολλανδός πρωθυπουργός Ρούτε την εγκαθίδρυση ειδικού δικαστηρίου του ΟΗΕ για να δικαστούν οι υπεύθυνοι για τη συντριβή του Boeing 777 τον Ιούλιο του 2014 στην ανατολική Ουκρανία', 'Η εγκαθίδρυση ενός ειδικού δικαστηρίου του ΟΗΕ είναι η "καλύτερη επιλογή" προκειμένου να δικαστούν οι υπεύθυνοι για τη συντριβή του αεροσκάφους Boeing 777 το οποίο εκτελούσε την πτήση MH17 τον Ιούλιο του 2014 στην ανατολική Ουκρανία, έκρινε σήμερα ο πρωθυπουργός της Ολλανδίας Μαρκ Ρούτε.\r\nΤο να συσταθεί "ένα δικαστήριο του ΟΗΕ είναι η καλύτερη επιλογή για να διωχθούν εκείνοι οι οποίοι ευθύνονται για την τραγωδία με την MH17, καθώς προσφέρει την καλύτερη πιθανότητα να οδηγηθούν ενώπιον ενός δικαστηρίου", επισήμανε ο Ρούτε κατά τη διάρκεια της εβδομαδιαίας συνέντευξης Τύπου που παραχώρησε στη Χάγη μετά τη συνεδρίαση του υπουργικού συμβουλίου.\r\nΗ Μαλαισία γνωστοποίησε χθες ότι ένα σχέδιο απόφασης, το οποίο προβλέπει την εγκαθίδρυση ενός τέτοιου δικαστηρίου και τους κανονισμούς λειτουργίας του, θα διανεμηθεί στα κράτη μέλη του Συμβουλίου Ασφαλείας την ερχόμενη εβδομάδα.\r\nΣύμφωνα με την Κουάλα Λουμπούρ η Αυστραλία, το Βέλγιο, η Ολλανδία και η Ουκρανία συνέβαλαν στην κατάρτιση του σχεδίου απόφασης, το οποίο ωστόσο προσκρούει στις έντονες επιφυλάξεις της Ρωσίας.\r\n"Το να εξασφαλιστεί (σ.σ. ότι θα αποδοθεί) δικαιοσύνη για τα θύματα ήταν προτεραιότητά μας από την αρχή", συνέχισε ο Μαρκ Ρούτε: "ζητάμε τώρα την υποστήριξη του ΟΗΕ για να εγκαθιδρυθεί" το δικαστήριο, πρόσθεσε.\r\nΓια τον υπουργό Εξωτερικών της Ολλανδίας Μπερτ Κούντερς, η εγκαθίδρυση ενός ειδικού δικαστηρίου του ΟΗΕ θα προσέφερε "μεγαλύτερη νομιμοποίηση και καλύτερες πιθανότητες να υπάρξουν δικαστικές διώξεις" των προσώπων που ευθύνονται για την αεροπορική τραγωδία.', 'images/imagefornews/rutte.jpg', 2, '2015-07-06 14:49:20', 8, '', ''),
(76, 'Δημοψήφισμα 2015: Ό,τι πρέπει να ξέρετε για τη διαδικασία', 'Οδηγίες χρήσης για την αυριανή ψηφοφορία. Πού και πώς ψηφίζετε. Πού ισχύουν οι προσφορές στις μετακινήσεις. ', 'Την Κυριακή 5 Ιουλίου οι Έλληνες πολίτες καλούνται να συμμετάσχουν στο δημοψήφισμα παίρνοντας θέση επί ερωτήματος σχετικά με τις προτάσεις των θεσμών προς την ελληνική κυβέρνηση.\r\n\r\nΟι εκλογείς ψηφίζουν στα ίδια εκλογικά τμήματα με αυτά των εθνικών εκλογών της 25ης Ιανουαρίου 2015. Εξαίρεση αποτελούν όσοι ετεροδημότες ψηφίζουν στον τόπο διαμονής τους (κατόπιν αίτησής τους έως τις 30 Απριλίου 2015), καθώς αντί για τα ειδικά εκλογικά τμήματα, την Κυριακή θα πρέπει να προσέλθουν στο εκλογικό τμήμα της περιοχής τους.\r\n\r\nΕξαιτίας των ασφυκτικών χρονικών περιθωρίων δεν κατέστη δυνατό να συμμετάσχουν οι ομογενείς. Για πρώτη φορά θα κληθούν να ψηφίσουν 108.371 18άρηδες. Οι ενδιαφερόμενοι μπορούν να μάθουν σε ποιο εκλογικό τμήμα ψηφίζουν συμπληρώνοντας τα στοιχεία τους στην εφαρμογή «Μάθε που ψηφίζεις» που βρίσκεται στην ιστοσελίδα του υπουργείου Εσωτερικών (www.ypes.gr). Εναλλακτικά, σχετικές πληροφορίες παρέχει το ίδιο υπουργείο στο τηλέφωνο 213 1361500 έως το κλείσιμο της κάλπης.\r\n', 'images/imagefornews/690_b434a245-93af-4a42-9462-5f2ba3fed66b.jpg', 1, '2015-07-06 11:11:17', 15, '', ''),
(77, 'Σπανίζουν τα 20ευρα, το πραγματικό πλαφόν στα 50 ευρώ', 'Σε επικίνδυνα οριακό σημείο έχει φτάσει η ρευστότητα των ελληνικών τραπεζών. Σύμφωνα με πληροφορίες, έχει εξαντληθεί πλέον το απόθεμα των 20εύρων.', 'Σε επικίνδυνα οριακό σημείο έχει φτάσει η ρευστότητα των ελληνικών τραπεζών. Σύμφωνα με πληροφορίες, έχει εξαντληθεί πλέον το απόθεμα των 20εύρων με αποτέλεσμα ο κόσμος που κάνει αναλήψεις να μπορεί να παίρνει μόνο χαρτονομίσματα των 50 ευρώ.\r\n\r\nΗ Τράπεζα της Ελλάδος να βρίσκεται από χθες σε ανοικτή γραμμή με την ΕΚΤ έτσι ώστε να αντιμετωπιστεί άμεσα το ζήτημα και να μπορέσουν οι τράπεζες να τροφοδοτούν με μετρητά τα ATMs.\r\n\r\nΤο σχέδιο αυτή τη στιγμή είναι να αξιοποιηθεί των stock των χαρτονομισμάτων των 100, των 200 και των 500 ευρώ που διαθέτουν ακόμη οι τράπεζες. Φυσικά, με το όριο των 60 ευρώ ημερησίως αυτά τα χαρτονομίσματα δεν μπορούν να κινηθούν. Μπορούν ωστόσο να ανταλλαχθούν στην ΕΚΤ με χαρτονομίσματα των 10,20 και 50 ευρώ τα οποία και θα... ταΐσουν τα άδεια ΑΤΜs.\r\n\r\nΣύσκεψη στο υπουργείο Οικονομικών\r\n\r\nΤο απόγευμα της Παρασκευής πραγματοποιήθηκε σύσκεψη στο υπουργείο Οικονομικών για την κατάσταση του ελληνικού τραπεζικού συστήματος με τη συμμετοχή τραπεζιτών. Στη σύσκεψη συμμετείχαν -από την πλευρά της κυβέρνησης- ο αντιπρόεδρος της κυβέρνησης, Γιάννης Δραγασάκης, ο υπουργός Οικονομικών, Γιάνης Βαρουφάκης και ο υπουργός αναπληρωτής Οικονομικών, Δημήτρης Μάρδας. \r\n ', 'images/imagefornews/teleiwnoun-ta-20eura-sta-atm-o-kosmos-parnei-50eura_8.w_l.jpg', 1, '2015-07-04 12:49:51', 15, '', ''),
(78, 'Reuters: Ευρωπαίοι αποπειράθηκαν να μείνει στο σκοτάδι η έκθεση του ΔΝΤ', 'Κράτη-μέλη της ευρωζώνης προσπάθησαν μάταια να εμποδίσουν τη δημοσιοποιήση της προκαταρκτικής έκθεσης του ΔΝΤ, η οποία σύμφωνα με την ελληνική κυβέρνηση δικαιώνει την έκκλησή της προς τους ψηφοφόρους να απορρίψουν στο δημοψήφισμα της Κυριακής τους', 'Κράτη-μέλη της ευρωζώνης προσπάθησαν μάταια να εμποδίσουν τη δημοσιοποιήση της προκαταρκτικής έκθεσης του ΔΝΤ, η οποία σύμφωνα με την ελληνική κυβέρνηση δικαιώνει την έκκλησή της προς τους ψηφοφόρους να απορρίψουν στο δημοψήφισμα της Κυριακής τους όρους της συμφωνίας που της προτάθηκε, σύμφωνα με ευρωπαϊκές πηγές τις οποίες επικαλείται το πρακτορείο ειδήσεων Reuters σε αποκλειστικό του ρεπορτάζ.\r\n\r\nΣύμφωνα με το έγγραφο το ελληνικό χρέος δεν θα γίνει βιώσιμο εάν δεν υπάρξει μεγάλη ελάφρυνσή του, ιδίως από τον επίσημο τομέα στην ΕΕ, ενώ η Ελλάδα θα χρειαστεί επιπρόσθετη χρηματοδοτική υποστήριξη ύψους 50 δισ. ευρώ τα επόμενα τρία χρόνια.\r\n\r\nΗ δημοσιοποίηση του εγγράφου αυτού αποκάλυψε τη διένεξη ανάμεσα στις Βρυξέλλες και τον διεθνή οικονομικό οργανισμό της Ουάσινγκτον, η οποία σιγόκαιγε για μήνες πίσω από κλειστές πόρτες, σχολιάζει το Reuters.\r\n\r\nΚατά τις πηγές του πρακτορείου, δεν υπήρξε ψηφοφορία για να αποφασιστεί εάν θα δημοσιευόταν η ανάλυση του ΔΝΤ για τη βιωσιμότητα του ελληνικού χρέους, αλλά οι Ευρωπαίοι, οι οποίοι αντιτάχθηκαν σε αυτή, ήταν πολύ λιγότεροι από εκείνους που την ενέκριναν -συμπεριλαμβανομένων και των ΗΠΑ, που έχουν τη μεγαλύτερη ισχύ στους κόλπους του θεσμού.\r\n\r\n', 'images/imagefornews/imf-logofff_425x.jpg', 2, '2015-07-06 14:48:40', 16, '', ''),
(79, 'Φαγητό και Διασκέδαση στη Νέα Σμύρνη\r\nΤα καλύτερα της γειτονιάς-I YOU FROZEN YOGHURT ', 'Άλλο ένα γιαουρτάδικο; Ε λοιπόν, όχι. Το I YOU, το πρώτο κατάστημα frozen yogurt που άνοιξε στην πλατεία Νέας Σμύρνης, είναι ξεχωριστό λόγω της εξαιρετικής ποιότητάς του. Το παγωμένο γιαούρτι ', 'Άλλο ένα γιαουρτάδικο; Ε λοιπόν, όχι. Το I YOU, το πρώτο κατάστημα frozen yogurt που άνοιξε στην πλατεία Νέας Σμύρνης, είναι ξεχωριστό λόγω της εξαιρετικής ποιότητάς του. Το παγωμένο γιαούρτι I YOU παρασκευάζεται σε ιδιόκτητο χώρο από έμπειρο προσωπικό, είναι φτιαγμένο από ολόφρεσκο ελληνικό παστεριωμένο γάλα ημέρας και από στραγγιστό γιαούρτι 2% λιπαρών και περιέχει φυτικές ίνες και πρεβιοτικά που βοηθούν το σώμα μας να λειτουργήσει σωστά ενισχύοντας την άμυνά του. Επιπλέον, είναι φανατικοί οπαδοί του όλοι εκείνοι που ενδιαφέρονται για τη σιλουέτα και την υγεία τους, αφού δεν περιέχει ζάχαρη ή άλλες χημικές ουσίες, αλλά στέβια και φρουκτόζη. Ενδεικτικά, τα 100 γραμμάρια έχουν περίπου 90 θερμίδες. Και μας κάνει καλό, και μας απαλλάσσει από τις τύψεις.', 'images/imagefornews/42229-94959.jpg', 9, '2015-07-06 11:10:11', 10, '', ''),
(80, 'Η διαδρομή του Τσίπρα από το Μαξίμου στο Σύνταγμα ', 'Ο Αλέξης Τσίπρας πήγε με τα πόδια από το Μέγαρο Μαξίμου στο Σύνταγμα για να μιλήσει. Η ερώτηση δημοσιογράφου από το εξωτερικό για το αν θα πιστεύει πώς θα κερδίσει και το “absolutely” του Πρωθυπουργού!', 'Ο Πρωθυπουργός Αλέξης Τσίπρας πήγε με τα πόδια από το Μέγαρο Μαξίμου στο Σύνταγμα για να εκφωνήσει την ομιλία του, μετρώνοντας αντίστροφα πλέον για το δημοψήφισμα της Κυριακής. Το πλήθος χειροκρότησε τον Έλληνα Πρωθυπουργό, ο οποίος μερικά λεπτά αργότερα έφτασε στο Σύνταγμα, παίρνοντας το χειροκρότημα όλων όσων είχαν παρεβρεθεί.\r\n\r\nΌσο περπατούσε ο Αλέξης Τσίπρας για να φτάσει στο χώρο που θα μιλούσε μία δημοσιογράφος από το εξωτερικό τον ρώτησε για το αν πιστεύει ότι θα κερδίσει, με τον κύριο Τσίπρα να απαντά: “absolutely”!', 'images/imagefornews/890.jpg', 1, '2015-07-06 14:59:45', 23, '', ''),
(81, 'Στήριξη εξέφρασε ο Πούτιν στον Τσίπρα', 'Τα αποτελέσματα του δημοψηφίσματος στην Ελλάδα και ζητήματα περαιτέρω ανάπτυξης της ελληνορωσικής συνεργασίας συζήτησαν ο πρωθυπουργός Αλέξης Τσίπρας και ο πρόεδρος Βλαντίμιρ Πούτιν στη διάρκεια τηλεφωνικής επικοινωνίας τους, σύμφωνα με την επίσημη ανακοίνωση του Κρεμλίνου.', '"Ο Βλαντίμιρ Πούτιν εξέφρασε την υποστήριξή του στο λαό της Ελλάδας ενώπιον των δυσκολιών που η χώρα οφείλει να υπερβεί", αναφέρεται στην ανακοίνωση της υπηρεσίας Τύπου του Κρεμλίνου που εκδόθηκε μετά την τηλεφωνική επικοινωνία των δύο ηγετών.\r\n\r\nΟ Αλέξης Τσίπρας και ο Βλαντίμιρ Πούτιν συζήτησαν επίσης "πολλά ζητήματα που συνδέονται με την ανάπτυξη της ρωσο-ελληνικής συνεργασίας", προστίθεται στην ανακοίνωση του Κρεμλίνου.\r\n\r\nΣημειώνεται πως ο πρόεδρος Πούτιν είχε την πρωτοβουλία της επικοινωνίας που πραγματοποιήθηκε άμεσα σε διάρκεια διαλείμματος της σύσκεψης των πολιτικών αρχηγών.\r\n\r\nΟ πρωθυπουργός είχε συνομιλίες και με την καγκελάριο Μέρκελ και τον Μάριο Ντράγκι.\r\n\r\nΔηλώσεις Πεσκόφ\r\n\r\nΗ Ρωσία ελπίζει στην επίτευξη μιας συμφωνίας της Ελλάδας με τους διεθνείς πιστωτές της το ταχύτερο δυνατό, δήλωσε νωρίτερα ο εκπρόσωπος του Κρεμλίνου Ντμίτρι Πεσκόφ, προσθέτοντας ότι τα ερωτήματα που αφορούν την οικονομική βοήθεια για την Ελλάδα θα πρέπει να απευθύνονται στην Αθήνα.\r\n\r\n"Θέλουμε οι Έλληνες εταίροι να επιτύχουν τους απαραίτητους συμβιβασμούς με τους πιστωτές το ταχύτερο και να πάρουν αποφάσεις που θα επιτρέπουν μια οικονομική και κοινωνική σταθερότητα με τον καλύτερο τρόπο", ανέφερε.\r\n\r\nΤο Κρεμλίνο σέβεται το αποτέλεσμα του δημοψηφίσματος και παρακολουθεί στενά τα γεγονότα στην Ελλάδα, κατέληξε ο εκπρόσωπος του Κρεμλίνου.', 'images/imagefornews/1112.jpg', 2, '2015-07-06 15:11:05', 59, '', ''),
(82, 'Ολοκληρώθηκε το συμβούλιο των πολιτικών αρχηγών -Εξουσιοδότηση από όλα τα κόμματα-πλην το ΚΚΕ', 'Ολοκληρώθηκε ύστερα από περίπου έξι ώρες το συμβούλιο των πολιτικών αρχών, ενώ από στιγμή σε στιγμή αναμένεται να δοθεί στη δημοσιότητα και το κοινό ανακοινωθέν.', 'Το ανακοινωθέν σύμφωνα με τον Στ. Θεοδωράκη θα αναφέρει το νέο πλαίσιο διαπραγμάτευσης που θα περιλαμβάνει και αναπτυξιακό πακέτο και είναι η κοινή εξουσιοδότηση, πλην ΚΚΕ, προς τον πρωθυπουργό να πάει στις Βρυξέλλες και να κλείσει τη συμφωνία το ταχύτερο.\r\nΝα συνδράμουν στο να υποστηριχθεί η σαφής και ξεκάθαρη ετυμηγορία του ελληνικού λαού από το χτεσινό δημοψήφισμα για μια βιώσιμη λύση, ζήτησε, σύμφωνα με κυβερνητικούς κύκλους, ο πρωθυπουργός Αλέξης Τσίπρας από τους υπόλοιπους πολιτικούς αρχηγούς κατά τη διάρκεια της σύσκεψης.\r\nΠληροφορίες αναφέρουν ότι υπήρξε νωρίτερα μία ακόμα ολιγόλεπτη διακοπή του συμβουλίου, προκειμένου ο Τσίπρας να έχει τηλεφωνική επικοινωνία με την Αγκελα Μέρκελ. Οι πληροφορίες από το Μαξίμου αναφέρουν ότι στην τηλεφωνική επικοινωνία αποφασίστηκε να καταθετούν στην αυριανή Σύνοδο Κορυφής οι νέες ελληνικές προτάσεις. Ουσιαστικά η συνάντηση των Ευρωπαίων ηγετών θα είναι η κρισιμότερη των τελευταίων ετών καθώς θα κριθούν τα πάντα με βάση την τελευταία πρόταση του Ζ.Κ. Γιούνκερ.\r\nΠηγές αναφέρουν ότι συντάσσεται κοινό κείμενο των αρχηγών για παραμονή της Ελλάδας στο ευρώ και αναζητεί βιώσιμη λύση εντός αυτής - διαφωνεί ο γραμματέας του ΚΚΕ Δημήτρης Κουτσούμπας. Ακόμη φέρεται να έπεσε στο τραπέζι η πρόταση για Εθνική Ομάδα Διαπραγμάτευσης.', 'images/imagefornews/symvoulioarxigwn761.jpg', 1, '2015-07-06 15:15:29', 74, '', ''),
(83, 'Ο Ομπάμα προειδοποιεί πως η μάχη εναντίον των τζιχαντιστών δεν θα είναι σύντομη', 'Ο αμερικανός πρόεδρος Μπαράκ Ομπάμα δεσμεύτηκε να αυξήσει την υποστήριξη των ΗΠΑ στην μετριοπαθή συριακή αντιπολίτευση -πυλώνα στον οποίο ελπίζει, μαζί με τους Κούρδους, για την αντιμετώπιση της τζιχαντιστικής ISIS- λέγοντας ότι οι ίδιες οι Ηνωμένες Πολιτείες χρειάζεται να κάνουν περισσότερα στο εσωτερικό για να αποτρέψουν επιθέσεις και να αντιμετωπίσουν τις προσπάθειες του Ισλαμικού Κράτους να στρατολογήσουν οπαδούς.', 'Μιλώντας στο Πεντάγωνο, ο Ομπάμα υπογράμμισε τη δυσκολία της αποτροπής επιθέσεων μικρής κλίμακας από μεμονωμένους ισλαμιστές μέσα στις ΗΠΑ, παρά τις επιτυχίες στην αποτροπή μεγάλης κλίμακας επιθέσεων όπως αυτές της 11ης Σεπτεμβρίου 2001 στη Νέα Υόρκη και Ουάσινγκτον. «Θα χρειαστεί να εντατικοποιήσουμε τις προσπάθειές μας για να αποτρέψουμε τέτοιες πράξεις» είπε.\r\n\r\nΟ Ομπάμα είπε ότι δεν υπήρχαν προς το παρόν σχέδια για την αποστολή περισσότερων αμερικανών στρατιωτών στο εξωτερικό και επανέλαβε ότι η μάχη ενάντια στους τζιχαντιστές της ISIS δεν θα είναι μικρής διάρκειας. Είπε ότι η εκπαίδευση των Ιρακινών δυνάμεων από αμερικανούς είχε εντατικοποιηθεί αφού κρίθηκε πολύ αργή.\r\n\r\n«Συνεχίζουμε να αυξάνουμε τους ρυθμούς παράδοσης υλικού κριτικής σημασίας, μεταξύ του οποίου συμπεριλαμβάνονται και αντιαρματικά όπλα, στις ιρακινές δυνάμεις ασφαλείας» είπε ο πρόεδρος των ΗΠΑ, «και έχω ξεκαθαρίσει στην ομάδα μου ότι θα κάνουμε περισότερα για να εκπαιδεύσουμε και εξοπλίσουμε την μετριοπαθή αντιπολίτευση στη Συρία».\r\n\r\nΟ Ομπάμα δεν έδωσε λεπτομέρειες για το τι παραπάνω θα μπορούσαν να κάνουν οι ΗΠΑ. Οι επικρίσεις των Ρεπουμπλικανών για «αποτυχημένη στρατηγική» έχουν ενταθεί, και αναμένεται να κορυφωθούν όσο πλησιάζει η μάχη των δύο κομμάτων για τον επόμενο ένοικο του Λευκού Οίκου.', 'images/imagefornews/22245020_US_Obama_Islamic_State_JPEG_04ace.limghandler.jpg', 2, '2015-07-07 11:52:27', 34, 'http://www.tanea.gr/news/world/article/5255901/o-ompama-proeidopoiei-pws-h-maxh-enantion-twn-tzixantistwn-den-tha-einai-syntomh/', ''),
(84, '"Έφυγε" ο γηραιότερος άνδρας στον κόσμο, ετών 112!', 'O Ιάπωνας Σακάρι Μομόι, ο οποίος πέρυσι είχε ανακηρυχθεί ο γηραιότερος άνθρωπος στον κόσμο, έσβησε σε ηλικία 112 ετών, ανακοίνωσαν οι αρχές της πόλης Σαϊτάμα, βορείως του Τόκιο, όπου ο εκλιπών ζούσε πολλά χρόνια.', 'Πέθανε την Κυριακή από νεφρική ανεπάρκεια σε οίκο ευγηρίας της πρωτεύουσας όπου διέμενε. "Η οικογένειά του μας είχε πει ότι η υγεία του είχε επιδεινωθεί πριν από μια ή δύο εβδομάδες", δήλωσε δημοτικός αξιωματούχος.\r\nΓεννημένος στις 5 Φεβρουαρίου του 1903 στη Μιναμισόμα της επαρχίας Φουκουσίμα, ο Μομόι ήταν εκπαιδευτικός, πρώην λυκειάρχης, λάτρης της καλλιγραφίας, των βιβλίων, κυρίως της κινεζικής ποίησης, καθώς και του σούμο. Παντρεύτηκε το 1928 και απέκτησε πέντε παιδιά.\r\nΌταν τον Αύγουστο του 2014 του απομενήθηκε το ρεκόρ Γκίνες, η κομψή του παρουσία είχε εντυπωσιάσει: φορούσε μαύρο κοστούμι και ασημί γραβάτα, ενώ χαμηλόφωνα είχε εκφράσει την επιθυμία να ζήσει ακόμη δύο χρόνια.\r\nΤο προσδόκιμο ζωής των ανδρών στην Ιαπωνία ξεπέρασε για πρώτη φορά το 2013 το όριο των 80 ετών (80,21 έτη), ενώ οι συμπατριώτισσές τους διατηρούν την παγκόσμια πρωτιά μακροβιότητας (86,61 έτη).Σχεδόν το ένα τέταρτο του πληθυσμού στην Ιαπωνία είναι 65 ετών και άνω, μια αναλογία που αναμένεται να ανέλθει στο 40% μέχρι το 2060. Τον περασμένο Σεπτέμβριο υπήρχαν σχεδόν 59.000 εκατοντάχρονοι στη χώρα -το 87% αυτών γυναίκες-, σύμφωνα με τα επίσημα στατιστικά στοιχεία του υπουργείου Υγείας.Ο γηραιότερος άνθρωπος στον κόσμο είναι πλέον κατα τα φαινόμενα ο Ιάπωνας Γιασουτάρο Κόιντε, που γεννήθηκε τον Μάρτιο του 1903, έναν μήνα μετά τη γέννηση του Μομόι. Η Ιαπωνία έχασε ωστόσο την άνοιξη τη γηραιότερη γυναίκα του κόσμου. Στο εξής η μακροβιότερη γυναίκα παγκοσμίως είναι η Αμερικανίδα Σουζάνα Μάσατ Τζόουνς, που γεννήθηκε στις 6 Ιουλίου του 1899 στη νότια πολιτεία Αλαμπάμα και γιόρτασε τη Δευτέρα στη Νέα Υόρκη τα 116α γενέθλιά της.\r\nΤο παγκόσμιο ρεκόρ μακροβιότητας επισήμως ανήκει στην Γαλλίδα Ζαν Καλμάν, η οποία πέθανε το 1997 σε ηλικία 122 ετών και 164 ημερών.', 'images/imagefornews/image (1).jpg', 2, '2015-07-07 11:55:53', 9, '', '');
INSERT INTO `nea` (`ID`, `Titlos_arthrou`, `Small_text`, `Keimeno`, `Eikona`, `category`, `Time`, `counter`, `url`, `source`) VALUES
(85, 'Νορβηγοί εμπειρογνώμονες: Έχετε πετρέλαιο για τα επόμενα 100 χρόνια', 'Πετρέλαιο και φυσικό αέριο για…100 χρόνια έχει η Ελλάδα!!Αυτή την απίστευτη ρήση είπαν οι Νορβηγοί ολοκληρώνοντας την έρευνά τους για τους ελληνικούς υδρογονάνθρακες! Οχι μόνο δε χρωστάμε συνέλληνες, αλλά αν φύγουμε απ το γιούρο και μέσα στα επόμενα 5 χρόνια όταν πέσει και η πρώτη γεωτρυπανιά, η Ελλάδα θα είναι η …νέα Σαουδική Αραβία του 21ου Αιώνα!', 'Τέσσερις περιοχές καταδεικνύουν μεγάλες δυνατότητες και σημαντική δυναμική σε κοιτάσματα υδρογονανθράκων (φυσικού αερίου και πετρελαίου).\r\n\r\nΑυτό ανέφερε ο υπουργός Γιάννης Μανιάτης, ο οποίος ταυτόχρονα ανακοίνωσε πως από σήμερα η νορβηγική εταιρεία PGS, που συνέλεξε στοιχεία μήκους 12.500 χλμ., θα πουλάει τα δεδομένα σε πετρελαϊκές εταιρείες.\r\n\r\nΤο πρόγραμμα καταγραφής, επεξεργασίας και ερμηνείας των δεδομένων, που ελήφθησαν με τη χρήση Geostreamer GS, μια καινοτόμο τεχνολογία της PGS, η οποία ελαχιστοποιεί θορύβους στα δεδομένα και διευκολύνει την πιο ευκρινή και σε βάθος αναζήτηση.\r\n\r\nΗ επεξεργασία των δεδομένων είναι σε εξέλιξη και ολοκληρωθεί τον Δεκέμβριο του 2013, καθώς το αρχικό πρόγραμμα της συλλογής των δεδομένων επεκτάθηκε από τα 8.500 χιλιόμετρα στα 12.500 χιλιόμετρα. Από τότε το θέμα το απέκρυψαν!\r\n\r\nΗ πρώτη περιοχή ξεκινά από τα βορειοδυτικά της Κέρκυρας, στα όρια με την Αλβανία, και εκτείνεται προς τα κάτω, στους Παξούς και στους Αντίπαξους, μέχρι και τη θαλάσσια περιοχή του Ιονίου απέναντι από τον Αμβρακικό Κόλπο.\r\n\r\nΗ δεύτερη περιοχή είναι δυτικά της Κεφαλονιάς και φτάνει μέχρι την τρίτη θαλάσσια περιοχή ανοιχτά του Κατάκολου.\r\n\r\nΗ τέταρτη είναι η πολλά υποσχόμενη περιοχή νότια της Κρήτης, που εκτείνεται μέχρι τα όρια με τη Λιβύη και δυτικά της Ιταλίας.\r\n\r\nΠαράλληλα, τα προσεχή 24ωρα η πολιτική ηγεσία του ΥΠΕΚΑ, κατόπιν γνωμοδότησης της ειδικής επιτροπής, θα ανακοινώσει τις εταιρείες αναδόχους των τριών πρώτων “οικοπέδων” που θα ξεκινήσουν τις έρευνες με γεωτρύπανα. Πρόκειται για τα Ιωάννινα, τον Πατραϊκό Κόλπο και το δυτικό Κατάκολο.\r\n\r\nΓύρω στα τέλη του επόμενου χρόνου αναμένεται να πέσουν και τα πρώτα γεωτρύπανα που θα δείξουν και το τελικό μέγεθος των κοιτασμάτων.\r\n\r\nΟι εκτιμήσεις αξιωματούχων του ΥΠΕΚΑ κάνουν λόγο για τη δυνατότητα από την ύπαρξη κοιτασμάτων υδρογονανθράκων στον ελληνικό χώρο να εισπράττει το Δημόσιο περί τα 150 δισ. ευρώ σε βάθος 30 ετών.', 'images/imagefornews/468petrelaio9.jpg', 1, '2015-07-08 16:42:24', 14, 'http://tro-ma-ktiko.blogspot.gr/2015/07/100_8.html', ''),
(86, 'Επικείμενη πλημμύρα γενετικών δεδομένων θα γονάτιζε και τη Google', 'Το κόστος ανάγνωσης των γονιδιωμάτων πέφτει, και η ροή δεδομένων θα λάβει σύντομα διαστάσεις πλημμύρας. Σε λίγα χρόνια, προειδοποιεί νέα μελέτη, η διαχείριση αυτών των πληροφοριών θα απαιτεί μεγαλύτερους υπολογιστικούς πόρους από ό,τι το YouTube ή το Twitter.', 'Το διεθνές Πρόγραμμα Ανθρώπινου Γονιδιώματος, το οποίο ολοκληρώθηκε το 2003, χρειάστηκε δισεκατομμύρια δολάρια για να διαβάσει το DNA που αντιστοιχεί σε έναν μόνο άνθρωπο. Σήμερα, το κόστος έχει πέσει σχεδόν στα 1.000 δολάρια ανά γονιδίωμα και συνεχίζει να μειώνεται με εκθετικό ρυθμό.\r\n\r\nΣύμφωνα με τη νέα μελέτη, η οποία δημοσιεύεται στην επιθεώρηση PLoS Biology, μέχρι το 2025 θα έχουν ολοκληρωθεί σε όλο τον κόσμο 100 εκατομμύρια έως 2 δισεκατομμύρια ανθρώπινα γονιδιώματα.\r\n\r\nΗ αποθήκευση αυτών των δεδομένων θα απαιτούσε τεράστια αποθηκευτική ικανότητα, ειδικά αν λάβει κανείς υπόψη ότι τα δεδομένα που πρέπει να συνοδεύουν κάθε γονιδίωμα είναι 30 φορές περισσότερα από την ίδια την αλληλουχία του γονιδιώματος, προκειμένου να αναγνωρίζονται τυχόν λάθη στη διαδικασία αλληλούχισης.\r\n\r\nΟι ερευνητές εκτιμούν ότι, μέχρι το 2025, η αποθήκευση αυτών των γενετικών πληροφοριών θα απαιτεί χωρητικότητα 2 έως 40 exabyte κάθε χρόνο (ένα exabyte ισούται με ένα τρισεκατομμύριο megabyte).\r\n\r\nΟ χωρητικότητα αυτή ξεπερνά κατά πολύ τα 1 με 2 exabyte των βίντεο που εκτιμάται ότι θα αποθηκεύονται κάθε χρόνο στο YouTube της Google μέχρι το 2025. Θα ξεπερνούσε επίσης κατά πολύ τις ετήσιες αποθηκευτικές ανάγκες του Twitter, οι οποίες εκτιμάται να φτάσουν μέχρι το 2025 τα 1 με 17 petabyte (δισεκατομμύρια megabyte).\r\n\r\nΟι αστρονομικοί αυτοί αριθμοί αφορούν μόνο την αποθήκευση των δεδομένων, και δεν καλύπτουν τις ανάγκες σε υπολογιστικούς πόρους για την ανάλυση των γενετικών πληροφοριών, επισημαίνει η μελέτη.\r\n\r\nΤα ευρήματα «κρούουν των κώδωνα του κινδύνου για τις τεράστιες προκλήσεις» που θα αντιμετωπίσει η ερευνητική κοινότητα, σχολιάζει στο Nature.com ο Τζιν Ρόμπινσον, μέλος της ερευνητικής ομάδας στο Πανεπιστήμιο του Ίλινοϊ στο Ουρμπάνα Σαμπέιν.\r\n\r\nΌπως φαίνεται, η σχετικά νέα επιστήμη της Γενωμικής θα αποτελέσει στο προσεχές μέλλον την κινητήριο δύναμη για την εξέλιξη των υπολογιστών σε κάτι πολύ, πάρα πολύ ισχυρότερο.\r\n\r\nΒαγγέλης Πρατικάκης\r\nNewsroom ΔΟΛ', 'images/imagefornews/server_room_20130422.jpg', 8, '2015-07-09 16:00:57', 17, '', ''),
(87, 'Δημοψήφισμα 2015: Άγριος καβγάς Ψαριανού - Μακρυγιάννη - Αξελού..."Μην έρχεσαι πιωμένος"', 'Καβγάς εκτός ορίων στο τραπέζι συζήτησης του Alpha! Όταν ο Γρηγόρης Ψαριανός αποκάλεσε τον βουλευτή του ΣΥΡΙΖΑ Γιάννη Μακρυγιάννη "Κασιδιάρη", ακολούθησε ένα καβγάς άνευ προηγουμένου με σκληρούς χαρακτηρισμούς. ', 'ΜΑΚΡΥΓΙΑΝΝΗΣ:Είναι ντροπή σου να μαζεύεις πελατεία και ψηφαλάκια! Είσαι ξεφτίλας!\r\nΨΑΡΙΑΝΟΣ: Ο Κασιδιάρης και ο Κουφοντίνας μαζί είσαστε!\r\nΜΑΚΡΥΓΙΑΝΝΗΣ: Άκουσε να δεις κάτι, όταν θα έρχεσαι στο στούντιο δεν θα είσαι πιωμένος! \r\nΨΑΡΙΑΝΟΣ: Στην υγειά σου Κασιδιάρη!\r\nΜΑΚΡΥΓΙΑΝΝΗΣ: Πόσα πήρες να ψηφίσεις ρε; Πουλημένε! \r\nΨΑΡΙΑΝΟΣ: 500 ρούβλια!\r\nΜΑΚΡΥΓΙΑΝΝΗΣ: Πονάει απόψε που σας έσκισε ο Τσίπρας;\r\n\r\nΚι ενώ οι παρουσιαστές προσπαθούσαν μάταια να ηρεμήσουν την κατάσταση, μια "έκρηξη" του Ρήγα Αξελού που καθόταν δίπλα από τον Γρηγόρη Ψαριανό ώθησε την κατάσταση στα άκρα! «Κόψε τη χυδαιότητα μαλ...! Σε έβρισα και δεν το παίρνω και πίσω!», είπε ο Ρήγας Αξελός. «Σταλινοφασίστες!», ήταν η απάντηση του Γρηγόρη Ψαριανού.\r\n', 'images/imagefornews/psarianos_alpha_tsakomos.jpg', 1, '2015-07-09 17:42:00', 20, 'http://www.zappit.gr/stories/Dhmopshfisma-2015-Agrios-kabgas-PSarianoy---Makrygiannh---Akseloy-Mhn-erxesai-piomenos/41091', 'https://www.youtube.com/watch?v=KcPXqJ4qUnM'),
(88, 'Πάπας: Να σταματήσει η «γενοκτονία» των χριστιανών στη Μ.Ανατολή', 'Ο Πάπας Φραγκίσκος κάλεσε σήμερα από τη Βολιβία όπου πραγματοποιεί επίσκεψη να σταματήσει η "γενοκτονία" εναντίον των χριστιανών στη Μέση Ανατολή και αλλού στον κόσμο, πέμπτη ημέρα της περιοδείας του στη Νότια Αμερική.', '"Σήμερα βλέπουμε με τρόμο πώς στη Μέση Ανατολή και αλλού διώκονται, βασανίζονται πολλοί χριστιανοί αδελφοί", δήλωσε ο Ποντίφικας κατά τη διάρκεια μιας Παγκόσμιας Συνάντησης Λαϊκών Κινημάτων στη Σάντα Κρουζ.\r\n\r\n"Πρέπει επίσης να το καταγγείλουμε αυτό: σε αυτόν τον τρίτο παγκόσμιο πόλεμο που διεξάγεται τμηματικά και τον οποίο βιώνουμε, διαπράττεται ένα είδος γενοκτονίας που πρέπει να σταματήσει", τόνισε ο πρώτος ιησουίτης και λατινοαμερικανός Πάπας στην ιστορία.\r\n\r\nΔεν είναι η πρώτη φορά που ο Προκαθήμενος της Ρωμαιοκαθολικής Εκκλησίας αναφέρεται με τόσο σκληρούς όρους στην κατάσταση των χριστιανών στη Μέση Ανατολή, κυρίως στο Ιράκ και τη Συρία, οι οποίοι υφίστανται διώξεις λόγω της προέλασης των τζιχαντιστών της οργάνωσης Ισλαμικό Κράτος.\r\n\r\nΤον Σεπτέμβριο του 2014, ο Πάπας για πρώτη φορά μίλησε για έναν "τρίτο παγκόσμιο πόλεμο που διεξάγεται τμηματικά" όταν εξέφραζε τη λύπη του για την άναρχη βία που επικρατούσε στον κόσμο.', 'images/imagefornews/fragkiskos.jpg', 2, '2015-07-10 09:20:13', 26, '', 'http://www.amna.gr/articleview.php?id=83060'),
(89, 'Ινδονησία: Έκρηξη ηφαιστείου - χιλιάδες εγκλωβισμένοι τουρίστες', 'Το όρος Ραούνγκ εκρήγνυται σχεδόν εδώ και μια εβδομάδα, με αποτέλεσμα να έχουν ακυρωθεί 112 πτήσεις από και προς Μπαλί.\r\n\r\n', 'Οι ινδονησιακές αρχές έκλεισαν σήμερα πέντε αεροδρόμια, ανάμεσά τους το Νγκουράχ Ράι του Μπαλί, του πιο δημοφιλούς τουριστικού προορισμού, λόγω της έκρηξης του ηφαιστείου Ραούνγκ στην Ανατολική Ιάβα, στην κεντρική χώρα, με χιλιάδες τουρίστες να έχουν εγκλωβιστεί στο νησί, ανακοίνωσε ο εθνικός αερομεταφορέας Garuda Indonesia στον επίσημο λογαριασμό του στο Twitter.\r\n\r\nΤο όρος Ραούνγκ εκρήγνυται σχεδόν εδώ και μια εβδομάδα, με αποτέλεσμα να έχουν ακυρωθεί 112 πτήσεις από και προς Μπαλί. Αυστραλιανές αεροπορικές εταιρίες επίσης ακύρωσαν πτήσεις τους προς και από το νησί αυτό, το πιο τουριστικό της χώρας. Το Μπαλί είναι ένας από τους αγαπημένους προορισμούς των Αυστραλών. Εκατοντάδες ξένοι τουρίστες έχουν εγκλωβιστεί στο αεροδρόμιο, περιμένοντας πληροφορίες για τις πτήσεις τους.\r\n\r\nΕπίσης το αεροδρόμιο του Λομπόκ, γειτονικού νησιού του Μπαλί, έκλεισε, μετά την έκρηξη του ηφαιστείου από το οποίο αναδύονταν πυκνά σύννεφα στάχτης, δήλωσε ο Τζ.Α. Μπαράτα, εκπρόσωπος του υπουργείου Μεταφορών.\r\n\r\n"Το μέτρο αυτό λήφθηκε για λόγους ασφαλείας. Παρακολουθούμε την κατάσταση από κοντά, δεν είμαστε σε θέση να γνωρίζουμε πότε τα αεροδρόμια της επαρχίας της Ανατολικής Ιάβας θα ανοίξουν ξανά".\r\n\r\nΤο επίπεδο συναγερμού στο βουνό Ραούνγκ, ένα ηφαίστειο με υψόμετρο περίπου 3.300 μέτρων αυξήθηκε --μόλις πριν από το υψηλότερο επίπεδο συναγερμού- στο τέλος του Ιουνίου, αφότου το ηφαίστειο άρχισε να εκτοξεύει λάβα και πυκνά σύννεφα στάχτης να καλύπτουν τον ουρανό.\r\n\r\nΤο όρος Ραούνγκ είναι ένα από τα 129 ενεργά ηφαίστεια της Ινδονησίας, ενός αρχιπελάγους που βρίσκεται στο "δαχτυλίδι της φωτιάς" του Ειρηνικού, μια μεγάλη περιοχή ιδιαίτερα σεισμογενής η οποία σχηματίζεται από ακτογραμμές και σειρά από ηφαίστεια που βρέχονται από τον Ειρηνικό Ωκεανό και επηρεάζονται άμεσα από την κίνηση και τις συγκρούσεις των τεκτονικών πλακών.\r\n', 'images/imagefornews/069AD2D39309FD11573B3B15F899E5AF.jpg', 2, '2015-07-10 09:43:30', 40, 'http://www.skai.gr/news/world/article/285742/indonisia-ekrixi-ifaisteiou-hiliades-eglovismenoi-touristes/', 'http://www.skai.gr/news/world/article/285742/indonisia-ekrixi-ifaisteiou-hiliades-eglovismenoi-touristes/'),
(90, 'Μπεργκ: "Αυτό που βλέπω μου αρέσει"', 'Ο Μάρκους Μπεργκ μίλησε για τη νέα σεζόν που αρχίζει και εξέφρασε την επιθυμία του να παραμείνει μέχρι το τέλος του συμβολαίου του στον Παναθηναϊκό, ενώ μίλησε και για την κατάσταση στην Ελλάδα.', 'κολουθούν αποσπάσματα όσων δήλωσε στην εφημερίδα "Goal":\r\n\r\n"Έχω υπογράψει με τον σύλλογο για ακόμα τρία χρόνια. Θα είμαι 32 όταν τελειώσει το νέο μου συμβόλαιο μου κι αυτό θέλω να γίνει! Αλλά βέβαια ποτέ δεν ξέρεις στο ποδόσφαιρο. Η αλήθεια είναι ότι αισθάνομαι πολύ καλά εδώ. Όχι μόνο στο κλαμπ, αλλά και στη χώρα. Δεν υπάρχει συζήτηση γι αυτό!\r\n\r\nΟ κάθε ποδοσφαιριστής θέλει να παίζει σε ένα μεγάλο κλαμπ, να κερδίζει πράγματα, να πετυχαίνει τους στόχους του κι όλα αυτά υπάρχουν στον Παναθηναϊκό. Υπάρχει βέβαια και η οικογένεια μου που είναι απόλυτα ευχαριστημένη. Να ξέρεις ότι έναν ποδοσφαιριστή μπορεί να τον μεταχειριστούν ορισμένες φορές στην καριέρα του σαν ζώο κι όχι ως άνθρωπο.\r\n\r\nΣτον Παναθηναϊκό αυτό δεν ισχύει! Ό,τι κάνουν το κάνουν με καρδιά! Δίνουν κίνητρα, ενθαρρύνουν τον παίκτη κι αυτό είναι πολύ σημαντικό για τις δύο πλευρές προκειμένου να συνεχίσουν να πορεύονται μαζί για χρόνια", είπε αναφερόμενος με την επιλογή του να συνεχίσει στο "τριφύλλι".\r\n\r\nΓια το πόσο ανησυχεί με την κατάσταση στην Ελλάδα: "Λίγο ασφαλώς. Όλοι ανησυχούν σ΄ έναν βαθμό. Ελπίζω να βρεθεί κάποια λύση και να επιστρέψουν τα πράγματα σε μία θετική ροή. Ίσως αυτό να χρειαστεί να πάρει χρόνο. Πρέπει όμως όλοι να δουλεύουν προς αυτήν την κατεύθυνση. Δεν θα είναι εύκολο, δεν μπορεί ένας μόνος του να το πετύχει αυτό. Πρέπει όλη η Ευρώπη να το κάνει. Όχι μόνο η Ελλάδα".\r\n\r\nΤο μήνυμα του προς τον κόσμο: "Αυτό που θέλω να τους πω είναι να συνεχίζουν να μας στηρίζουν όπως τις προηγούμενες χρονιές. Δεν μπορούν να καταλάβουν πόσο πολύ μας βοηθούν όταν το γήπεδο είναι γεμάτο και υπάρχει αυτή η ατμόσφαιρα. Ελπίζω να συνεχίσουν έτσι και φέτος, να είναι το γήπεδό μας σε ακόμη περισσότερα ματς γεμάτο, για να τους φέρνουμε χαρά και ευτυχία. Για να κάνουμε κάτι καλό πρέπει να το κάνουμε όλοι μαζί".\r\n\r\nΓια τις μεταγραφές: "Είναι πολύ νωρίς ακόμα, προπονούμαστε μόλις πέντε ημέρες μαζί. Ο Σάντσεθ ήρθε προχθές, όλοι όμως γνωρίζουν τι παίκτης είναι. Ο Εσιέν επίσης είναι πολύ καλός ποδοσφαιριστής και όλοι πιστεύω ότι θα μας βοηθήσουν. Το πιο σημαντικό είναι να ξέρεις ότι οι μεταγραφές θα βοηθήσουν την ομάδα. Και μέχρι στιγμής αυτό που βλέπω μου αρέσει".\r\n', 'images/imagefornews/berg_ekpomph.jpg', 6, '2015-07-10 09:53:01', 3, '', 'http://www.contra.gr/Soccer/Hellas/Superleague/panathinaikos/mpergk-ayto-poy-vlepw-moy-aresei.3568528.html'),
(91, 'Πέθανε ο Ομάρ Σαρίφ', 'Ο διάσημος Αιγύπτιος ηθοποιός Όμαρ Σαρίφ απεβίωσε από καρδιακή ανακοπή στο Κάιρο σε ηλικία 83 ετών, ανακοίνωσε ο ατζέντης του. \r\nΟ Σαρίφ γεννήθηκε στις 10 Απριλίου του 1932 Αλεξάνδρεια της Αιγύπτου. Ήταν γιος της Κλαιρ και του Ιωσήφ Χαλούμπ, ενός εμπόρου ξυλείας. Στην οικογένειά του ήταν καθολικοί στο θρήσκευμα. Ο Σαρίφ αποφοίτησε από το Κολλέγιο Βικτωρία της Αλεξάνδρειας και κατόπιν από το Πανεπιστήμιο του Καΐρου, όπου σπούδασε φυσική και μαθηματικά. ', 'Κατόπιν εργάστηκε με τον πατέρα του στην οικογενειακή επιχείρηση ξυλείας. Το 1953, ο Σαρίφ ξεκίνησε την καριέρα του σε μία αιγυπτιακή ταινία, με τίτλο Σιρα’ Φι αλ-Γουαντι (Ο Φλογερός Ήλιος ή Μάχη στην Κοιλάδα). Ακολούθησαν πολυάριθμες αιγυπτιακές παραγωγές. Πρωταγωνίστησε στο πλάι της πρώην συζύγου του, Φατέν Χαμαμά, σε αρκετές ρομαντικές ταινίες. Ανάμεσα στις ταινίες της φιλμογραφίας του περιλαμβάνονται: Οι Καλύτερες μας Μέρες (1955), Δεν Κοιμάμαι (1958), Η Κυρία του Κάστρου (1959) και η διασκευή της Άννα Καρένινα με τίτλο Ο Ποταμός της Αγάπης (1961). Η πρώτη αγγλόφωνη ταινία του Σαρίφ ήταν Λώρενς της Αραβίας το 1962 όπου υποδύθηκε τον Σαρίφ Άλι. \r\n\r\nΗ ερμηνεία του αυτή του εξασφάλισε μια υποψηφιότητα για Όσκαρ Β’ Ανδρικού Ρόλου, μια βράβευση με Χρυσή Σφαίρα Β’ Ανδρικής Ερμηνείας σε Κινηματογραφική Ταινία και Πολλά Υποσχόμενου Άνδρα Ηθοποιού και παγκόσμια φήμη ως ο δημοφιλέστερος Γαλλοάραβας ηθοποιός. Ο Σαρίφ επίσης υποδύθηκε τον ομώνυμο ρόλο στην ταινία του 1965 Δόκτωρ Ζιβάγκο του σκηνοθέτη Ντέιβιντ Λην. Μετά από μια περίοδο κατά τη διάρκεια της οποίας έμπαινε στους τίτλους των εφημερίδων για τις επιδόσεις του στο επαγγελματικό μπριτζ, έκανε την επάνοδό του το 2003 στη διασκευή του μυθιστορήματος Ο Κύριος Ιμπραήμ και τα Λουλούδια του Κορανίου. Για την ερμηνεία του βραβεύτηκε στο Φεστιβάλ Κινηματογράφου Βενετίας και με Βραβείο Σεζάρ. Το Νοέμβριο του 2005 τιμήθηκε με μετάλλιο από την UNESCO σαν αναγνώριση της αξιοπρόσεκτης προσφοράς του στην ποικιλότητα του παγκόσμιου κινηματογράφου και του πολιτισμού. \r\n\r\nΤο μετάλλιο – το οποίο αποδίδεται πολύ σπάνια – φέρει το όνομα του Ρώσου σκηνοθέτη Σεργκέι Άιζενσταϊν και μπορεί να απονεμηθεί συνολικά 25 φορές από τη Mosfilm της Ρωσίας. Ο Σαρίφ προσηλυτίστηκε στο Ισλάμ ώστε να παντρευτεί τη διάσημη Αιγύπτια ηθοποιό Φατέν Χαμαμά το 1955, οπότε και πήρε το όνομα Ομάρ αλ-Σαρίφ. Ο γάμος τους κράτησε περίπου 20 χρόνια και έλαβε τέλος το 1974. Καρπός του ήταν ένα παιδί, ο Ταρέκ Σαρίφ (γεν. 1957), που εμφανίστηκε στο Δόκτωρ Ζιβάγκο σε ηλικία 8 ετών, στο ρόλο του μικρού Γιούρι. Φήμες που ήθελαν το Σαρίφ να παντρεύεται την ηθοποιό Σοχαΐρ Ραμζί το 1977, αποδείχτηκαν αναληθείς', 'images/imagefornews/590_1bb4519b06dc2f443efc568145b73544[1].png', 1, '2015-07-10 18:36:13', 22, 'http://www.karatzova.com/2015/07/blog-post_577.html', 'http://www.karatzova.com/2015/07/blog-post_577.html'),
(92, 'Πουτίν: Η κρίση στην Ελλάδα θα διευθετηθεί σύντομα', 'Ο πρόεδρος της Ρωσίας Βλαντίμιρ Πούτιν εξέφρασε την εκτίμηση ότι η κρίση με την Ελλάδα θα διευθετηθεί σύντομα, ενώ επανέλαβε πως η Αθήνα δεν έχει ζητήσει οικονομική βοήθεια από την κυβέρνησή του. Επικρίνοντας εμμέσως τους χειρισμούς της Ευρωπαϊκής Ένωσης, ο Πούτιν διερωτήθηκε πού ήταν η Ε.Ε. όταν τα προβλήματα συσσωρεύονταν στην Ελλάδα.', 'Ρωσικό ενδιαφέρον για λιμάνια, σιδηρόδρομους\r\nΤο ρωσικό υπουργείο Μεταφορών αναμένει από την Ελλάδα την ανακοίνωση της δεύτερης φάσης του διαγωνισμού για την ιδιωτικοποίηση περιουσιακών στοιχείων στις μεταφορές, είπε ο υπουργός Μεταφορών της Ρωσίας Μαξίμ Σοκολόφ. ', 'images/imagefornews/Vladimir.jpg', 2, '2015-07-12 08:13:02', 18, '', 'http://www.avgi.gr/article/5702159/poutin-i-krisi-stin-ellada-tha-dieuthetithei-suntoma'),
(93, 'Α. Μέρκελ: Θα είναι μια δύσκολη νύχτα με σκληρές διαπραγματεύσεις', 'Θα είναι μια δύσκολη νύχτα με σκληρές διαπραγματεύσεις είπε η Γερμανίδα καγκελάριος Άνγκελα Μέρκελ προσερχόμενη στη Σύνοδο Κορυφής των αρχηγών κρατών και κυβερνήσεων της ζώνης του ευρώ.', 'Θα είναι μια δύσκολη νύχτα με σκληρές διαπραγματεύσεις είπε η Γερμανίδα καγκελάριος Άνγκελα Μέρκελ προσερχόμενη στη Σύνοδο Κορυφής των αρχηγών κρατών και κυβερνήσεων της ζώνης του ευρώ.\r\n\r\nΗ κα Μέρκελ σημείωσε ότι η ελληνική οικονομία έχει επιδεινωθεί πολύ το τελευταίο διάστημα και τόνισε ότι θα γίνουν συζητήσεις με τεντωμένα τα νεύρα, διαμηνύοντας πως δεν είναι διατεθειμένη να πει «ναι» σε « συμφωνία με κάθε κόστος».\r\n\r\nΠρέπει να εξασφαλιστεί ότι τα πλεονεκτήματα ξεπερνούν τα μειονεκτήματα, όχι μόνο για την Ελλάδα αλλά και για την ευρωζώνη, ανέφερε η καγκελάριος της Γερμανίας', 'images/imagefornews/12-17443415537029.jpg', 1, '2015-07-12 16:30:30', 69, '', 'http://www.amna.gr/article/83242/A.-Merkel:-Tha-einai-mia-duskoli-nuchta-me-sklires-diapragmateuseis-'),
(94, 'Ο υπερυπολογιστής Aris στα 500 ισχυρότερα συστήματα, παγκοσμίως!', 'Ο υπερυπολογιστής Aris, με ισχύ σχεδόν 180 τρισεκατομμυρίων πράξεων ανά δευτερόλεπτο, κατασκευάστηκε από την IBM για το Εθνικό Δίκτυο Έρευνας και Τεχνολογίας και τέθηκε σε δοκιμαστική λειτουργία στις αρχές Ιουνίου.', 'Λίγους μήνες πριν τεθεί σε πλήρη λειτουργία στο υπόγειο του υπουργείο Παιδείας στο Μαρούσι, ο πρώτος υπερυπολογιστής της Ελλάδας κατάφερε να μπει στη λίστα με τα 500 ισχυρότερα συστήματα του κόσμου.\r\n\r\nΟ υπερυπολογιστής Aris, με ισχύ σχεδόν 180 τρισεκατομμυρίων πράξεων ανά δευτερόλεπτο, κατασκευάστηκε από την IBM για το Εθνικό Δίκτυο Έρευνας και Τεχνολογίας και τέθηκε σε δοκιμαστική λειτουργία στις αρχές Ιουνίου.\r\n\r\nΒρίσκεται πλέον στην 468η θέση του τελευταίου Top500, το οποίο ενημερώνεται δύο φορές το χρόνο από την ομάδα του καθηγητή Χανς Μόιερ στο Πανεπιστήμιο του Μάνχαϊμ στη Γερμανία.\r\n\r\nΤο σύστημα αποτελείται από επεξεργαστές Intel Xeon με συνολικά 8.500 πυρήνες, μοιρασμένους σε 8.500 υπολογιστικούς κόμβους. \r\n\r\nΣύμφωνα με το τεστ Linpack του Top500, το οποίο βαθμολογεί τους υπολογιστές ως προς την ταχύτητα εκτέλεσης μιας ειδικής γραμμικής εξίσωσης, το Aris προσφέρει ισχύ 169,7 teraflop/sec (τρισεκατομμύρια πράξεις κινητής υποδιαστολής ανά δευτερόλεπτο). Η μέγιστη θεωρητική ισχύς εκτιμάται στα 190,8 Tflop/sec.\r\n\r\nΓια τη λειτουργία του Aris στο υπουργείο απαιτούνται ούτε λίγο ούτε πολύ 154 kilowatt ηλεκτρικής ενέργειας, όσο θα αρκούσε για μερικές πολυκατοικίες.\r\n\r\nΑπό το φθινόπωρο, οπότε θα τεθεί σε πλήρη λειτουργία, ο εθνικός υπερυπολογιστής θα είναι διαθέσιμος στην ελληνική ερευνητική κοινότητα. Επιστημονικοί τομείς που απαιτούν τέτοια συστήματα υψηλών επιδόσεων είναι ενδεικτικά η Υπολογιστική Χημεία, η Φυσική, η Βιολογία, η Βιοϊατρική, η Μετεωρολογία, η Σεισμολογία, η Υπολογιστική Μηχανή και οι Επιστήμες Υλικών.\r\n\r\nΣύμφωνα με τον Π.Τσανάκα, επικεφαλής του Εθνικού Δικτύου Έρευνας και Τεχνολογίας (ΕΔΕΤ) και καθηγητή στο ΕΜΠ, τα δικαιώματα χρήσης του συστήματος θα παραχωρούνται με διεθνή κριτήρια όπως η αξία του ερευνητικού έργου και οι πόροι που απαιτούνται. Για έρευνες μικρών απαιτήσεων θα υπάρχει η δυνατότητα παραχώρησης ωρών μικρό όγκο απαιτήσεων προβλέπεται η χρήση του HPC τις λεγόμενες "core hours". Για μεγαλύτερα, και πιο απαιτητικά ερευνητικά έργα, θα προηγείται αξιολόγηση.\r\n\r\nΣτην πρώτη θέση του τελευταίου Top500 βρίσκεται για πέμπτη συνεχή φορά τοTianhe-2 του κινεζικού Εθνικού Πανεπιστημίου Αμυντικής Τεχνολογίας με ισχύ 33.86 petaflop/s.\r\n\r\nΑξιοσημείωτο είναι ότι στο Top10 περνά για πρώτη φορά ένας υπερυπολογιστής από τη Μέση Ανατολή. Το σύστημα Shajeen II του Πανεπιστημίου «Βασιλιάς Αμπντάλα» της Σαουδικής Αραβίας, κατασκευασμένο από την αμερικανική Cray, έρχεται έβδομο με ισχύ 5,536 petaflop/s.', 'images/imagefornews/122315.jpg', 8, '2015-07-14 08:57:43', 45, '', 'http://www.cretalive.gr/culture/view/o-uperupologisths-aris-sta-500-ischurotera-susthmata-pagkosmiws/259894'),
(95, 'Ο καρκίνος μπορεί να νικηθεί χωρίς χημειοθεραπείες', 'Μελλοντικά η θεραπεία του καρκίνου σε πρώιμο στάδιο, θα μπορεί να γίνεται με ένεση τοπικά και τη χρήση ιών, χωρίς να χρειάζεται να υποβληθεί ο ασθενής σε χημειοθεραπείες. Οι επιστημονικές έρευνες για την θεραπεία του καρκίνου έχουν στραφεί τα τελευταία χρόνια στη χρήση ιών', 'Μελλοντικά η θεραπεία του καρκίνου σε πρώιμο στάδιο, θα μπορεί να γίνεται με ένεση τοπικά και τη χρήση ιών, χωρίς να χρειάζεται να υποβληθεί ο ασθενής σε χημειοθεραπείες. Οι επιστημονικές έρευνες για την θεραπεία του καρκίνου έχουν στραφεί τα τελευταία χρόνια στη χρήση ιών και ήδη υπάρχουν δέκα ιοί με τους οποίους, στις ΗΠΑ γίνονται κλινικές μελέτες πρώτου και δεύτερου σταδίου για τη δημιουργία θεραπευτικών σχημάτων για διάφορες μορφές της νόσου. Πρόσφατα πήρε έγκριση από τον Αμερικανικό Οργανισμό Τροφίμων και Ποτών (FDA) η πρώτη θεραπεία  του μελανώματος με ερπητοϊό «φορτωμένο» με ένα πεπτίδιο, την λεμφοκίνη. Το φάρμακο αυτό (Talimogene Laherparepvec -T-VEC) αναμένεται να κυκλοφορήσει το επόμενο έτος και θα είναι το πρώτο προϊόν βιολογικού ιού που θα καταπολεμά τον καρκίνο.\r\n\r\nΤα παραπάνω επισημαίνει ο αντιπρόεδρος του Κρατικού Πανεπιστημίου της Λουιζιάνας των ΗΠΑ, καθηγητής Κωνσταντίνος Κούσουλας, ο οποίος βρέθηκε στη Θεσσαλονίκη με την ευκαιρία του συνεδρίου NANOTEXNOLOGY 2015, οι εργασίες του οποίου ολοκληρώθηκαν χτες.\r\n\r\nΣύμφωνα με τον κ. Κούσουλα ο ερπητοϊός που πήρε έγκριση για την θεραπεία του μελανώματος έχει το μειονέκτημα ότι μπαίνει στα νευροκύτταρα, ενώ η έρευνα της δικής του ομάδας είναι επικεντρωμένη στη θεραπεία του καρκίνου με έναν μεταλλαγμένο ερπητοϊό ο οποίος έχει το πλεονέκτημα να μην μπαίνει στα νευροκύτταρα.\r\n\r\n«Η βασική ιδιότητα του ερπητοϊού είναι ότι μπαίνει στα νευροκύτταρα και παραμένει εκεί εφ΄ όρου ζωής και, όταν υπάρχει κάποιο στρες ή οτιδήποτε άλλο, δημιουργεί κάποια προβλήματα πχ μπορεί να κάνει κερατίτιδα ή οτιδήποτε άλλο. Εμείς έχουμε μεταλλάξει τον ιό ώστε να μην μπορεί να μπει μέσα στα νευροκύτταρα και στα πρώτα πειράματα βρήκαμε ότι αυτός ο ιός είναι ένα τρομερό εμβόλιο εναντίον του ίδιου του ερπητοϊού, ιδίως έρπη των γεννητικών οργάνων που είναι ένα σεξουαλικώς μεταδιδόμενο νόσημα. Εμβολιάσαμε ποντίκια με αυτόν τον ιό και μετά προσπαθήσαμε να τα μολύνουμε στα γεννητικά όργανα με κάποιον ιο ο οποίος είναι θανατηφόρος. Διαπιστώσαμε ότι τα ποντίκια είχαν θεραπευτεί 100%. Κι επειδή μελετήσαμε αυτή την κατάσταση, βρήκαμε ότι αυτές οι μεταλλάξεις που κάναμε στον ιό τον αλλάξανε ώστε να μην μπορεί να μπει μέσα στα νευροκύτταρα και παράλληλα ενίσχυσαν το αμυντικό σύστημα πολύ καλύτερα από κάποιους άλλους ιούς που είδαμε νωρίτερα. Αυτός ο ιός όταν μολύνει τα μελανώματα τα καταστρέφει με τέτοιο τρόπο που δημιουργείται μια ανοσολογική αντίδραση και ενισχύεται το αμυντικό σύστημα ώστε να δημιουργείται ένα εμβόλιο εναντίον του καρκίνου», εξηγεί ο κ Κούσουλας.\r\n\r\nΈρευνες για θεραπευτικό εμβόλιο κατά του έρπη και του καρκίνου\r\n\r\nΤο προσεχές φθινόπωρο οι έρευνες της ομάδας του Κούσουλα θα στραφούν στη δημιουργία θεραπευτικού εμβολίου κατά του έρπη αλλά και κατά του καρκίνου με τη χρήση του μεταλλαγμένου ερπητοϊού.\r\n\r\n«Αυτό ο ιός μπορεί να μεταλλαχτεί περαιτέρω ώστε να περιέχει και γονίδια άλλων ιών και βακτηριδίων, όπως είναι τα χλαμύδια και να δημιουργήσουμε ένα εμβόλιο εναντίον του έρπη και εναντίον των χλαμυδίων. Ή να βάλουμε στον ιό πεπτίδια - πρωτεΐνες από μερικούς καρκίνους ώστε να δημιουργήσουμε ένα καλύτερο εμβόλιο για τον καρκίνο. Ήδη έχουμε βάλει στον ιό κάποια γονίδια για καρκίνο του νευρικού συστήματος, όπως είναι το γλοιοβλάστωμα στον εγκέφαλο αλλά επίσης και για καρκίνο του προστάτη. Αυτά είναι εμβόλια θεραπευτικά γιατί ο πρωταρχικός ρόλος είναι να βρεθεί ένας μικρός όγκος όπως πχ ένα καρκίνος του μαστού. Την πρώτη φορά που θα χρησιμοποιηθεί γίνεται ενέσιμα παροχή του ιού μέσα στον πρώτο όγκο ο οποίος καταστρέφεται και καθώς καταστρέφεται δημιουργείται ένα εμβόλιο. Υπάρχει η εκδοχή να χρησιμοποιηθεί κάποτε και προληπτικά, πχ θα εμβολιαστεί μια γυναίκα με τον ιό αυτό για το γονίδιο BRCA1/BRCA2 του καρκίνου του μαστού και έτσι να έχει πολύ περισσότερα αντισώματα από εκείνα που θα είχε κάποια άλλη. Οπότε αν έχει κανείς κάποια γενετική προδιάθεση για κάποιον καρκίνο να μπορούμε και να μειώσουμε τον κίνδυνο», προσθέτει ο κ Κούσουλας.\r\n\r\nΘεραπεία του καρκίνου με μια ένεση\r\n\r\nΟ κ. Κούσουλας αισιοδοξεί ότι με τις θεραπείες αυτές τα επόμενα 10-20 χρόνια θα μπορούν να καταστέλλονται οι καρκίνοι με φάρμακα και οι ασθενείς θα μπορούν να ζουν πολλές δεκαετίες.\r\n\r\n«Στο μέλλον με την πρώτη γνωμάτευση σε αρχικά στάδια του καρκίνου μπορεί να γίνεται μια ένεση και να προστατεύεται ο άνθρωπος για πολλά χρόνια. Ο ερπητοϊός θα χορηγείται ενέσιμα επί του τοπικού καρκινώματος, η τοξικότητά του είναι ελάχιστη και δεν θα χρειάζεται να κάνει χημειοθεραπεία ο ασθενής. Η χημειοθεραπεία σκοτώνει τα κύτταρα τα οποία πολλαπλασιάζονται πιο γρήγορα από τα κανονικά αλλά σκοτώνει όλα τα κύτταρα και το χειρότερο απ΄ όλα, σκοτώνει το αμυντικό σύστημα. Δηλαδή αν υπήρχε μια ευκαιρία στο αμυντικό σύστημα να μπορέσει να ξυπνήσει να καταπολεμήσει το καρκίνωμα μετά τη χημειοθεραπεία αυτή η ευκαιρία χάνεται. Υπάρχει περίπτωση να διαφοροποιηθεί ο ιός να μπει με ένεση στο αίμα και να βρει και κάποια μετάσταση αλλά αυτό δεν συνιστάται γιατί μπορεί να πάει και αλλού. Επίσης όσο πιο μεγάλος είναι ο καρκίνος τόσο πιο δύσκολο είναι να ξυπνήσει το αμυντικό σύστημα. Γι αυτό λέμε αν προλάβουμε τον καρκίνο σε πρώιμο στάδιο τότε το αμυντικό σύστημα έχει καλύτερες επιδόσεις. Το χειρότερο που μπορεί να γίνει σε κάποιον που έχει μικρά καρκινώματα είναι η χημειοθεραπεία. Γιατί αν πετύχει καλώς, αν όμως ξεφύγουν κάποια καρκινώματα μετά γίνονται ανθεκτικά στην χημειοθεραπεία και μετά είναι πολύ δύσκολο να καταπολεμηθούν» καταλήγει ο κ. Κούσουλας.', 'images/imagefornews/el02042011-21-thumb-large1.jpg', 4, '2015-07-14 10:11:49', 11, '', 'http://www.newsbeast.gr/health/arthro/1876533/o-karkinos-bori-na-nikithi-choris-chimiotherapies'),
(96, 'Ηλεκτρονικές sim cards από Samsung & Apple\r\n', 'Σε συζητήσεις για τη δημιουργία των πρώτων ηλεκτρονικών καρτών sim βρίσκονται οι δύο κολοσσοί της τεχνολογίας Samsung και Apple, σύμφωνα με ρεπορτάζ των Financial Times.\r\n', 'Σε συζητήσεις για τη δημιουργία των πρώτων ηλεκτρονικών καρτών sim βρίσκονται οι δύο κολοσσοί της τεχνολογίας Samsung και Apple, σύμφωνα με ρεπορτάζ των Financial Times.\r\n\r\nΟι δύο από τους μεγαλύτερους κατασκευαστές συσκευών κινητής τηλεφωνίας αποσκοπούν στην κατάργηση των "φυσικών" καρτών sim και την ενσωμάτωση αυτών στα chip των κινητών.\r\n\r\nΜε τον τρόπο αυτό θα μπορεί κάποιος να αλλάξει πάροχο απλά ενεργοποιώντας  τη νέα σύνδεση με κωδικό. \r\n\r\nΈτσι αποφεύγεται η γνωστή διαδικασία αφαίρεσης της μπαταρίας, της παλιάς sim και τοποθέτησης της νέας.\r\n\r\nΣύμφωνα με το ρεπορτάζ, στις συζητήσεις παίρνουν μέρος και άλλες μεγάλες εταιρείες του κλάδου, όπως οι AT&T,Deutsche Telekom, Etisalat, Hutchison Whampoa, Orange, Telefónica και Vodafone.', 'images/imagefornews/34C5804871ED1FE5E3A5C5F915891887.jpg', 8, '2015-07-21 08:38:32', 8, '', 'http://www.nooz.gr/tech/ilektronikes-sim-apo-samsung--apple');
INSERT INTO `nea` (`ID`, `Titlos_arthrou`, `Small_text`, `Keimeno`, `Eikona`, `category`, `Time`, `counter`, `url`, `source`) VALUES
(97, 'Οι ΗΠΑ ελπίζουν σε συνεργασία με την Τουρκία για την καταπολέμηση της τρομοκρατίας', 'Οι ΗΠΑ ελπίζουν σε συνεργασία με την Τουρκία και τους εταίρους της για την καταπολέμηση της τρομοκρατίας στην περιοχή έπειτα από τη χθεσινή επίθεση στην τουρκική πόλη Σουρούτς, στα σύνορα με τη Συρία, στην οποία σκοτώθηκαν 28 άνθρωποι και τραυματίστηκαν τουλάχιστον 100', 'Οι ΗΠΑ ελπίζουν σε συνεργασία με την Τουρκία και τους εταίρους της για την καταπολέμηση της τρομοκρατίας στην περιοχή έπειτα από τη χθεσινή επίθεση στην τουρκική πόλη Σουρούτς, στα σύνορα με τη Συρία, στην οποία σκοτώθηκαν 28 άνθρωποι και τραυματίστηκαν τουλάχιστον 100, αναφέρει σε ανακοίνωσή του ο εκπρόσωπος του αμερικανικού Εθνικού Συμβουλίου Ασφαλείας (NSC) Νεντ Πράις.\r\n\r\nΤην επίθεση πραγματοποίησε γυναίκα καμικάζι, που σύμφωνα με τις πρώτες πληροφορίες ήταν μέλος του Ισλαμικού Κράτους, μπροστά από ένα καφέ στο κέντρο της πόλης.\r\n\r\n«Εκφράζουμε επίσης την αλληλεγγύη μας προς την τουρκική κυβέρνηση και τον τουρκικό λαό και επαναλαμβάνουμε την αποφασιστικότητά μας να συνεργαστούμε με την Τουρκία και με άλλους εταίρους για την καταπολέμηση της κοινής απειλής της τρομοκρατίας», αναφέρει ο Πράις.\r\n\r\nΟ Πράις πρόσθεσε ότι οι ΗΠΑ «καταδικάζουν σφόδρα την απεχθή τρομοκρατική επίθεση στο Σουρούτς» και η Ουάσινγκτον συλλυπείται τους συγγενείς των θυμάτων, πολλά από τα οποία είχαν ταξιδέψει στην μεθοριακή αυτή πόλη για να βοηθήσουν στις προσπάθειες ανοικοδόμησης του γειτονικού Κομπάνι.\r\n\r\nΤην επίθεση καταδίκασε και ο γενικός γραμματέας του ΟΗΕ Μπαν Κι Μουν, ο οποίος εξέφρασε την ελπίδα οι δράστες να προσαχθούν στη δικαιοσύνη, σύμφωνα με χθεσινή ανακοίνωσή του.', 'images/imagefornews/21-85354TurkeySuruc.jpg', 2, '2015-07-21 09:29:15', 3, '', 'http://www.amna.gr/category/15/%CE%9A%CF%8C%CF%83%CE%BC%CE%BF%CF%82'),
(98, 'Ταινία της εβδομάδας: Ο ΚΟΣ ΧΟΛΜΣ', 'Έναν διαφορετικό Σέρλοκ Χολμς υποδύεται ο Ίαν ΜακΚέλεν στην ταινία του Μπιλ Κόντον που παρακολουθεί τον διάσημο χαρακτήρα σε υπέργηρη πλέον ηλικία. Η ταινία βασίζεται στο μυθιστόρημα του Μιτς Κάλιν «A Slight Trick of the Mind».', 'Το 1947, ένας ηλικωμένος πλέον Σέρλοκ Χολμς, επιστρέφει από ένα ταξίδι στην Ιαπωνία στο απομακρυσμένο παραθαλάσσιο σπίτι του, και περνάει το τέλος των ημερών του φροντίζοντας τις μέλισσες του. Μόνη του συντροφιά, η οικονόμος και ο νεαρός γιος της. Παλεύοντας με τις δυνάμεις του μυαλού του που τον εγκαταλείπουν, ο Χολμς βασίζεται στο νεαρό αγόρι για να επαναφέρει τις αναμνήσεις της τελευταίας του υπόθεσης που τον έκαναν να παραιτηθεί. Αναζητεί απαντήσεις στα μυστήρια της ζωής και της αγάπης- πριν να είναι αργά.', 'images/imagefornews/xolms.jpg', 9, '2015-07-22 13:46:36', 5, '', 'http://www.clickatlife.gr/cinema/movie/6711/o-kos-xolms');

